import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function PostCoursePage() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [isVisible, setIsVisible] = useState(false);

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    instructor: '',
    duration: '',
    price: ''
  });

  useEffect(() => { setIsVisible(true); }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/courses', formData);
      alert('تم نشر الدورة بنجاح!');
      navigate('/12_CoursesPage');
    } catch (err) {
      alert('خطأ في نشر الدورة');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen bg-[#E3DAD0] p-6 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      <div className="max-w-2xl mx-auto bg-white rounded-[3.5rem] shadow-2xl p-10 text-right" dir="rtl">
        <h2 className="text-3xl font-black text-[#1A365D] mb-8 border-r-8 border-[#1A365D] pr-4">نشر دورة تدريبية جديدة</h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <input 
            type="text" placeholder="عنوان الدورة" 
            className="w-full p-4 bg-gray-50 rounded-2xl border-2 border-transparent focus:border-[#1A365D]/10 outline-none font-black"
            value={formData.title} onChange={(e) => setFormData({...formData, title: e.target.value})} required
          />
          <input 
            type="text" placeholder="اسم المدرب" 
            className="w-full p-4 bg-gray-50 rounded-2xl border-2 border-transparent focus:border-[#1A365D]/10 outline-none font-black"
            value={formData.instructor} onChange={(e) => setFormData({...formData, instructor: e.target.value})} required
          />
          <textarea 
            placeholder="وصف الدورة والمحتويات" 
            className="w-full p-4 bg-gray-50 rounded-2xl border-2 border-transparent focus:border-[#1A365D]/10 outline-none font-black h-40"
            value={formData.description} onChange={(e) => setFormData({...formData, description: e.target.value})} required
          />
          <button type="submit" disabled={loading} className="w-full bg-[#1A365D] text-white py-6 rounded-[2.5rem] font-black text-xl shadow-xl active:scale-95 transition-all">
            {loading ? "جاري النشر..." : "نشر الدورة الآن"}
          </button>
        </form>
      </div>
    </div>
  );
}
