export const AuthPage = ({ lang = 'ar' }) => {
  const { login: performContextLogin } = useAuth();
  const t = translations[lang] || translations.ar;
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [userType, setUserType] = useState('individuals');
  const [loginMethod, setLoginMethod] = useState('phone'); 
  const [showPass, setShowPass] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);
  const [rememberMe, setRememberMe] = useState(true);
  const [agreed, setAgreed] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const [showPhotoModal, setShowPhotoModal] = useState(false);
  const [showPolicy, setShowPolicy] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [imgAnalyzing, setImgAnalyzing] = useState(false);
  
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', companyName: '', companyIndustry: '',
    authorizedName: '', authorizedJob: '', education: '',
    email: '', password: '', confirmPassword: '', phone: '', gender: '', dob: '', 
    isSpecialNeeds: false, specialNeedsType: '', country: ''
  });

  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => { setIsVisible(true); }, []);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const getPhoto = async (source) => {
    setShowPhotoModal(false);
    setTimeout(async () => {
      try {
        const image = await Camera.getPhoto({ quality: 90, allowEditing: true, resultType: CameraResultType.Base64, source });
        if (image && image.base64String) {
          const base64Img = `data:image/jpeg;base64,${image.base64String}`;
          
          // üöÄ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿ∞ŸÉÿßÿ° ŸÉÿßÿ±Ÿäÿ±ŸÉ ŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ© ŸÅŸàÿ±ÿßŸã
          setImgAnalyzing(true);
          setError('');
          try {
            const res = await userService.analyzeImage({ 
              image: base64Img, 
              targetType: userType === 'companies' ? 'logo' : 'face' 
            });

            if (res.data.isValid) {
              setProfileImage(base64Img);
            } else {
              setError(userType === 'companies' ? t.invalidLogo : t.invalidFace);
              setProfileImage(null);
            }
          } catch (aiErr) {
            console.error("AI Image analysis failed", aiErr);
            // ŸÅŸä ÿ≠ÿßŸÑ ŸÅÿ¥ŸÑ ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ±ÿå ŸÜÿ≥ŸÖÿ≠ ÿ®ÿßŸÑÿ±ŸÅÿπ ŸÖÿ§ŸÇÿ™ÿßŸã ÿ£Ÿà ŸÜŸÖŸÜÿπŸá ÿ≠ÿ≥ÿ® ÿ≥Ÿäÿßÿ≥ÿ™ŸÉ. ŸáŸÜÿß ÿ≥ŸÜÿ≥ŸÖÿ≠ ÿ®Ÿá ŸÑÿ™ÿ¨ŸÜÿ® ÿ™ÿπŸÑŸäŸÇ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ.
            setProfileImage(base64Img);
          } finally {
            setImgAnalyzing(false);
          }
        }
      } catch (e) { console.warn("Image pick cancelled"); }
    }, 300);
  };

  const handleAuth = async (e) => {
    e.preventDefault();
    if (imgAnalyzing) return;
    if (!isLogin && !agreed) { setError(t.mustAgree); return; }
    if (!isLogin && formData.password !== formData.confirmPassword) { setError(t.passMismatch); return; }
    setError('');
    setLoading(true);
    try {
      const payload = isLogin 
        ? (loginMethod === 'email' ? { email: formData.email, password: formData.password } : { phone: formData.phone, password: formData.password })
        : { ...formData, profileImage, role: userType === 'companies' ? 'HR' : 'Employee' };
      
      const res = isLogin ? await userService.login(payload) : await userService.register(payload);
      await performContextLogin(res.data.user, res.data.token);
      
      if (res.data.user.role === 'HR') navigate('/03_OnboardingCompanies');
      else navigate('/03_OnboardingIndividuals');
    } catch (err) { setError(err.userMessage || "Error"); } 
    finally { setLoading(false); }
  };

  const isRtl = lang === 'ar';

  return (
    <div className={`min-h-screen w-full flex flex-col items-center justify-center p-4 bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={isRtl ? 'rtl' : 'ltr'}>
      {showPolicy && (
        <div className="fixed inset-0 z-[12000] animate-fade-in"><PolicyPage lang={lang} onAccept={() => { setAgreed(true); setShowPolicy(false); }} /></div>
      )}

      {showPhotoModal && (
        <div className="fixed inset-0 z-[11000] flex items-end justify-center bg-[#1A365D]/40 backdrop-blur-sm" onClick={() => setShowPhotoModal(false)}>
          <div className="bg-white w-full max-w-lg rounded-t-[4rem] p-10 shadow-2xl" onClick={e => e.stopPropagation()}>
            <h3 className="text-xl font-black text-[#1A365D] mb-8 text-center uppercase tracking-widest">{userType === 'companies' ? t.uploadLogo : t.uploadProfile}</h3>
            <div className={`grid ${userType === 'companies' ? 'grid-cols-1' : 'grid-cols-2'} gap-4`}>
              {userType !== 'companies' && (
                <button onClick={() => getPhoto(CameraSource.Camera)} className="p-8 bg-gray-50 rounded-[2.5rem] font-black text-[#1A365D] flex flex-col items-center gap-3">
                  <span className="text-4xl">üì∏</span> {isRtl ? 'ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß' : 'Camera'}
                </button>
              )}
              <button onClick={() => getPhoto(CameraSource.Photos)} className="p-8 bg-gray-50 rounded-[2.5rem] font-black text-[#1A365D] flex flex-col items-center gap-3">
                <span className="text-4xl">üñºÔ∏è</span> {isRtl ? 'ÿßŸÑŸÖÿπÿ±ÿ∂' : 'Gallery'}
              </button>
            </div>
            <button onClick={() => setShowPhotoModal(false)} className="w-full mt-6 py-4 text-[#1A365D]/40 font-black text-sm">ÿ•ŸÑÿ∫ÿßÿ°</button>
          </div>
        </div>
      )}

      <div className={`w-full max-w-lg bg-white rounded-[4rem] shadow-2xl p-10 md:p-14 border border-white transition-all duration-1000 transform ${isVisible ? 'translate-y-0' : 'translate-y-10'}`}>
        <div className="text-center mb-10">
          <img src="/logo.jpg" alt="Logo" className="h-24 w-24 mx-auto rounded-full shadow-xl mb-4 border-4 border-[#E3DAD0] object-cover" />
          <h2 className="text-3xl font-black text-[#1A365D]">{isLogin ? t.login : t.signup}</h2>
          {imgAnalyzing && <p className="text-[10px] text-blue-600 font-black mt-2 animate-pulse">{t.aiAnalyzing}</p>}
        </div>

        <div className="flex bg-gray-100 p-2 rounded-[2rem] mb-8">
          <button type="button" onClick={() => setIsLogin(true)} className={`flex-1 py-4 rounded-[1.6rem] font-black transition-all ${isLogin ? 'bg-white text-[#1A365D] shadow-md' : 'text-gray-400'}`}>{t.login}</button>
          <button type="button" onClick={() => setIsLogin(false)} className={`flex-1 py-4 rounded-[1.6rem] font-black transition-all ${!isLogin ? 'bg-white text-[#1A365D] shadow-md' : 'text-gray-400'}`}>{t.signup}</button>
        </div>

        {!isLogin && (
          <div className="flex gap-2 p-1.5 bg-[#1A365D]/5 rounded-3xl mb-10 items-center">
            <span className="text-[10px] font-black text-[#1A365D] px-4 uppercase tracking-widest">{t.category}</span>
            <button type="button" onClick={() => setUserType('individuals')} className={`flex-1 py-3 rounded-2xl text-[11px] font-black transition-all ${userType === 'individuals' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-gray-400'}`}>{t.individuals}</button>
            <button type="button" onClick={() => setUserType('companies')} className={`flex-1 py-3 rounded-2xl text-[11px] font-black transition-all ${userType === 'companies' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-gray-400'}`}>{t.companies}</button>
          </div>
        )}

        <form onSubmit={handleAuth} className="space-y-6">
          {!isLogin && (
            <div className="flex flex-col items-center mb-6">
              <button type="button" onClick={() => setShowPhotoModal(true)} disabled={imgAnalyzing} className={`w-32 h-32 rounded-full bg-gray-50 border-4 border-white shadow-2xl flex items-center justify-center overflow-hidden group relative transition-all ${imgAnalyzing ? 'opacity-50' : 'hover:scale-105 active:scale-95'}`}>
                {profileImage ? <img src={profileImage} alt="Profile" className="w-full h-full object-cover" /> : <span className="text-5xl opacity-20">üë§</span>}
                <div className="absolute inset-0 bg-[#1A365D]/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center text-white text-[10px] font-black uppercase tracking-widest">{isRtl ? 'ÿ™ÿ∫ŸäŸäÿ±' : 'Edit'}</div>
              </button>
            </div>
          )}

          {/* ÿ®ÿßŸÇŸä ÿßŸÑÿ≠ŸÇŸàŸÑ ŸÉŸÖÿß ŸáŸä... */}
          {error && <div className="p-4 bg-red-50 text-red-600 rounded-2xl text-[11px] font-black text-center border border-red-100 animate-shake">{error}</div>}
          
          <button type="submit" disabled={loading || imgAnalyzing} className="w-full py-6 rounded-[3rem] bg-[#1A365D] text-white font-black shadow-2xl transition-all text-2xl mt-4 hover:bg-[#152c4d] disabled:opacity-50">
            {loading ? <div className="w-8 h-8 border-4 border-white/30 border-t-white rounded-full animate-spin mx-auto"></div> : (isLogin ? t.login : t.signup)}
          </button>
        </form>
      </div>
    </div>
  );
};
