import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Navbar } from '../components/Navbar';
import { Footer } from '../components/Footer';
import api from '../services/api';

export default function AdminDashboard() {
  const { user, language } = useAuth();
  const [stats, setStats] = useState({ users: 0, jobs: 0, courses: 0, revenue: 0 });
  const [isVisible, setIsVisible] = useState(false);
  const isRTL = language === 'ar';

  useEffect(() => {
    setIsVisible(true);
    // ŸÖÿ≠ÿßŸÉÿßÿ© ÿ¨ŸÑÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ•ÿØÿßÿ±ÿ©
    setStats({ users: 1250, jobs: 45, courses: 12, revenue: 15800 });
  }, []);

  const t = {
    ar: {
      title: "ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ¥ÿßŸÖŸÑÿ©",
      welcome: "ÿ£ŸáŸÑÿßŸã ÿ®ŸÉÿå ÿßŸÑŸÇÿßÿ¶ÿØ",
      stats: "ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÖŸÜÿµÿ©",
      users: "ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ",
      jobs: "ÿßŸÑŸàÿ∏ÿßÿ¶ŸÅ ÿßŸÑŸÜÿ¥ÿ∑ÿ©",
      courses: "ÿßŸÑÿØŸàÿ±ÿßÿ™ ÿßŸÑÿ™ÿπŸÑŸäŸÖŸäÿ©",
      revenue: "ÿµÿßŸÅŸä ÿßŸÑÿ£ÿ±ÿ®ÿßÿ≠",
      sections: ["ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ", "ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑÿ¥ÿ±ŸÉÿßÿ™", "ÿßŸÑÿ±ŸÇÿßÿ®ÿ© ÿßŸÑŸÖÿßŸÑŸäÿ©", "ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑŸÖÿ™ŸÇÿØŸÖÿ©"]
    },
    en: {
      title: "Master Admin Dashboard",
      welcome: "Welcome, Commander",
      stats: "Platform Stats",
      users: "Total Users",
      jobs: "Active Jobs",
      courses: "Academy Courses",
      revenue: "Net Revenue",
      sections: ["User Management", "Company Requests", "Financial Audit", "Core Settings"]
    }
  }[language || 'ar'];

  return (
    <div className={`min-h-screen bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={isRTL ? 'rtl' : 'ltr'}>
      <Navbar lang={language} user={user} />

      <main className="max-w-7xl mx-auto px-6 py-12">
        {/* Header Section */}
        <div className="flex flex-col md:flex-row justify-between items-center mb-16 gap-6">
          <div className={`${isRTL ? 'text-right' : 'text-left'}`}>
            <h1 className="text-5xl font-black text-[#1A365D] tracking-tighter mb-2 italic">Careerak Tower</h1>
            <p className="text-[#1A365D]/40 font-black uppercase tracking-[0.3em] text-sm">{t.welcome}</p>
          </div>
          <div className="w-24 h-24 rounded-full border-4 border-[#1A365D] p-1 shadow-2xl bg-white overflow-hidden">
             <img src="/logo.jpg" alt="Admin" className="w-full h-full object-cover rounded-full" />
          </div>
        </div>

        {/* Stats Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 mb-16">
          {[
            { label: t.users, val: stats.users, icon: 'üë•' },
            { label: t.jobs, val: stats.jobs, icon: 'üíº' },
            { label: t.courses, val: stats.courses, icon: 'üéì' },
            { label: t.revenue, val: `$${stats.revenue}`, icon: 'üí∞' }
          ].map((s, i) => (
            <div key={i} className="bg-white p-8 rounded-[3.5rem] shadow-xl border border-white hover:scale-105 transition-all">
              <span className="text-4xl mb-4 block">{s.icon}</span>
              <p className="text-xs font-black text-gray-400 uppercase mb-1">{s.label}</p>
              <h3 className="text-3xl font-black text-[#1A365D]">{s.val}</h3>
            </div>
          ))}
        </div>

        {/* Main Sections Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {t.sections.map((section, i) => (
            <button key={i} className="group p-10 bg-[#1A365D] text-white rounded-[4rem] shadow-2xl flex justify-between items-center hover:bg-[#1A365D]/90 transition-all text-right">
              <span className={`text-3xl font-black ${!isRTL && 'order-2'}`}>{section}</span>
              <div className="w-16 h-16 bg-white/10 rounded-full flex items-center justify-center group-hover:scale-125 transition-transform">
                <span className="text-2xl">‚ûî</span>
              </div>
            </button>
          ))}
        </div>
      </main>

      <Footer />
    </div>
  );
}
