import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import { userService } from '../services/api';
import { useAuth } from '../context/AuthContext';
import PolicyPage from './14_PolicyPage';

const countryCodes = [
  { code: '+970', flag: 'üáµüá∏', name: 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ' }, { code: '+962', flag: 'üáØüá¥', name: 'ÿßŸÑÿ£ÿ±ÿØŸÜ' },
  { code: '+966', flag: 'üá∏üá¶', name: 'ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©' }, { code: '+20', flag: 'üá™üá¨', name: 'ŸÖÿµÿ±' },
  { code: '+971', flag: 'üá¶üá™', name: 'ÿßŸÑÿ•ŸÖÿßÿ±ÿßÿ™' }, { code: '+90', flag: 'üáπüá∑', name: 'ÿ™ÿ±ŸÉŸäÿß' },
];

const translations = {
  ar: {
    signup: "ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ", firstName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ", lastName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ÿÆŸäÿ±", 
    companyName: "ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ¥ÿ£ÿ©", country: "ÿßÿÆÿ™ÿ± ÿßŸÑÿ®ŸÑÿØ", mustAgree: "Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖŸàÿßŸÅŸÇÿ© ÿπŸÑŸâ ÿßŸÑÿ≥Ÿäÿßÿ≥ÿ©",
    loading: "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...", aiAnalyzing: "ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ©... ü§ñ", 
    invalidFace: "‚ö†Ô∏è ÿµŸàÿ±ÿ© Ÿàÿ¨Ÿá ÿ∫Ÿäÿ± Ÿàÿßÿ∂ÿ≠ÿ©", invalidLogo: "‚ö†Ô∏è ŸÑŸàÿ¨Ÿà ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠",
    email: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä", phone: "ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ", password: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±", 
    confirmPassword: "ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±", alreadyHave: "ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ® ÿ®ÿßŸÑŸÅÿπŸÑÿü", loginNow: "ÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑŸÉ ÿßŸÑÿ¢ŸÜ"
  },
  en: {
    signup: "Create Account", firstName: "First Name", lastName: "Last Name",
    companyName: "Company Name", country: "Select Country", mustAgree: "Please agree to policy",
    loading: "Loading...", aiAnalyzing: "Analyzing image... ü§ñ",
    invalidFace: "‚ö†Ô∏è Invalid face photo", invalidLogo: "‚ö†Ô∏è Invalid logo",
    email: "Email", phone: "Phone", password: "Password",
    confirmPassword: "Confirm Password", alreadyHave: "Already have an account?", loginNow: "Login now"
  }
};

export default function AuthPage() {
  const { language, login: performContextLogin } = useAuth();
  const lang = language || 'ar';
  const t = translations[lang] || translations.ar;
  const navigate = useNavigate();
  const [userType, setUserType] = useState('individuals');
  const [agreed, setAgreed] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const [showPhotoModal, setShowPhotoModal] = useState(false);
  const [showPolicy, setShowPolicy] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [imgAnalyzing, setImgAnalyzing] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const [formData, setFormData] = useState({
    firstName: '', lastName: '', companyName: '', email: '', password: '', 
    confirmPassword: '', phone: '', country: ''
  });

  useEffect(() => { setIsVisible(true); }, []);

  const handleInputChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const getPhoto = async (source) => {
    setShowPhotoModal(false);
    try {
      const image = await Camera.getPhoto({
        quality: 90,
        allowEditing: true,
        resultType: CameraResultType.Base64,
        source
      });
      const base64Img = `data:image/jpeg;base64,${image.base64String}`;
      setImgAnalyzing(true);
      try {
        const res = await userService.analyzeImage({
          image: base64Img,
          targetType: userType === 'companies' ? 'logo' : 'face'
        });
        if (res.data.isValid) setProfileImage(base64Img);
        else {
          setError(userType === 'companies' ? t.invalidLogo : t.invalidFace);
          setProfileImage(null);
        }
      } catch (e) {
        setProfileImage(base64Img); // Fallback if AI service is down
      } finally {
        setImgAnalyzing(false);
      }
    } catch (e) {}
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!agreed) { setError(t.mustAgree); return; }
    if (formData.password !== formData.confirmPassword) {
      setError(lang === 'ar' ? "ŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ŸÖÿ™ÿ∑ÿßÿ®ŸÇÿ©" : "Passwords do not match");
      return;
    }
    
    setError('');
    setLoading(true);
    try {
      const payload = {
        ...formData,
        profileImage,
        role: userType === 'companies' ? 'HR' : 'Employee'
      };
      const res = await userService.register(payload);
      await performContextLogin(res.data.user, res.data.token);
      navigate(res.data.user.role === 'HR' ? '/onboarding-companies' : '/onboarding-individuals');
    } catch (err) {
      setError(err.response?.data?.error || "Error in signup");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen w-full flex flex-col items-center justify-center p-4 bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={lang === 'ar' ? 'rtl' : 'ltr'}>
      {showPolicy && (
        <div className="fixed inset-0 z-[12000] bg-white overflow-y-auto">
          <PolicyPage />
          <div className="fixed bottom-6 left-1/2 -translate-x-1/2 w-full max-w-xs px-4">
            <button
              onClick={() => { setAgreed(true); setShowPolicy(false); }}
              className="w-full py-5 bg-[#1A365D] text-white rounded-2xl font-black shadow-2xl"
            >
              {lang === 'ar' ? 'ÿ£ŸàÿßŸÅŸÇ ÿπŸÑŸâ ÿßŸÑÿ¥ÿ±Ÿàÿ∑' : 'I Agree'}
            </button>
          </div>
        </div>
      )}

      {showPhotoModal && (
        <div className="fixed inset-0 z-[11000] flex items-center justify-center p-6 bg-black/40 backdrop-blur-sm">
          <div className="bg-white rounded-[3rem] p-8 w-full max-w-xs text-center shadow-2xl border-t-8 border-[#1A365D]">
            <h3 className="text-[#1A365D] font-black mb-6">{lang === 'ar' ? 'ÿ•ÿ∂ÿßŸÅÿ© ÿµŸàÿ±ÿ©' : 'Add Photo'}</h3>
            <div className="space-y-3">
              <button onClick={() => getPhoto(CameraSource.Camera)} className="w-full py-4 bg-gray-50 text-[#1A365D] rounded-2xl font-bold shadow-sm">{lang === 'ar' ? 'ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß' : 'Camera'}</button>
              <button onClick={() => getPhoto(CameraSource.Photos)} className="w-full py-4 bg-gray-50 text-[#1A365D] rounded-2xl font-bold shadow-sm">{lang === 'ar' ? 'ÿßŸÑŸÖÿπÿ±ÿ∂' : 'Gallery'}</button>
              <button onClick={() => setShowPhotoModal(false)} className="w-full py-4 text-gray-400 font-bold">{lang === 'ar' ? 'ÿ•ŸÑÿ∫ÿßÿ°' : 'Cancel'}</button>
            </div>
          </div>
        </div>
      )}

      <div className={`w-full max-w-lg bg-white rounded-[4rem] shadow-2xl p-8 md:p-14 border border-white transform transition-all duration-1000 ${isVisible ? 'translate-y-0' : 'translate-y-10'}`}>
        <div className="text-center mb-8">
          <img src="/logo.jpg" alt="Logo" className="h-20 w-20 mx-auto rounded-full shadow-xl mb-4 border-4 border-[#E3DAD0]" />
          <h2 className="text-3xl font-black text-[#1A365D]">{t.signup}</h2>
        </div>

        <div className="flex gap-2 p-1 bg-[#1A365D]/5 rounded-2xl mb-8">
          <button onClick={() => setUserType('individuals')} className={`flex-1 py-3 rounded-xl text-xs font-black transition-all ${userType === 'individuals' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-gray-400'}`}>
            {lang === 'ar' ? 'ÿ£ŸÅÿ±ÿßÿØ' : 'Individuals'}
          </button>
          <button onClick={() => setUserType('companies')} className={`flex-1 py-3 rounded-xl text-xs font-black transition-all ${userType === 'companies' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-gray-400'}`}>
            {lang === 'ar' ? 'ÿ¥ÿ±ŸÉÿßÿ™' : 'Companies'}
          </button>
        </div>

        <form onSubmit={handleRegister} className="space-y-5">
          <div className="flex flex-col items-center mb-6">
            <button type="button" onClick={() => setShowPhotoModal(true)} className="w-24 h-24 rounded-full bg-gray-50 border-4 border-white shadow-xl flex items-center justify-center overflow-hidden relative group">
              {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <span className="text-4xl opacity-20">üë§</span>}
              {imgAnalyzing && <div className="absolute inset-0 bg-[#1A365D]/40 flex items-center justify-center"><div className="w-6 h-6 border-2 border-white border-t-transparent rounded-full animate-spin"></div></div>}
            </button>
            {imgAnalyzing && <p className="text-[10px] text-blue-600 font-black mt-2 animate-pulse">{t.aiAnalyzing}</p>}
          </div>

          <div className="space-y-4">
            <select name="country" onChange={handleInputChange} className="w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-sm text-center border-2 border-transparent focus:border-[#1A365D]/10 shadow-inner appearance-none">
              <option value="">-- {t.country} --</option>
              {countryCodes.map(c => <option key={c.code} value={c.name}>{c.flag} {c.name}</option>)}
            </select>
            {userType === 'individuals' ? (
              <div className="grid grid-cols-2 gap-3">
                <input type="text" name="firstName" placeholder={t.firstName} onChange={handleInputChange} className="p-5 bg-gray-50 rounded-[2rem] font-bold text-center text-sm shadow-inner" required />
                <input type="text" name="lastName" placeholder={t.lastName} onChange={handleInputChange} className="p-5 bg-gray-50 rounded-[2rem] font-bold text-center text-sm shadow-inner" required />
              </div>
            ) : (
              <input type="text" name="companyName" placeholder={t.companyName} onChange={handleInputChange} className="w-full p-5 bg-gray-50 rounded-[2rem] font-bold text-center text-sm shadow-inner" required />
            )}
            <input type="email" name="email" onChange={handleInputChange} placeholder={t.email} className="w-full p-5 bg-gray-50 rounded-[2rem] font-bold text-sm text-center shadow-inner" required />
            <input type="tel" name="phone" onChange={handleInputChange} placeholder={t.phone} className="w-full p-5 bg-gray-50 rounded-[2rem] font-bold text-sm text-center shadow-inner" required />
            <input type="password" name="password" onChange={handleInputChange} placeholder={t.password} className="w-full p-5 bg-gray-50 rounded-[2rem] font-bold text-sm text-center shadow-inner" required />
            <input type="password" name="confirmPassword" onChange={handleInputChange} placeholder={t.confirmPassword} className="w-full p-5 bg-gray-50 rounded-[2rem] font-bold text-sm text-center shadow-inner" required />
          </div>

          <div className="flex items-center gap-3 px-4 text-[11px] font-bold text-gray-400">
            <input type="checkbox" checked={agreed} onChange={(e) => setAgreed(e.target.checked)} className="w-5 h-5 rounded-lg" />
            <p>{lang === 'ar' ? 'ÿ£ŸàÿßŸÅŸÇ ÿπŸÑŸâ' : 'I agree to'} <button type="button" onClick={() => setShowPolicy(true)} className="text-[#1A365D] underline font-black">{lang === 'ar' ? 'ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©' : 'Privacy Policy'}</button></p>
          </div>

          {error && <div className="p-4 bg-red-50 text-red-600 rounded-2xl text-[11px] font-black text-center border border-red-100">{error}</div>}

          <button type="submit" disabled={loading || imgAnalyzing} className="w-full py-6 rounded-[3rem] bg-[#1A365D] text-white font-black shadow-2xl active:scale-95 transition-all text-xl">
            {loading ? <div className="w-6 h-6 border-4 border-white/30 border-t-white rounded-full animate-spin mx-auto"></div> : t.signup}
          </button>
        </form>

        <div className="mt-8 text-center">
          <p className="text-gray-400 font-bold text-sm">
            {t.alreadyHave} <span onClick={() => navigate('/login')} className="text-[#1A365D] cursor-pointer hover:underline font-black">{t.loginNow}</span>
          </p>
        </div>
      </div>
    </div>
  );
}
