import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { useAppSettings } from "../context/AppSettingsContext";

const translations = {
  ar: {
    confirmLang: "هل أنت متأكد من اللغة التي تم اختيارها؟ مع العلم أن التطبيق سيعمل بشكل كامل باللغة التي تم اختيارها، ويمكنكم تغيير اللغة في اي وقت شئتم من لوحة التحكم",
    audioTitle: "هل توافق على تشغيل الموسيقى والصوتيات في التطبيق؟ مع العلم أنكم بإمكانكم التحكم في الصوتيات والموسيقى من لوحة التحكم متى شئتم",
    yes: "نعم",
    no: "لا",
    ok: "تأكيد",
    title: "اختر اللغة"
  },
  en: {
    confirmLang: "Are you sure about the selected language? The app will work entirely in this language, and you can change it anytime from the dashboard.",
    audioTitle: "Do you agree to play music and audio in the app? You can control audio and music from the dashboard whenever you want.",
    yes: "Yes",
    no: "No",
    ok: "Confirm",
    title: "Choose Language"
  },
  fr: {
    confirmLang: "Êtes-vous sûr de la langue sélectionnée ? L'application fonctionnera entièrement dans cette langue et vous pourrez la modifier à tout moment depuis le tableau de bord.",
    audioTitle: "Acceptez-vous de jouer de la musique et de l'audio dans l'application ? Vous pouvez contrôler l'audio et la musique depuis le tableau de bord quand vous le souhaitez.",
    yes: "Oui",
    no: "Non",
    ok: "Confirmer",
    title: "Choisir la langue"
  }
};

export default function LanguagePage() {
  const { changeLanguage, setAudio } = useAuth();
  const { saveLanguage, saveAudio } = useAppSettings();
  const navigate = useNavigate();
  
  const [selectedLang, setSelectedLang] = useState(null);
  const [showConfirmPopup, setShowConfirmPopup] = useState(false);
  const [showAudioPopup, setShowAudioPopup] = useState(false);

  const handleLangPick = (lang) => {
    setSelectedLang(lang);
    setShowConfirmPopup(true);
  };

  const finalize = async (audioConsent) => {
    // حفظ الإعدادات
    await saveLanguage(selectedLang);
    await saveAudio(audioConsent);
    
    if (changeLanguage) await changeLanguage(selectedLang);
    if (setAudio) await setAudio(audioConsent);

    // الانتقال لصفحة الانتري
    navigate("/entry", { replace: true });
  };

  const t = translations[selectedLang] || translations.ar;

  return (
    <div className="min-h-screen bg-[#E3DAD0] flex flex-col items-center justify-center gap-6 p-4">
      <h1 className="text-[#1A365D] font-black text-2xl text-center mb-8">
        اختر اللغة / Choose Language / Choisir la langue
      </h1>

      <div className="flex flex-col gap-4 w-full max-w-xs">
        <button onClick={() => handleLangPick("ar")} className="py-4 bg-white text-[#1A365D] rounded-2xl font-black shadow-lg border-2 border-transparent hover:border-[#1A365D] transition-all text-xl">
          العربية
        </button>
        <button onClick={() => handleLangPick("en")} className="py-4 bg-white text-[#1A365D] rounded-2xl font-black shadow-lg border-2 border-transparent hover:border-[#1A365D] transition-all text-xl">
          English
        </button>
        <button onClick={() => handleLangPick("fr")} className="py-4 bg-white text-[#1A365D] rounded-2xl font-black shadow-lg border-2 border-transparent hover:border-[#1A365D] transition-all text-xl">
          Français
        </button>
      </div>

      {/* Popup 1: تأكيد اللغة */}
      {showConfirmPopup && (
        <div className="fixed inset-0 bg-black/60 flex items-center justify-center p-6 z-[1000]">
          <div className="bg-white p-8 rounded-[2.5rem] shadow-2xl text-center max-w-sm border-t-8 border-[#1A365D] animate-in fade-in zoom-in duration-300">
            <p className="text-[#1A365D] font-bold text-lg mb-6 leading-relaxed" dir={selectedLang === 'ar' ? 'rtl' : 'ltr'}>
              {t.confirmLang}
            </p>
            <button 
              onClick={() => { setShowConfirmPopup(false); setShowAudioPopup(true); }} 
              className="w-full bg-[#1A365D] text-white py-4 rounded-2xl font-black text-lg shadow-lg active:scale-95 transition-transform"
            >
              {t.ok}
            </button>
          </div>
        </div>
      )}

      {/* Popup 2: أذونات الصوت */}
      {showAudioPopup && (
        <div className="fixed inset-0 bg-black/60 flex items-center justify-center p-6 z-[1000]">
          <div className="bg-white p-8 rounded-[2.5rem] shadow-2xl text-center max-w-sm border-t-8 border-[#1A365D] animate-in fade-in zoom-in duration-300">
            <p className="text-[#1A365D] font-bold text-lg mb-6 leading-relaxed" dir={selectedLang === 'ar' ? 'rtl' : 'ltr'}>
              {t.audioTitle}
            </p>
            <div className="flex gap-4">
              <button 
                onClick={() => finalize(true)} 
                className="flex-1 bg-[#1A365D] text-white py-4 rounded-2xl font-black text-lg shadow-lg active:scale-95 transition-transform"
              >
                {t.yes}
              </button>
              <button 
                onClick={() => finalize(false)} 
                className="flex-1 border-2 border-[#1A365D] text-[#1A365D] py-4 rounded-2xl font-black text-lg active:scale-95 transition-transform"
              >
                {t.no}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
