import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import logo from "../assets/logo.png";

export default function LanguagePage() {
  const { changeLanguage } = useAuth();
  const navigate = useNavigate();

  const [lang, setLang] = useState(null);

  const texts = {
    ar: {
      title: "اختر اللغة",
      text: "سيتم اعتماد هذه اللغة في التطبيق ويمكن تغييرها لاحقًا",
      ok: "تأكيد",
      cancel: "إلغاء",
    },
    en: {
      title: "Choose Language",
      text: "This language will be used in the app and can be changed later",
      ok: "Confirm",
      cancel: "Cancel",
    },
    fr: {
      title: "Choisir la langue",
      text: "Cette langue sera utilisée dans l'application",
      ok: "Confirmer",
      cancel: "Annuler",
    },
  };

  const t = lang ? texts[lang] : texts.ar;

  const confirm = async () => {
    await changeLanguage(lang);
    navigate("/auth", { replace: true });
  };

  return (
    <div className="min-h-screen bg-[#E3DAD0] flex flex-col items-center justify-center gap-6">
      <div className="w-44 h-44 rounded-full border-4 border-[#1A365D] overflow-hidden">
        <img src={logo} className="w-full h-full object-cover scale-110" />
      </div>

      <h2 className="text-[#1A365D] font-bold text-lg">
        اختر اللغة / Choose Language
      </h2>
      <p className="text-[#1A365D]">Choisir la langue</p>

      <div className="flex flex-col gap-4 w-72">
        <button onClick={() => setLang("ar")} className="lang-btn">العربية</button>
        <button onClick={() => setLang("en")} className="lang-btn">English</button>
        <button onClick={() => setLang("fr")} className="lang-btn">Français</button>
      </div>

      {lang && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center">
          <div className="bg-white p-6 rounded-2xl w-[90%] max-w-sm text-center">
            <h3 className="font-bold text-[#1A365D] mb-2">{t.title}</h3>
            <p className="text-gray-600 mb-4">{t.text}</p>
            <div className="flex gap-3">
              <button onClick={confirm} className="flex-1 bg-[#1A365D] text-white py-2 rounded-full">
                {t.ok}
              </button>
              <button onClick={() => setLang(null)} className="flex-1 border py-2 rounded-full">
                {t.cancel}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
