    subtitle: "Ù…Ø±Ø­Ø¨Ø§Ù‹ Ø¨Ùƒ Ù…Ø¬Ø¯Ø¯Ø§Ù‹",
    userLabel: "Ø§Ù„Ø¨Ø±ÙŠØ¯ Ø§Ù„Ø¥Ù„ÙƒØªØ±ÙˆÙ†ÙŠ / Ø§Ø³Ù… Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…",
    userPlaceholder: "Ø§Ø³Ù… Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø£Ùˆ Ø§Ù„Ø¨Ø±ÙŠØ¯",
    passLabel: "ÙƒÙ„Ù…Ø© Ø§Ù„Ù…Ø±ÙˆØ±",
    passPlaceholder: "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢",
    loginBtn: "Ø¯Ø®ÙˆÙ„",
    noAccount: "Ù„ÙŠØ³ Ù„Ø¯ÙŠÙƒ Ø­Ø³Ø§Ø¨ØŸ",
    createAccount: "Ø£Ù†Ø´Ø¦ Ø­Ø³Ø§Ø¨Ø§Ù‹ Ø§Ù„Ø¢Ù†",
    error: "ÙØ´Ù„ ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„ØŒ ÙŠØ±Ø¬Ù‰ Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª",
    whatsappMsg: "Ù…Ø±Ø­Ø¨Ø§Ù‹ ÙØ±ÙŠÙ‚ CareerakØŒ Ø£Ø­ØªØ§Ø¬ Ø¥Ù„Ù‰ Ù…Ø³Ø§Ø¹Ø¯Ø© ÙÙŠ ØªØ³Ø¬ÙŠÙ„ Ø§Ù„Ø¯Ø®ÙˆÙ„."
  },
  en: {
    title: "Login",
    subtitle: "Welcome Back",
    userLabel: "Email / Username",
    userPlaceholder: "Username or Email",
    passLabel: "Password",
    passPlaceholder: "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢",
    loginBtn: "Login",
    noAccount: "Don't have an account?",
    createAccount: "Create account now",
    error: "Login failed, please check your credentials",
    whatsappMsg: "Hello Careerak team, I need help with my login."
  },
  fr: {
    title: "Connexion",
    subtitle: "Bienvenue",
    userLabel: "Email / Nom d'utilisateur",
    userPlaceholder: "Nom d'utilisateur ou Email",
    passLabel: "Mot de passe",
    passPlaceholder: "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢",
    loginBtn: "Entrer",
    noAccount: "Vous n'avez pas de compte ?",
    createAccount: "CrÃ©er un compte maintenant",
    error: "Ã‰chec de la connexion, veuillez vÃ©rifier vos identifiants",
    whatsappMsg: "Bonjour l'Ã©quipe Careerak, j'ai besoin d'aide pour me connecter."
  }
};

export default function LoginPage() {
  const navigate = useNavigate();
  const { language, audioEnabled } = useAuth();
  const t = loginTranslations[language] || loginTranslations.ar;
  const isRTL = language === 'ar';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => { 
    setIsVisible(true); 
    
    // ØªØ´ØºÙŠÙ„ Ù…ÙˆØ³ÙŠÙ‚Ù‰ Ø§Ù„Ø®Ù„ÙÙŠØ© Ø¥Ø°Ø§ ÙƒØ§Ù† Ù…Ø³Ù…ÙˆØ­Ø§Ù‹ Ø¨Ù‡Ø§
    let audio;
    if (audioEnabled) {
      audio = new Audio('/Music.mp3');
      audio.loop = true;
      audio.volume = 0.5;
      audio.play().catch(e => console.log("Audio play blocked"));
    }

    return () => {
      if (audio) audio.pause();
    };
  }, [audioEnabled]);

  const encryptToken = (token) => {
    return CryptoJS.AES.encrypt(token, SECRET_KEY).toString();
  };

  const handleWhatsAppHelp = () => {
    const phone = "00201228195728"; 
    const url = `https://wa.me/${phone}?text=${encodeURIComponent(t.whatsappMsg)}`;
    window.open(url, '_blank');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await userService.login({ email, password });
      const encryptedToken = encryptToken(response.data.token);
      await Preferences.set({ key: 'auth_token', value: encryptedToken });
      localStorage.setItem('user', JSON.stringify(response.data.user));
      
      const userType = response.data.user.role?.toLowerCase(); 
      if (userType === 'admin') navigate('/admin-dashboard');
      else if (userType === 'hr') navigate('/hr-dashboard');
      else navigate('/profile');
    } catch (err) {
      setError(err.userMessage || t.error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen flex items-center justify-center px-4 py-10 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'} bg-[#E3DAD0]`} dir={isRTL ? 'rtl' : 'ltr'}>
      <div className={`w-full max-w-md bg-white p-8 sm:p-12 rounded-[3.5rem] shadow-2xl border border-white/50 relative overflow-hidden transition-all duration-1000 transform ${isVisible ? 'translate-y-0' : 'translate-y-20'}`}>
        <div className="relative z-10">
          <div className="flex flex-col items-center mb-10">
             <img src="/logo.jpg" alt="Logo" className="w-24 h-24 rounded-full border-4 border-gray-50 shadow-lg mb-6 object-cover" />
             <h2 className="text-3xl font-black text-[#1A365D] text-center">{t.title}</h2>
             <p className="text-[#1A365D]/40 text-sm mt-2 font-bold uppercase tracking-widest">{t.subtitle}</p>
          </div>

          {error && (
            <div className="text-red-600 mb-6 p-4 bg-red-50 rounded-[1.5rem] text-sm font-bold border border-red-100 text-center">
              {error}
            </div>
          )}

          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className={`block text-sm font-black text-[#1A365D] mb-3 ${isRTL ? 'mr-4' : 'ml-4'}`}>{t.userLabel}</label>
              <input
                type="text"
                placeholder={t.userPlaceholder}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className={`w-full p-5 bg-gray-50 rounded-[2rem] border-2 border-transparent focus:border-[#1A365D]/10 focus:outline-none text-lg font-bold ${isRTL ? 'text-right' : 'text-left'}`}
                required
              />
            </div>

            <div>
              <label className={`block text-sm font-black text-[#1A365D] mb-3 ${isRTL ? 'mr-4' : 'ml-4'}`}>{t.passLabel}</label>
              <input
                type="password"
                placeholder={t.passPlaceholder}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className={`w-full p-5 bg-gray-50 rounded-[2rem] border-2 border-transparent focus:border-[#1A365D]/10 focus:outline-none text-lg font-bold ${isRTL ? 'text-right' : 'text-left'}`}
                required
              />
            </div>

            <button
              type="submit"
              disabled={loading}
              className="w-full bg-[#1A365D] text-white p-6 rounded-[2.5rem] font-black text-xl shadow-lg transition-all hover:-translate-y-1 active:scale-95 flex items-center justify-center gap-3"
            >
              {loading ? <div className="w-6 h-6 border-4 border-white/30 border-t-white rounded-full animate-spin"></div> : t.loginBtn}
            </button>
          </form>

          <div className="mt-10 text-center space-y-4">
             <p className="text-gray-400 font-bold text-sm">
               {t.noAccount} <span onClick={() => navigate('/auth')} className="text-[#1A365D] cursor-pointer hover:underline">{t.createAccount}</span>
             </p>
             <button 
               onClick={handleWhatsAppHelp}
               className="flex items-center justify-center gap-2 w-full text-[#25D366] font-bold text-sm hover:opacity-80 transition-opacity"
             >
               <span className="text-xl">ðŸ’¬</span> WhatsApp Support
             </button>
          </div>
        </div>
      </div>
    </div>
  );
}
