import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "./00_LanguagePage.css";

export default function LanguagePage() {
  const [selected, setSelected] = useState("ar");
  const { changeLanguage } = useAuth();
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const handleLanguageSelect = (lang) => {
    setSelected(lang);
  };

  const handleNext = async () => {
    await changeLanguage(selected);
    navigate("/entry");
  };

  return (
    <div className={`fixed inset-0 bg-[#E3DAD0] flex items-center justify-center overflow-hidden transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>

      {/* ÿØŸàÿßÿ¶ÿ± ÿÆŸÑŸÅŸäÿ© ÿ¨ŸÖÿßŸÑŸäÿ© */}
      <div className="absolute w-[500px] h-[500px] bg-[#1A365D]/5 rounded-full -top-20 -right-20 animate-pulse"></div>
      <div className="absolute w-[300px] h-[300px] bg-white/20 rounded-full -bottom-10 -left-10 animate-bounce" style={{ animationDuration: '6s' }}></div>

      <div className="relative z-10 w-full max-w-sm px-8 flex flex-col items-center">

        {/* ÿßŸÑÿ¥ÿπÿßÿ± */}
        <div className="mb-12 transform transition-transform duration-1000 scale-110">
          <div className="w-40 h-40 rounded-full border-[6px] border-[#1A365D] shadow-2xl overflow-hidden bg-white p-1">
             <img src="/logo.jpg" alt="Careerak Logo" className="w-full h-full object-cover rounded-full" />
          </div>
        </div>

        {/* ŸÉÿ±Ÿàÿ™ ÿßŸÑŸÑÿ∫ÿßÿ™ */}
        <div className="w-full space-y-4 mb-12">
          {[
            { id: 'ar', label: 'ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', flag: 'üá∏üá¶' },
            { id: 'en', label: 'English', flag: 'üá∫üá∏' },
            { id: 'fr', label: 'Fran√ßais', flag: 'üá´üá∑' }
          ].map((lang) => (
            <button
              key={lang.id}
              onClick={() => handleLanguageSelect(lang.id)}
              className={`w-full p-6 rounded-[2.5rem] flex items-center justify-between transition-all duration-300 border-2 ${
                selected === lang.id
                ? 'bg-[#1A365D] border-[#1A365D] text-white shadow-2xl scale-105'
                : 'bg-white/60 border-transparent text-[#1A365D] hover:bg-white'
              }`}
            >
              <span className="text-xl font-black">{lang.label}</span>
              <span className="text-3xl">{lang.flag}</span>
            </button>
          ))}
        </div>

        {/* ÿ≤ÿ± ÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ© */}
        <button
          onClick={handleNext}
          className="w-full bg-[#1A365D] text-white py-6 rounded-[3rem] font-black text-2xl shadow-2xl active:scale-95 transition-all flex items-center justify-center gap-3"
        >
          {selected === 'ar' ? 'ÿßÿ≥ÿ™ŸÖÿ±ÿßÿ±' : selected === 'en' ? 'Continue' : 'Continuer'}
          <span className={`${selected === 'ar' ? 'rotate-180' : ''}`}>‚ûî</span>
        </button>

      </div>
    </div>
  );
}
