import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { AuthProvider, useAuth } from "./context/AuthContext";

import LanguagePage from "./pages/00_LanguagePage";
import EntryPage from "./pages/00_EntryPage";
import { AuthPage } from "./pages/03_AuthPage";

function AppRoutes() {
  const { language, audioEnabled, loading } = useAuth();

  if (loading) return null;

  // أول تشغيل فقط
  if (!language || audioEnabled === null) {
    return <LanguagePage />;
  }

  return (
    <Routes>
      <Route path="/" element={<EntryPage />} />
      <Route path="/auth" element={<AuthPage lang={language} />} />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <AppRoutes />
      </Router>
    </AuthProvider>
  );
}
