import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import api from '../services/api';

export default function ApplyPage() {
  const { jobId } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [isVisible, setIsVisible] = useState(false);

  const [formData, setFormData] = useState({
    expectedSalary: '',
    availability: 'immediate',
    workPreference: 'office',
    coverLetter: ''
  });

  useEffect(() => { setIsVisible(true); }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post(`/jobs/${jobId}/apply`, formData);
      alert('تم تقديم طلبك بنجاح!');
      navigate('/job-postings');
    } catch (err) {
      alert('حدث خطأ أثناء التقديم');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen bg-[#E3DAD0] p-6 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      <div className="max-w-2xl mx-auto bg-white rounded-[3.5rem] shadow-2xl p-10 text-right" dir="rtl">
        <h2 className="text-3xl font-black text-[#1A365D] mb-8 border-r-8 border-[#1A365D] pr-4">تقديم طلب وظيفة</h2>
        
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-bold text-gray-400 mb-2">الراتب المتوقع (دولار)</label>
            <input 
              type="number" 
              className="w-full p-4 bg-gray-50 rounded-2xl border-2 border-transparent focus:border-[#1A365D]/10 outline-none font-black"
              value={formData.expectedSalary}
              onChange={(e) => setFormData({...formData, expectedSalary: e.target.value})}
              required
            />
          </div>

          <div>
            <label className="block text-sm font-bold text-gray-400 mb-2">تفضيل العمل</label>
            <select 
              className="w-full p-4 bg-gray-50 rounded-2xl border-2 border-transparent focus:border-[#1A365D]/10 outline-none font-black"
              value={formData.workPreference}
              onChange={(e) => setFormData({...formData, workPreference: e.target.value})}
            >
              <option value="office">مكتبي</option>
              <option value="remote">عن بعد</option>
              <option value="field">ميداني</option>
            </select>
          </div>

          <button 
            type="submit" 
            disabled={loading}
            className="w-full bg-[#1A365D] text-white py-6 rounded-[2.5rem] font-black text-xl shadow-xl active:scale-95 transition-all mt-8"
          >
            {loading ? "جاري الإرسال..." : "تأكيد تقديم الطلب"}
          </button>
        </form>
      </div>
    </div>
  );
}
