import React, { useState, useEffect } from 'react';
import api from '../services/api';

export default function CoursesPage() {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const res = await api.get('/courses');
        setCourses(res.data);
      } catch (err) {
        console.error("Failed to fetch courses");
      } finally {
        setLoading(false);
      }
    };
    fetchCourses();
  }, []);

  return (
    <div className="min-h-screen bg-[#E3DAD0] p-6" dir="rtl">
      <h2 className="text-3xl font-black text-[#1A365D] mb-8 border-r-8 border-[#1A365D] pr-4">الدورات المتاحة</h2>
      
      {loading ? (
        <div className="text-center font-bold text-[#1A365D]">جاري التحميل...</div>
      ) : (
        <div className="grid gap-6">
          {courses.map(course => (
            <div key={course._id} className="bg-white p-6 rounded-[2.5rem] shadow-xl border border-white/50">
              <h3 className="text-xl font-black text-[#1A365D]">{course.title}</h3>
              <p className="text-gray-400 font-bold mb-4">{course.instructor}</p>
              <div className="flex justify-between items-center mt-4">
                <span className="text-[#1A365D] font-black">{course.price || "مجانية"}</span>
                <button className="bg-[#1A365D] text-white px-6 py-2 rounded-xl font-bold">تسجيل الآن</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
