import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Camera } from '@capacitor/camera';
import { Geolocation } from '@capacitor/geolocation';

const translations = {
  ar: {
    welcome: "مرحباً بك في كاريرك",
    slogan: "شريكك المهني الذكي",
    description: "شريكك الاستراتيجي للارتقاء بمسارك المهني وإدارة مواردك البشرية بمنتهى الذكاء",
    start: "ابدأ الرحلة",
    jobs: "وظيفة",
    courses: "دورة",
    users: "مستخدم"
  },
  en: {
    welcome: "Welcome to Careerak",
    slogan: "Your Smart Career Partner",
    description: "Your strategic partner to advance your career and manage your HR with absolute intelligence",
    start: "Start Journey",
    jobs: "Jobs",
    courses: "Courses",
    users: "Users"
  },
  fr: {
    welcome: "Bienvenue sur Careerak",
    slogan: "Votre partenaire carrière intelligent",
    description: "Votre partenaire stratégique pour faire progresser votre carrière et gérer vos RH avec une intelligence absolue",
    start: "Commencer",
    jobs: "Emplois",
    courses: "Cours",
    users: "Utilisateurs"
  }
};

export const WelcomePage = () => {
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState(false);
  const lang = localStorage.getItem('lang') || 'ar';
  const t = translations[lang] || translations.en;
  const isArabic = lang === 'ar';

  useEffect(() => {
    setIsVisible(true);
    const requestPermissions = async () => {
      try {
        await Camera.requestPermissions();
        await Geolocation.requestPermissions();
      } catch (error) {}
    };
    requestPermissions();
  }, []);

  const NAVY = '#1A365D';
  const BEIGE = '#E3DAD0';

  return (
    <div 
      className={`min-h-screen flex flex-col items-center justify-center p-6 text-center transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} 
      style={{ backgroundColor: BEIGE }}
    >
      {/* عنصر جمالي في الخلفية */}
      <div className="absolute top-0 right-0 w-64 h-64 bg-[#1A365D] opacity-[0.02] rounded-full -mr-32 -mt-32 blur-3xl"></div>
      <div className="absolute bottom-0 left-0 w-96 h-96 bg-[#1A365D] opacity-[0.03] rounded-full -ml-48 -mb-48 blur-3xl"></div>

      {/* اللوجو مع أنيميشن الدخول */}
      <div className={`mb-12 relative group transition-all duration-1000 transform ${isVisible ? 'scale-100' : 'scale-75 opacity-0'}`}>
        <div className="absolute inset-0 bg-[#1A365D]/5 rounded-full blur-[80px] group-hover:blur-[100px] transition-all"></div>
        <img 
          src="/logo.jpg" 
          alt="Careerak Logo" 
          className="relative h-48 w-48 md:h-56 md:w-56 mx-auto rounded-full shadow-[0_30px_70px_rgba(26,54,93,0.25)] border-[8px] border-white object-cover transform transition-all duration-700 hover:scale-105"
        />
      </div>

      {/* النصوص الترحيبية */}
      <div className={`max-w-2xl px-4 mb-16 transition-all duration-1000 delay-300 transform ${isVisible ? 'translate-y-0' : 'translate-y-10 opacity-0'}`}>
        <h1 className="text-5xl md:text-7xl font-black text-[#1A365D] mb-6 tracking-tight leading-tight">
          {t.welcome}
        </h1>
        <div className="flex items-center justify-center gap-4 mb-8">
          <div className="h-[1px] w-12 bg-[#1A365D]/20"></div>
          <p className="text-xl md:text-2xl font-bold text-[#1A365D]/60 italic tracking-wide">
            {t.slogan}
          </p>
          <div className="h-[1px] w-12 bg-[#1A365D]/20"></div>
        </div>
        <p className="text-[#1A365D]/50 text-lg md:text-xl leading-relaxed max-w-lg mx-auto font-medium">
          {t.description}
        </p>
      </div>

      {/* زر البداية بقواعد الدستور (حواف دائرية واسعة + فخامة) */}
      <div className={`w-full max-w-sm transition-all duration-1000 delay-500 transform ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
        <button
          onClick={() => navigate('/auth')}
          className="w-full bg-[#1A365D] hover:bg-[#152c4d] text-white font-black py-7 px-12 rounded-[3rem] shadow-[0_25px_50px_-12px_rgba(26,54,93,0.5)] transform transition-all hover:-translate-y-1 active:scale-95 text-2xl group flex items-center justify-center gap-4 overflow-hidden relative"
        >
          <div className="absolute inset-0 bg-white/5 translate-x-full group-hover:translate-x-0 transition-transform duration-500"></div>
          <span className="relative z-10">{t.start}</span>
          <span className={`relative z-10 transform transition-transform duration-500 ${isArabic ? 'group-hover:-translate-x-3' : 'group-hover:translate-x-3'}`}>
            {isArabic ? '←' : '→'}
          </span>
        </button>
      </div>

      {/* إحصائيات بلمسة خفيفة */}
      <div className={`mt-24 grid grid-cols-3 gap-10 md:gap-20 opacity-20 transition-all duration-1000 delay-700 ${isVisible ? 'opacity-20' : 'opacity-0'}`}>
        <div className="flex flex-col items-center">
          <span className="text-2xl md:text-3xl font-black text-[#1A365D]">10K+</span>
          <span className="text-[10px] md:text-xs font-bold uppercase tracking-[0.3em]">{t.jobs}</span>
        </div>
        <div className="flex flex-col items-center">
          <span className="text-2xl md:text-3xl font-black text-[#1A365D]">500+</span>
          <span className="text-[10px] md:text-xs font-bold uppercase tracking-[0.3em]">{t.courses}</span>
        </div>
        <div className="flex flex-col items-center">
          <span className="text-2xl md:text-3xl font-black text-[#1A365D]">50K+</span>
          <span className="text-[10px] md:text-xs font-bold uppercase tracking-[0.3em]">{t.users}</span>
        </div>
      </div>
    </div>
  );
};
