import React from 'react';
import { useAuth } from '../context/AuthContext';

export const LanguagePage = ({ onSelect }) => {
  const { changeLanguage } = useAuth();

  const handleSelect = (lang) => {
    changeLanguage(lang);
    if (onSelect) onSelect();
  };

  return (
    <div className="fixed inset-0 z-[10000] flex flex-col items-center justify-center p-6 bg-[#E3DAD0]">
      <div className="absolute w-[150vw] h-[150vw] bg-[#1A365D] opacity-[0.03] rounded-full top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 animate-pulse"></div>
      <div className="relative z-10 flex flex-col items-center w-full max-w-md text-center">
        <div className="mb-8 relative">
          <img src="/logo.jpg" alt="Logo" className="w-32 h-32 rounded-full border-4 border-white shadow-2xl relative z-10 object-cover" />
        </div>
        <h2 className="text-xl font-black text-[#1A365D] mb-8 uppercase tracking-tighter">
          اختار اللغة / Select Language / Choisir la langue
        </h2>
        <div className="flex flex-col gap-4 w-full px-4">
          <button onClick={() => handleSelect('ar')} className="bg-[#1A365D] text-white py-5 rounded-[2rem] font-black text-xl shadow-lg active:scale-95 transition-all">العربية</button>
          <button onClick={() => handleSelect('en')} className="bg-white text-[#1A365D] py-5 rounded-[2rem] font-black text-xl border-2 border-[#1A365D]/20 shadow-md active:scale-95 transition-all">English</button>
          <button onClick={() => handleSelect('fr')} className="bg-white text-[#1A365D] py-5 rounded-[2rem] font-black text-xl border-2 border-[#1A365D]/20 shadow-md active:scale-95 transition-all">Français</button>
        </div>
      </div>
    </div>
  );
};
