import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { userService } from '../services/api';
import { useAuth } from '../context/AuthContext';

export default function OnboardingCompanies() {
  const navigate = useNavigate();
  const { language, updateUser } = useAuth();
  const [loading, setLoading] = useState(false);
  const [socialLoading, setSocialLoading] = useState(false);
  const [error, setError] = useState('');
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => { setIsVisible(true); }, []);

  const [formData, setFormData] = useState({
    bio: '', website: '', commercialRegister: '', employeesCount: '',
    country: '', industry: '', address: '',
    linkedIn: '', facebook: ''
  });

  const t = {
    ar: { 
      title: 'ÿ™Ÿàÿ´ŸäŸÇ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖŸÜÿ¥ÿ£ÿ©', 
      sub: 'ÿ£ŸÉŸÖŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿ¥ÿ±ŸÉÿ™ŸÉÿå Ÿàÿ≥ŸäŸÇŸàŸÖ ÿ∞ŸÉÿßÿ° ŸÉÿßÿ±Ÿäÿ±ŸÉ ÿ®ÿ™ÿ≠ŸÑŸäŸÑ ÿ±Ÿàÿßÿ®ÿ∑ŸÉ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ü§ñ', 
      desc: 'ŸàÿµŸÅ ŸÜÿ¥ÿßÿ∑ ÿßŸÑÿ¥ÿ±ŸÉÿ© Ÿàÿ±ÿ§Ÿäÿ™Ÿáÿß...', 
      social: 'ÿ±Ÿàÿßÿ®ÿ∑ ÿßŸÑÿ™ŸàÿßÿµŸÑ (ÿ≥Ÿäÿ™ŸÖ ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã üîç)',
      analyzing: 'ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÑŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖŸÜÿ¥ÿ£ÿ©... ü§ñ',
      finish: 'ÿ≠ŸÅÿ∏ ŸàÿßŸÑÿ®ÿØÿ° ÿ®ÿßŸÑÿ™Ÿàÿ∏ŸäŸÅ',
      error: 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ÿå Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÑÿßÿ≠ŸÇÿßŸã'
    },
    en: {
      title: 'Company Verification',
      sub: 'Complete your company details, Careerak AI will analyze your links automatically ü§ñ',
      desc: 'Company description and vision...',
      social: 'Social Links (Auto-analyzed üîç)',
      analyzing: 'Analyzing company data... ü§ñ',
      finish: 'Save and Start Hiring',
      error: 'An error occurred, please try again later'
    }
  }[language || 'ar'];

  const autoAnalyzeCompany = async (platform, url) => {
    if (!url || !url.startsWith('http')) return;
    setSocialLoading(true);
    try {
      const res = await userService.parseSocial({ url, platform, type: 'company' });
      const aiData = res.data.data;
      setFormData(prev => ({
        ...prev,
        bio: aiData.bio || prev.bio,
        industry: aiData.industry || prev.industry,
        website: aiData.website || prev.website
      }));
    } catch (err) {
      console.error("Auto company analysis failed", err);
    } finally {
      setSocialLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const res = await userService.updateProfile(formData);
      updateUser(res.data.user);
      navigate('/profile');
    } catch (err) {
      setError(err.response?.data?.error || t.error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen flex flex-col items-center justify-center p-4 md:p-8 bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={language === 'ar' ? 'rtl' : 'ltr'}>
      <div className={`w-full max-w-2xl bg-white rounded-[4rem] shadow-2xl p-8 md:p-16 border border-white transform transition-all duration-1000 ${isVisible ? 'translate-y-0' : 'translate-y-10'}`}>
        
        <div className="mb-12 text-center">
          <div className="w-24 h-24 bg-[#1A365D]/5 text-[#1A365D] rounded-full flex items-center justify-center mx-auto mb-6 shadow-inner">
            <span className="text-4xl">üè¢</span>
          </div>
          <h2 className="text-3xl md:text-4xl font-black text-[#1A365D] mb-4">{t.title}</h2>
          <p className="text-[#1A365D]/40 font-bold text-sm">{t.sub}</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <section className="space-y-4 pt-4 border-t border-gray-50">
            <h3 className="text-xs font-black text-[#1A365D]/40 uppercase tracking-widest mr-4">{t.social}</h3>
            {socialLoading && <p className="text-[10px] text-blue-600 animate-pulse font-black px-4">{t.analyzing}</p>}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-5">
               <div className="relative group">
                 <span className="absolute right-5 top-1/2 -translate-y-1/2 text-xl opacity-40">üîó</span>
                 <input 
                   type="url" 
                   placeholder="LinkedIn URL" 
                   value={formData.linkedIn} 
                   onChange={e => setFormData({...formData, linkedIn: e.target.value})} 
                   onBlur={() => autoAnalyzeCompany('linkedin', formData.linkedIn)}
                   className="w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-sm pr-14 border-2 border-transparent focus:border-[#1A365D]/20 focus:bg-white transition-all placeholder-gray-300 shadow-inner"
                 />
               </div>
               <div className="relative group">
                 <span className="absolute right-5 top-1/2 -translate-y-1/2 text-xl opacity-40">üìò</span>
                 <input 
                   type="url" 
                   placeholder="Facebook URL" 
                   value={formData.facebook} 
                   onChange={e => setFormData({...formData, facebook: e.target.value})} 
                   onBlur={() => autoAnalyzeCompany('facebook', formData.facebook)}
                   className="w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-sm pr-14 border-2 border-transparent focus:border-[#1A365D]/20 focus:bg-white transition-all placeholder-gray-300 shadow-inner"
                 />
               </div>
            </div>
          </section>

          <textarea 
            placeholder={t.desc} 
            value={formData.bio}
            className="w-full p-6 bg-gray-50 rounded-[2.5rem] outline-none font-bold text-sm h-32 shadow-inner border-2 border-transparent focus:border-[#1A365D]/10 focus:bg-white transition-all placeholder-gray-300"
            onChange={e => setFormData({...formData, bio: e.target.value})} 
          />

          {error && <div className="p-4 bg-red-50 text-red-600 rounded-2xl text-[11px] font-black text-center border border-red-100">{error}</div>}

          <button type="submit" disabled={loading || socialLoading} className="w-full py-7 rounded-[3rem] font-black shadow-2xl bg-[#1A365D] text-white text-2xl active:scale-95 transition-all mt-6">
            {loading ? <div className="w-8 h-8 border-4 border-white/30 border-t-white rounded-full animate-spin mx-auto"></div> : t.finish}
          </button>
        </form>
      </div>
    </div>
  );
}
