    jobType: '', languages: '', computerSkills: '', otherSkills: '',
    materialBenefits: '', moralBenefits: '', ageRequired: '', experienceYears: '',
    requiredCourses: '', travelType: '', licenseLevel: '', otherInfo: '', description: ''
  });

  const t = {
    ar: {
