      labels: {
        title: 'Ø§Ù„Ù…Ø³Ù…Ù‰ Ø§Ù„ÙˆØ¸ÙŠÙÙŠ', education: 'Ø§Ù„Ù…Ø¤Ù‡Ù„ Ø§Ù„Ø¯Ø±Ø§Ø³ÙŠ Ø§Ù„Ù…Ø·Ù„ÙˆØ¨', salary: 'Ø§Ù„Ø±Ø§ØªØ¨ ÙˆØ§Ù„Ù…Ù…ÙŠØ²Ø§Øª Ø§Ù„Ù…Ø§Ù„ÙŠØ©',
        hours: 'Ø¹Ø¯Ø¯ Ø³Ø§Ø¹Ø§Øª Ø§Ù„Ø¹Ù…Ù„', city: 'Ù…ÙƒØ§Ù† Ø§Ù„ÙˆØ¸ÙŠÙØ© (Ø§Ù„Ù…Ø¯ÙŠÙ†Ø©)', type: 'Ù†ÙˆØ¹ Ø§Ù„Ø¹Ù…Ù„',
        age: 'Ø§Ù„Ø³Ù† Ø§Ù„Ù…Ø·Ù„ÙˆØ¨', experience: 'Ø¹Ø¯Ø¯ Ø³Ù†ÙˆØ§Øª Ø§Ù„Ø®Ø¨Ø±Ø©', courses: 'Ø§Ù„Ø¯ÙˆØ±Ø§Øª Ø§Ù„Ù…ØªØ¨Ø¹Ø© Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©',
        languages: 'Ø§Ù„Ù„ØºØ§Øª Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©', computer: 'Ù…Ù‡Ø§Ø±Ø© Ø§Ù„Ø­Ø§Ø³ÙˆØ¨', skills: 'Ù…Ù‡Ø§Ø±Ø§Øª Ø£Ø®Ø±Ù‰',
        material: 'Ø§Ù„Ù…ÙŠØ²Ø§Øª Ø§Ù„Ù…Ø§Ø¯ÙŠØ© Ø§Ù„Ù…Ù‚Ø¯Ù…Ø©', moral: 'Ø§Ù„Ù…ÙŠØ²Ø§Øª Ø§Ù„Ù…Ø¹Ù†ÙˆÙŠØ© ÙˆØ§Ù„Ø¨ÙŠØ¦ÙŠØ©',
        travel: 'Ù‡Ù„ ØªØªØ·Ù„Ø¨ Ø§Ù„ÙˆØ¸ÙŠÙØ© ØªÙ†Ù‚Ù„ØŸ', license: 'Ø±Ø®ØµØ© Ø§Ù„Ù‚ÙŠØ§Ø¯Ø© Ø§Ù„Ù…Ø·Ù„ÙˆØ¨Ø©',
        other: 'Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ© Ù‡Ø§Ù…Ø©', desc: 'Ø§Ù„Ù…Ù‡Ø§Ù… ÙˆØ§Ù„Ù…Ø³Ø¤ÙˆÙ„ÙŠØ§Øª Ø§Ù„ÙˆØ¸ÙŠÙÙŠØ© Ø¨Ø§Ù„ØªÙØµÙŠÙ„'
      },
      placeholders: {
        travel: 'Ù…Ø«Ø§Ù„: Ù…Ø­Ù„ÙŠØŒ Ø¥Ù‚Ù„ÙŠÙ…ÙŠØŒ Ù„Ø§ ÙŠÙˆØ¬Ø¯',
        type: 'Ù…Ø«Ø§Ù„: Ø¥Ø¯Ø§Ø±ÙŠØŒ Ù…ÙŠØ¯Ø§Ù†ÙŠØŒ Ø¹Ù† Ø¨Ø¹Ø¯',
        license: 'Ù…Ø«Ø§Ù„: Ø®Ø§ØµØ©ØŒ Ø¹Ù…ÙˆÙ…ÙŠØŒ Ù„Ø§ ÙŠÙˆØ¬Ø¯'
      },
      submit: 'Ø§Ø¹ØªÙ…Ø§Ø¯ ÙˆÙ†Ø´Ø± Ø§Ù„ÙˆØ¸ÙŠÙØ© Ø§Ù„Ø¢Ù†'
