  }[lang] || { ar: {} };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/job-postings', formData);
      navigate('/04_JobPostingsPage');
    } catch (err) {
      alert('âš ï¸ Ø­Ø¯Ø« Ø®Ø·Ø£ Ø£Ø«Ù†Ø§Ø¡ Ø¹Ù…Ù„ÙŠØ© Ø§Ù„Ù†Ø´Ø±ØŒ ÙŠØ±Ø¬Ù‰ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ù„Ø§Ø­Ù‚Ø§Ù‹');
    } finally {
      setLoading(false);
    }
  };

  const PRIMARY_NAVY = '#1A365D';

  const InputField = ({ label, name, placeholder, type = 'text' }) => (
    <div className="space-y-2 group">
      <label className="text-[11px] font-black text-[#1A365D]/40 px-4 uppercase tracking-widest">{label}</label>
      <input 
        type={type} name={name} value={formData[name]} onChange={handleInputChange} placeholder={placeholder}
        className={`w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-base border-2 border-transparent focus:border-[#1A365D]/10 focus:bg-white transition-all shadow-inner placeholder-gray-300 text-[${PRIMARY_NAVY}]`}
      />
    </div>
  );

  return (
    <div className={`min-h-screen p-4 md:p-8 bg-[#E3DAD0] pb-32 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      <div className={`max-w-4xl mx-auto bg-white rounded-[4rem] shadow-[0_40px_100px_-20px_rgba(26,54,93,0.15)] p-8 md:p-16 border border-white/50 transform transition-all duration-1000 ${isVisible ? 'translate-y-0' : 'translate-y-10'}`}>
        
        <div className="text-center mb-16">
          <div className="w-24 h-24 bg-[#1A365D]/5 text-[#1A365D] rounded-full flex items-center justify-center mx-auto mb-6 shadow-inner relative group">
            <span className="text-4xl">ðŸ’¼</span>
          </div>
          <h2 className="text-4xl font-black text-[#1A365D] tracking-tight">{t.ar.title}</h2>
          <p className="text-[#1A365D]/40 font-bold mt-2 italic">{t.ar.sub}</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-16 text-right" dir="rtl">
          
          {/* Section 1 */}
          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec1}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <InputField label={t.ar.labels.title} name="title" />
              <InputField label={t.ar.labels.city} name="location" />
              <InputField label={t.ar.labels.type} name="jobType" placeholder={t.ar.placeholders.type} />
              <InputField label={t.ar.labels.age} name="ageRequired" type="number" />
            </div>
          </section>

          {/* Section 2 */}
          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec2}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <InputField label={t.ar.labels.education} name="education" />
              <InputField label={t.ar.labels.experience} name="experienceYears" />
            </div>
            <InputField label={t.ar.labels.courses} name="requiredCourses" />
          </section>

          {/* Section 3 */}
          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec3}</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <InputField label={t.ar.labels.languages} name="languages" />
              <InputField label={t.ar.labels.computer} name="computerSkills" />
              <InputField label={t.ar.labels.skills} name="otherSkills" />
            </div>
          </section>

          {/* Section 4 */}
          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec4}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <InputField label={t.ar.labels.salary} name="salary" />
              <InputField label={t.ar.labels.hours} name="workHours" />
              <InputField label={t.ar.labels.travel} name="travelType" placeholder={t.ar.placeholders.travel} />
              <InputField label={t.ar.labels.license} name="licenseLevel" placeholder={t.ar.placeholders.license} />
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <InputField label={t.ar.labels.material} name="materialBenefits" />
              <InputField label={t.ar.labels.moral} name="moralBenefits" />
            </div>
          </section>

          {/* Section 5 */}
          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec5}</h3>
            <textarea 
              name="description" value={formData.description} onChange={handleInputChange} placeholder={t.ar.labels.desc}
              className={`w-full p-8 bg-gray-50 rounded-[3rem] outline-none font-bold text-base h-48 shadow-inner border-2 border-transparent focus:border-[#1A365D]/10 focus:bg-white transition-all placeholder-gray-300 text-[${PRIMARY_NAVY}]`}
            />
            <InputField label={t.ar.labels.other} name="otherInfo" />
          </section>

          <button 
            type="submit" disabled={loading}
            className="w-full py-7 rounded-[3.5rem] font-black shadow-[0_25px_50px_-12px_rgba(26,54,93,0.35)] active:scale-95 transition-all text-2xl bg-[#1A365D] text-white hover:bg-[#152c4d] hover:-translate-y-1 flex items-center justify-center gap-4"
          >
            {loading ? <div className="w-8 h-8 border-4 border-white/30 border-t-white rounded-full animate-spin"></div> : t.ar.submit}
          </button>

        </form>
      </div>
    </div>
  );
};
