import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useNavigate, Link } from 'react-router-dom';
import { StatusBar, Style } from '@capacitor/status-bar';
import { App as CapApp } from '@capacitor/app';
import { AuthProvider, useAuth } from './context/AuthContext';
import { WelcomePage } from './pages/01_WelcomePage';
import { AuthPage } from './pages/03_AuthPage';
import { OnboardingIndividuals } from './pages/03_OnboardingIndividuals';
import { OnboardingCompanies } from './pages/03_OnboardingCompanies';
import { OTPVerification } from './pages/03_OTPVerification';
import { SplashScreen } from './components/SplashScreen';
import { FloatingWhatsApp } from './components/FloatingWhatsApp';
import { Navbar } from './components/Navbar';
import { SettingsPage } from './pages/08_SettingsPage';
import { PolicyPage } from './pages/07_PolicyPage';
import api from './services/api';
import './index.css';

const BottomNavbar = ({ user, lang }) => {
  const isArabic = lang === 'ar';
  const isFrench = lang === 'fr';
  const speak = (text) => {
    const u = new SpeechSynthesisUtterance(text);
    u.lang = isArabic ? "ar-SA" : (isFrench ? "fr-FR" : "en-US");
    window.speechSynthesis.speak(u);
  };

  const navItems = [
    { label: isArabic ? 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©' : (isFrench ? 'Accueil' : 'Home'), path: '/dashboard', icon: 'üè†' },
    { label: isArabic ? 'ÿßŸÑÿÆÿØŸÖÿßÿ™' : (isFrench ? 'Services' : 'Services'), path: '/services', icon: 'üõ†Ô∏è' },
    { 
      label: user?.role === 'HR' ? (isArabic ? 'ÿ•ÿ∂ÿßŸÅÿ©' : (isFrench ? 'Ajouter' : 'Add')) : (isArabic ? 'ÿ∑ŸÑÿ®' : (isFrench ? 'Demander' : 'Request')), 
      path: user?.role === 'HR' ? '/post-job' : '/apply-job', 
      icon: '‚ûï',
      special: true 
    },
    { label: isArabic ? 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™' : (isFrench ? 'Param√®tres' : 'Settings'), path: '/settings', icon: '‚öôÔ∏è' },
    { label: isArabic ? 'ÿ≠ÿ≥ÿßÿ®Ÿä' : (isFrench ? 'Profil' : 'Profile'), path: '/profile', icon: 'üë§' },
  ];

  return (
    <div className="fixed bottom-0 left-0 right-0 z-[1000] px-4 pb-6 pt-2 bg-gradient-to-t from-[#E3DAD0] to-transparent pointer-events-none">
      <div className="max-w-md mx-auto bg-white/90 backdrop-blur-xl rounded-[2.5rem] shadow-[0_20px_50px_rgba(26,54,93,0.2)] border border-white/50 flex justify-between items-center p-2 pointer-events-auto">
        {navItems.map((item, i) => (
          <Link 
            key={i} 
            to={item.path}
            onContextMenu={(e) => { e.preventDefault(); speak(item.label); }}
            className={`flex flex-col items-center justify-center transition-all active:scale-90 ${item.special ? 'bg-[#1A365D] text-white w-14 h-14 rounded-full shadow-lg -mt-8' : 'flex-1 text-gray-400'}`}
          >
            <span className={`${item.special ? 'text-2xl' : 'text-xl'}`}>{item.icon}</span>
            {!item.special && <span className="text-[8px] font-black mt-1 uppercase tracking-tighter">{item.label}</span>}
          </Link>
        ))}
      </div>
    </div>
  );
};

function AppContent() {
  const { isAuthenticated, user, loading: authLoading, language, changeLanguage } = useAuth();
  const [showSplash, setShowSplash] = useState(true);
  const [showLangSelect, setShowLangSelect] = useState(!localStorage.getItem('lang')); 
  
  const location = useLocation();
  const navigate = useNavigate();
  const bgMusic = useRef(null);

  useEffect(() => {
    if (!showSplash) {
      if (!bgMusic.current) {
        bgMusic.current = new Audio('/Music.mp3');
        bgMusic.current.loop = true;
        bgMusic.current.volume = 0.4;
      }
      const playMusic = () => {
        bgMusic.current.play().catch(e => console.log("Music waiting"));
      };
      playMusic();
      window.addEventListener('click', playMusic, { once: true });
    }
    return () => { if (bgMusic.current) bgMusic.current.pause(); };
  }, [showSplash]);

  useEffect(() => {
    const setupApp = async () => {
      try { 
        await StatusBar.setBackgroundColor({ color: '#E3DAD0' }); 
        await StatusBar.setStyle({ style: Style.Light }); 
      } catch (e) {}
      
      CapApp.addListener('backButton', () => {
        if (location.pathname === '/' || showLangSelect || location.pathname === '/dashboard') {
          CapApp.exitApp();
        } else {
          window.history.back();
        }
      });
    };
    setupApp();
    return () => { CapApp.removeAllListeners(); };
  }, [location, showLangSelect]);

  if (showSplash || authLoading) return <SplashScreen onFinish={() => setShowSplash(false)} />;
  if (showLangSelect) return <LanguageSelect onSelect={(l) => { changeLanguage(l); setShowLangSelect(false); }} />;

  return (
    <div className="flex flex-col min-h-screen w-full bg-[#E3DAD0] font-arabic overflow-x-hidden" dir={language === 'ar' ? 'rtl' : 'ltr'}>
      {isAuthenticated && <Navbar lang={language} user={user} />}
      <FloatingWhatsApp />
      
      <main className={`flex-grow w-full max-w-screen-xl mx-auto px-6 ${isAuthenticated ? 'pb-32 pt-4' : 'py-4'}`}>
        <Routes>
          <Route path="/" element={<WelcomePage lang={language} />} />
          <Route path="/auth" element={<AuthPage lang={language} />} />
          <Route path="/policy" element={<PolicyPage lang={language} />} />
          <Route path="/verify-otp" element={<OTPVerification />} />
          <Route path="/onboarding-individuals" element={<OnboardingIndividuals lang={language} />} />
          <Route path="/onboarding-companies" element={<OnboardingCompanies lang={language} />} />
          <Route path="/settings" element={<SettingsPage lang={language} />} />
          
          {isAuthenticated ? (
            <>
              <Route path="/dashboard" element={<DashboardSwitcher user={user} language={language} />} />
              {user?.role === 'Admin' && (
                <>
                  <Route path="/preview/employee" element={<EmployeeDashboard user={{firstName: 'ŸÖÿπÿßŸäŸÜÿ©'}} />} />
                  <Route path="/preview/hr" element={<HRDashboard user={{companyName: 'ŸÖÿπÿßŸäŸÜÿ©'}} />} />
                  <Route path="/preview/special" element={<SimplifiedDashboard user={{firstName: 'ŸÖÿπÿßŸäŸÜÿ©'}} />} />
                </>
              )}
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/" />} />
          )}
        </Routes>
      </main>

      {isAuthenticated && <BottomNavbar user={user} lang={language} />}
    </div>
  );
}

const DashboardSwitcher = ({ user }) => {
  if (!user) return null;
  if (user.role === 'Admin') return <AdminDashboard />;
  if (user.education === 'ÿßŸÑÿ£ŸÖŸä' || user.isSpecialNeeds) return <SimplifiedDashboard user={user} />;
  if (user.role === 'HR') return <HRDashboard user={user} />;
  return <EmployeeDashboard user={user} />;
};

const AdminDashboard = () => {
  const navigate = useNavigate();
  return (
    <div className="w-full space-y-10 animate-fade-in pt-4">
      <div className="text-center space-y-2">
        <h1 className="text-4xl font-black text-[#1A365D]">Master Control Tower üõ∞Ô∏è</h1>
        <p className="text-gray-500 font-bold text-sm uppercase tracking-widest">ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑŸÖÿ±ŸÉÿ≤Ÿäÿ© - Careerak Admin</p>
      </div>
      
      <section className="bg-white/50 p-8 rounded-[3rem] border border-white shadow-xl">
        <h2 className="text-lg font-black text-[#1A365D] mb-6 border-r-4 border-[#1A365D] pr-3">ŸÖÿ≥ÿ™ŸÉÿ¥ŸÅ Ÿàÿßÿ¨Ÿáÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ (Live Preview)</h2>
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
          <button onClick={() => navigate('/preview/employee')} className="p-6 bg-white rounded-3xl shadow-md hover:shadow-lg transition-all font-black text-[#1A365D] flex items-center gap-3">üë§ Ÿàÿßÿ¨Ÿáÿ© ÿßŸÑÿ£ŸÅÿ±ÿßÿØ</button>
          <button onClick={() => navigate('/preview/hr')} className="p-6 bg-white rounded-3xl shadow-md hover:shadow-lg transition-all font-black text-[#1A365D] flex items-center gap-3">üè¢ Ÿàÿßÿ¨Ÿáÿ© ÿßŸÑÿ¥ÿ±ŸÉÿßÿ™</button>
          <button onClick={() => navigate('/preview/special')} className="p-6 bg-white rounded-3xl shadow-md hover:shadow-lg transition-all font-black text-[#1A365D] flex items-center gap-3">ü¶æ Ÿàÿßÿ¨Ÿáÿ© ÿ∞ŸàŸä ÿßŸÑŸáŸÖŸÖ</button>
        </div>
      </section>

      <div className="grid grid-cols-2 gap-6 pb-10">
        <DashboardCard title="ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ" icon="üìä" color="bg-white" />
        <DashboardCard title="ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ" icon="üë•" color="bg-white" />
        <DashboardCard title="ÿ•ÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ≥Ÿäÿ±ŸÅÿ±" icon="‚öôÔ∏è" color="bg-white" />
        <DashboardCard title="ÿßŸÑÿØÿπŸÖ ÿßŸÑŸÅŸÜŸä" icon="üõ°Ô∏è" color="bg-white" />
      </div>
    </div>
  );
};

const HRDashboard = ({ user }) => (
  <div className="w-full space-y-10 animate-fade-in text-center">
    <div className="bg-white p-8 rounded-[3rem] shadow-xl border-b-8 border-[#1A365D]">
      <h1 className="text-3xl font-black text-[#1A365D]">{user?.companyName} üè¢</h1>
      <p className="text-gray-500 font-bold mt-2 text-sm uppercase">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖŸàÿßÿ±ÿØ ŸàÿßŸÑÿ™Ÿàÿ∏ŸäŸÅ</p>
    </div>
    <div className="grid grid-cols-2 gap-4">
      <DashboardCard title="ŸÜÿ¥ÿ± Ÿàÿ∏ŸäŸÅÿ©" icon="‚ûï" color="bg-white" />
      <DashboardCard title="ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑÿ™Ÿàÿ∏ŸäŸÅ" icon="üì•" color="bg-white" />
      <DashboardCard title="ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™" icon="üîç" color="bg-white" />
      <DashboardCard title="ÿßŸÑÿ£ŸÉÿßÿØŸäŸÖŸäÿ©" icon="üéì" color="bg-white" />
    </div>
  </div>
);

const EmployeeDashboard = ({ user }) => (
  <div className="w-full space-y-8 animate-fade-in pb-10">
    <div className="text-center">
      <h1 className="text-3xl font-black text-[#1A365D]">ÿ£ŸáŸÑÿßŸãÿå {user?.firstName} üëã</h1>
      <p className="text-gray-400 font-bold mt-1 text-sm">ÿßÿ≥ÿ™ŸÉÿ¥ŸÅ ÿßŸÑŸÅÿ±ÿµ ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÉ ÿßŸÑŸäŸàŸÖ</p>
    </div>
    <div className="grid grid-cols-2 gap-4">
      <DashboardCard title="ŸÉŸÑ ÿßŸÑŸàÿ∏ÿßÿ¶ŸÅ" icon="üè¢" color="bg-white" />
      <DashboardCard title="ÿØŸàÿ±ÿßÿ™ ÿ™ÿØÿ±Ÿäÿ®Ÿäÿ©" icon="üéì" color="bg-white" />
      <DashboardCard title="ÿ≥Ÿäÿ±ÿ™Ÿä ÿßŸÑÿ∞ÿßÿ™Ÿäÿ©" icon="üìÑ" color="bg-white" />
      <DashboardCard title="ÿßŸÑŸÖŸÇÿ™ÿ±ÿ≠ÿßÿ™" icon="ü§ñ" color="bg-white" />
    </div>
  </div>
);

const SimplifiedDashboard = ({ user }) => {
  const speak = (txt) => { const u = new SpeechSynthesisUtterance(txt); u.lang="ar-SA"; window.speechSynthesis.speak(u); };
  return (
    <div className="flex flex-col gap-10 text-center py-10">
      <div className="bg-white p-10 rounded-[4rem] shadow-2xl border-b-8 border-[#1A365D]">
        <h1 className="text-4xl font-black text-[#1A365D]">ŸÖÿ±ÿ≠ÿ®ÿßŸãÿå {user?.firstName} üëã</h1>
        <p className="text-gray-500 font-bold mt-4 text-xl">ÿßÿ∂ÿ∫ÿ∑ ŸÖÿ∑ŸàŸÑÿßŸã ŸÑÿ≥ŸÖÿßÿπ ÿßŸÑÿÆŸäÿßÿ±</p>
      </div>
      <button 
        onContextMenu={(e) => { e.preventDefault(); speak("ÿ£ÿ®ÿ≠ÿ´ ÿπŸÜ ÿπŸÖŸÑ"); }}
        className="bg-green-600 p-16 rounded-[5rem] text-white text-5xl font-black shadow-2xl border-8 border-white active:scale-95 transition-all"
      >üíº</button>
      <button 
        onContextMenu={(e) => { e.preventDefault(); speak("ÿ£ÿ±ŸäÿØ ÿ£ŸÜ ÿ£ÿ™ÿπŸÑŸÖ ŸÖŸáÿßÿ±ÿ© ÿ¨ÿØŸäÿØÿ©"); }}
        className="bg-blue-600 p-16 rounded-[5rem] text-white text-5xl font-black shadow-2xl border-8 border-white active:scale-95 transition-all"
      >üéì</button>
    </div>
  );
};

const DashboardCard = ({ title, icon, color }) => (
  <button className={`${color} p-8 rounded-[3rem] shadow-lg flex flex-col items-center group active:scale-95 transition-all h-full w-full border border-[#1A365D]/5`}>
    <div className="text-4xl mb-4 group-hover:scale-110 transition-transform">{icon}</div>
    <h3 className="text-[10px] font-black text-[#1A365D] tracking-tight uppercase">{title}</h3>
  </button>
);

const LanguageSelect = ({ onSelect }) => (
  <div className="fixed inset-0 z-[10000] flex flex-col items-center justify-center p-6 bg-[#E3DAD0]">
    <div className="absolute w-[150vw] h-[150vw] bg-[#1A365D] opacity-[0.03] rounded-full top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 animate-pulse"></div>
    <div className="relative z-10 flex flex-col items-center w-full max-w-md text-center">
      <div className="mb-8 relative">
        <img src="/logo.jpg" alt="Logo" className="w-32 h-32 rounded-full border-4 border-white shadow-2xl relative z-10 object-cover" />
      </div>
      <h2 className="text-xl font-black text-[#1A365D] mb-8">ÿßÿÆÿ™ÿßÿ± ÿßŸÑŸÑÿ∫ÿ© / Select Language / Choisir la langue</h2>
      <div className="flex flex-col gap-4 w-full px-4">
        <button onClick={() => onSelect('ar')} className="bg-[#1A365D] text-white py-4 rounded-[2rem] font-black text-xl shadow-lg hover:scale-[1.02] transition-all">ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</button>
        <button onClick={() => onSelect('en')} className="bg-white text-[#1A365D] py-4 rounded-[2rem] font-black text-xl border-2 border-[#1A365D]/20 shadow-md hover:scale-[1.02] transition-all">English</button>
        <button onClick={() => onSelect('fr')} className="bg-white text-[#1A365D] py-4 rounded-[2rem] font-black text-xl border-2 border-[#1A365D]/20 shadow-md hover:scale-[1.02] active:scale-95 transition-all">Fran√ßais</button>
      </div>
    </div>
  </div>
);

function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}

export default App;
