import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function EntryPage() {
  const { audioEnabled } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    let audio;

    if (audioEnabled) {
      audio = new Audio("/intro.mp3");
      audio.play().catch(() => {});
    }

    const timer = setTimeout(() => {
      audio?.pause();
      navigate("/auth");
    }, 9000);

    return () => {
      clearTimeout(timer);
      audio?.pause();
    };
  }, []);

  return (
    <div className="min-h-screen bg-[#E3DAD0] flex items-center justify-center">
      {/* نفس التصميم السابق بدون تعديل */}
    </div>
  );
}
