import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Preferences } from "@capacitor/preferences";
import logo from "../assets/logo.png";

export default function LanguagePage() {
  const navigate = useNavigate();
  const [selectedLang, setSelectedLang] = useState("ar");

  const texts = {
    ar: {
      title: "اختر اللغة",
      confirm: "تأكيد",
    },
    en: {
      title: "Choose Language",
      confirm: "Confirm",
    },
    fr: {
      title: "Choisir la langue",
      confirm: "Confirmer",
    },
  };

  const t = texts[selectedLang];

  const confirmLanguage = async () => {
    await Preferences.set({ key: "lang", value: selectedLang });
    navigate("/auth");
  };

  return (
    <div className="relative min-h-screen bg-[#E3DAD0] flex flex-col items-center justify-center gap-6">
      {/* الشفافية الكحلية */}
      <div className="absolute inset-0 bg-[#1A365D]/40 animate-fadeSlow"></div>

      {/* اللوجو */}
      <div className="relative z-10 w-48 h-48 rounded-full border-2 border-[#1A365D] overflow-hidden">
        <img src={logo} alt="logo" className="w-full h-full object-cover scale-110" />
      </div>

      <h1 className="relative z-10 text-[#1A365D] font-bold text-xl">
        {t.title}
      </h1>

      <div className="relative z-10 flex gap-4">
        <button onClick={() => setSelectedLang("ar")}>العربية</button>
        <button onClick={() => setSelectedLang("en")}>English</button>
        <button onClick={() => setSelectedLang("fr")}>Français</button>
      </div>

      <button
        onClick={confirmLanguage}
        className="relative z-10 bg-[#1A365D] text-white px-6 py-2 rounded-full"
      >
        {t.confirm}
      </button>

      <style>{`
        @keyframes fadeSlow {
          0% { opacity: 0; }
          50% { opacity: .4; }
          100% { opacity: 0; }
        }
        .animate-fadeSlow {
          animation: fadeSlow 10s infinite;
        }
      `}</style>
    </div>
  );
}
