import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';

export const LanguagePage = ({ onSelect }) => {
  const { changeLanguage } = useAuth();
  const [isVisible, setIsVisible] = useState(false);
  const [selectedLang, setSelectedLang] = useState(null);
  const [showConfirm, setShowConfirm] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  const t = {
    ar: {
      confirmTitle: 'ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑŸÑÿ∫ÿ©',
      msg: 'ÿßŸÑŸÑÿ∫ÿ© ÿßŸÑÿ™Ÿä ÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ±Ÿáÿß ŸáŸä ÿßŸÑÿπÿ±ÿ®Ÿäÿ©ÿå Ÿàÿ≥Ÿäÿ™ŸÖ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖŸáÿß ŸÅŸä ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿ®ÿ¥ŸÉŸÑ ŸÉÿßŸÖŸÑ. ŸäŸÖŸÉŸÜŸÉ ÿ™ÿ∫ŸäŸäÿ±Ÿáÿß ŸÅŸä ÿ£Ÿä ŸàŸÇÿ™ ŸÖŸÜ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿÆÿßÿµÿ© ÿ®ŸÉ.',
      btn: 'ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ©',
      cancel: 'ÿ™ÿ±ÿßÿ¨ÿπ'
    },
    en: {
      confirmTitle: 'Confirm Language',
      msg: 'The selected language is English, and it will be used throughout the application. You can change it anytime from your dashboard.',
      btn: 'Confirm & Continue',
      cancel: 'Back'
    },
    fr: {
      confirmTitle: 'Confirmer la langue',
      msg: 'La langue s√©lectionn√©e est le Fran√ßais, elle sera utilis√©e dans toute l‚Äôapplication. Vous pouvez la changer √† tout moment depuis votre tableau de bord.',
      btn: 'Confirmer et continuer',
      cancel: 'Retour'
    }
  };

  const handleLangPick = (lang) => {
    setSelectedLang(lang);
    setShowConfirm(true);
  };

  const handleFinalConfirm = () => {
    setIsVisible(false);
    setTimeout(() => {
      changeLanguage(selectedLang);
      if (onSelect) onSelect();
    }, 500);
  };

  const NAVY_COLOR = '#1A365D';
  const BEIGE_COLOR = '#E3DAD0';

  return (
    <div className={`fixed inset-0 z-[10000] flex flex-col items-center justify-center p-6 bg-[${BEIGE_COLOR}] transition-opacity duration-700 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      
      {/* ŸÜÿßŸÅÿ∞ÿ© ÿßŸÑÿ™ÿ£ŸÉŸäÿØ ÿßŸÑŸÅÿÆŸÖÿ© */}
      {showConfirm && (
        <div className="fixed inset-0 z-[11000] flex items-center justify-center p-6 bg-[#1A365D]/40 backdrop-blur-md animate-fade-in">
          <div className="bg-white w-full max-w-md rounded-[3.5rem] p-10 shadow-2xl border-t-8 border-[#1A365D] transform animate-pop-up">
             <div className="text-4xl mb-6 text-center">üåç</div>
             <h3 className="text-2xl font-black text-[#1A365D] mb-4 text-center">
                {t[selectedLang].confirmTitle}
             </h3>
             <p className="text-[#1A365D]/60 font-bold text-center leading-relaxed mb-10 px-2">
                {t[selectedLang].msg}
             </p>
             <div className="space-y-3">
                <button 
                  onClick={handleFinalConfirm}
                  className="w-full py-5 bg-[#1A365D] text-white rounded-[2rem] font-black text-xl shadow-xl hover:bg-[#152c4d] active:scale-95 transition-all"
                >
                  {t[selectedLang].btn}
                </button>
                <button 
                  onClick={() => setShowConfirm(false)}
                  className="w-full py-4 text-[#1A365D]/40 font-black text-sm hover:text-[#1A365D] transition-colors"
                >
                  {t[selectedLang].cancel}
                </button>
             </div>
          </div>
        </div>
      )}

      {/* ÿÆŸÑŸÅŸäÿ© ŸÅÿÆŸÖÿ© ÿ®ÿ£ŸÜŸÖÿßÿ∑ ÿÆŸÅŸäŸÅÿ© */}
      <div className="absolute w-[200vw] h-[200vw] bg-[#1A365D] opacity-[0.02] rounded-full top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 animate-pulse"></div>
      
      <div className={`relative z-10 flex flex-col items-center w-full max-w-md text-center transition-all duration-1000 transform ${isVisible ? 'scale-100 translate-y-0' : 'scale-95 translate-y-10'}`}>
        
        <div className="mb-12 relative group">
          <div className="absolute inset-0 bg-[#1A365D]/10 rounded-full blur-xl group-hover:blur-2xl transition-all duration-500"></div>
          <img 
            src="/logo.jpg" 
            alt="Logo" 
            className="w-32 h-32 md:w-40 md:h-40 rounded-full border-4 border-white shadow-[0_20px_50px_rgba(0,0,0,0.1)] relative z-10 object-cover" 
          />
        </div>

        <h2 className="text-xl md:text-2xl font-black text-[#1A365D] mb-12 uppercase tracking-tight opacity-90">
          ÿßÿÆÿ™ÿßÿ± ÿßŸÑŸÑÿ∫ÿ© <span className="mx-2 text-gray-300">|</span> Select Language
        </h2>

        <div className="flex flex-col gap-5 w-full px-6">
          <button 
            onClick={() => handleLangPick('ar')} 
            className="group relative overflow-hidden bg-[#1A365D] text-white py-6 rounded-[2.5rem] font-black text-2xl shadow-2xl hover:shadow-[#1A365D]/40 active:scale-95 transition-all duration-300"
          >
            <span className="relative z-10">ÿßŸÑÿπÿ±ÿ®Ÿäÿ©</span>
            <div className="absolute inset-0 bg-white/10 translate-y-full group-hover:translate-y-0 transition-transform duration-300"></div>
          </button>

          <button 
            onClick={() => handleLangPick('en')} 
            className="bg-white text-[#1A365D] py-6 rounded-[2.5rem] font-black text-2xl border-2 border-[#1A365D]/10 shadow-lg hover:shadow-xl hover:bg-gray-50 active:scale-95 transition-all duration-300"
          >
            English
          </button>

          <button 
            onClick={() => handleLangPick('fr')} 
            className="bg-white text-[#1A365D] py-6 rounded-[2.5rem] font-black text-2xl border-2 border-[#1A365D]/10 shadow-lg hover:shadow-xl hover:bg-gray-50 active:scale-95 transition-all duration-300"
          >
            Fran√ßais
          </button>
        </div>

        <p className="mt-16 text-[#1A365D]/40 font-bold text-[10px] uppercase tracking-[0.5em]">
          Careerak &copy; 2024
        </p>
      </div>
    </div>
  );
};
