import React, { useState, useEffect } from 'react';
import { jobPostingService } from '../services/api';

export const JobPostingsPage = ({ lang }) => {
  const [jobPostings, setJobPostings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [isVisible, setIsVisible] = useState(false);

  const isArabic = lang === 'ar';

  const t = {
    ar: {
      title: 'ÿßŸÑŸÅÿ±ÿµ ÿßŸÑŸàÿ∏ŸäŸÅŸäÿ©',
      desc: 'ÿßŸÉÿ™ÿ¥ŸÅ ŸÖÿ≥ÿ™ŸÇÿ®ŸÑŸÉ ÿßŸÑŸÖŸáŸÜŸä ŸÅŸä ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿ¥ÿ±ŸÉÿßÿ™ ŸàÿßŸÑŸÖŸÜÿ∏ŸÖÿßÿ™',
      search: 'ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÖÿ≥ŸÖŸâ Ÿàÿ∏ŸäŸÅŸäÿå ÿ¥ÿ±ŸÉÿ©ÿå ÿ£Ÿà ŸÖÿØŸäŸÜÿ©...',
      apply: 'ÿ™ŸÇÿØŸäŸÖ ÿßŸÑÿ∑ŸÑÿ®',
      noJobs: 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ Ÿàÿ∏ÿßÿ¶ŸÅ ŸÖÿ™ÿßÿ≠ÿ© ÿ≠ÿßŸÑŸäÿßŸã ÿ™ÿ∑ÿßÿ®ŸÇ ÿ®ÿ≠ÿ´ŸÉ',
      details: 'ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ',
      posted: 'ŸÜÿ¥ÿ± ŸÖŸÜÿ∞'
    },
    en: {
      title: 'Job Opportunities',
      desc: 'Discover your professional future in top companies',
      search: 'Search for job title, company, or city...',
      apply: 'Apply Now',
      noJobs: 'No jobs available matching your search',
      details: 'Details',
      posted: 'Posted'
    }
  }[lang || 'ar'];

  useEffect(() => {
    setIsVisible(true);
    const fetchJobPostings = async () => {
      try {
        const response = await jobPostingService.getAll();
        setJobPostings(response.data);
      } catch (error) {
        console.error('Error fetching jobs:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchJobPostings();
  }, []);

  const filteredJobs = jobPostings.filter(job => 
    job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    job.location.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-[#E3DAD0]">
      <div className="w-16 h-16 border-4 border-[#1A365D]/20 border-t-[#1A365D] rounded-full animate-spin mb-4"></div>
      <p className="text-[#1A365D] font-black animate-pulse">ÿ¨ÿßÿ±Ÿä ÿ¨ŸÑÿ® ÿ£ŸÅÿ∂ŸÑ ÿßŸÑŸÅÿ±ÿµ ŸÑŸÉ...</p>
    </div>
  );

  return (
    <div className={`min-h-screen pb-32 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} style={{ backgroundColor: '#E3DAD0' }} dir={isArabic ? 'rtl' : 'ltr'}>
      
      {/* Hero Section ÿ®ŸÑŸÖÿ≥ÿ© ŸÅÿÆÿßŸÖÿ© ÿØÿßÿ¶ÿ±Ÿäÿ© */}
      <div className="bg-[#1A365D] pt-20 pb-28 px-6 rounded-b-[5rem] shadow-[0_30px_60px_rgba(26,54,93,0.3)] relative overflow-hidden">
        <div className="absolute inset-0 opacity-10 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')]"></div>
        <div className="max-w-4xl mx-auto text-center relative z-10">
          <h1 className="text-4xl md:text-6xl font-black text-white mb-6 tracking-tight drop-shadow-md">{t.title}</h1>
          <p className="text-blue-100/70 text-lg md:text-xl font-medium italic">{t.desc}</p>
        </div>
      </div>

      <div className="max-w-5xl mx-auto px-6 -mt-16 relative z-20">
        {/* ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑÿ®ÿ≠ÿ´ ÿßŸÑÿßÿ≠ÿ™ÿ±ÿßŸÅŸä (ŸÇÿßÿπÿØÿ© ÿßŸÑŸáŸäŸÜÿ™) */}
        <div className="mb-16">
          <div className="relative group transform transition-all hover:scale-[1.01]">
            <input 
              type="text" 
              placeholder={t.search} 
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className={`w-full p-7 bg-white/90 backdrop-blur-xl rounded-[3rem] shadow-[0_40px_80px_-20px_rgba(0,0,0,0.15)] text-xl font-bold text-[#1A365D] outline-none border-2 border-transparent focus:border-[#1A365D]/10 focus:bg-white transition-all placeholder-gray-300 text-center`}
            />
            <div className={`absolute ${isArabic ? 'left-10' : 'right-10'} top-1/2 -translate-y-1/2 text-2xl opacity-20 group-focus-within:opacity-100 transition-opacity`}>
              üîç
            </div>
          </div>
        </div>

        {/* ÿ¥ÿ®ŸÉÿ© ÿßŸÑŸàÿ∏ÿßÿ¶ŸÅ ÿ®ŸÑŸÖÿ≥ÿ© ŸÅŸÜÿØŸÇŸäÿ© */}
        <div className="space-y-8">
          {filteredJobs.length > 0 ? filteredJobs.map((job, index) => (
            <div 
              key={job._id} 
              style={{ transitionDelay: `${index * 100}ms` }}
              className={`bg-white p-8 md:p-12 rounded-[4rem] shadow-[0_20px_50px_rgba(0,0,0,0.05)] hover:shadow-[0_40px_80px_rgba(26,54,93,0.15)] transition-all duration-700 border border-white/50 group relative overflow-hidden transform hover:-translate-y-2 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
            >
              <div className={`absolute top-0 ${isArabic ? 'right-0' : 'left-0'} w-3 h-full bg-[#1A365D] opacity-5 group-hover:opacity-100 transition-all duration-500`}></div>
              
              <div className="flex flex-col md:flex-row md:items-center justify-between gap-8 relative z-10">
                <div className="flex-1">
                  <div className="flex items-center gap-4 mb-4">
                    <div className="w-16 h-16 bg-gray-50 rounded-3xl flex items-center justify-center text-3xl shadow-inner group-hover:scale-110 transition-transform">üè¢</div>
                    <div>
                      <h2 className="text-2xl md:text-3xl font-black text-[#1A365D] group-hover:tracking-tight transition-all">{job.title}</h2>
                      <p className="text-[#1A365D]/40 font-bold text-sm uppercase tracking-widest">{job.companyName || 'ÿ¥ÿ±ŸÉÿ© ŸÖŸÖŸäÿ≤ÿ©'}</p>
                    </div>
                  </div>
                  
                  <div className="flex flex-wrap gap-3 mt-6">
                    <span className="bg-[#1A365D]/5 text-[#1A365D] px-6 py-2 rounded-full text-xs font-black border border-[#1A365D]/5">
                      üìç {job.location}
                    </span>
                    <span className="bg-blue-50 text-blue-700 px-6 py-2 rounded-full text-xs font-black border border-blue-100">
                      ‚è±Ô∏è {job.jobType}
                    </span>
                    {job.salary && (
                      <span className="bg-green-50 text-green-700 px-6 py-2 rounded-full text-xs font-black border border-green-100">
                        üí∞ {job.salary}
                      </span>
                    )}
                  </div>
                </div>

                <div className="flex flex-col items-center md:items-end gap-5">
                  <button className="w-full md:w-auto bg-[#1A365D] hover:bg-[#152c4d] text-white px-12 py-5 rounded-[2rem] font-black shadow-xl hover:shadow-[#1A365D]/40 transform transition active:scale-95 text-xl flex items-center gap-3">
                    {t.apply}
                    <span className={isArabic ? 'rotate-180' : ''}>‚Üí</span>
                  </button>
                  <div className="flex items-center gap-2 text-[10px] text-gray-400 font-bold uppercase tracking-[0.2em]">
                    <span className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></span>
                    {t.posted} 24h
                  </div>
                </div>
              </div>
            </div>
          )) : (
            <div className="text-center py-32 bg-white/30 rounded-[4rem] border-4 border-dashed border-[#1A365D]/5 animate-pulse">
              <div className="text-8xl mb-6 opacity-10">üìÇ</div>
              <p className="text-2xl font-black text-[#1A365D]/20 uppercase tracking-widest">{t.noJobs}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};
