import React from 'react';
import { Link } from 'react-router-dom';
import { App as CapApp } from '@capacitor/app';

export const Navbar = ({ lang, user }) => {
  // ✅ وظيفة الخروج من التطبيق نهائياً
  const handleExitApp = () => {
    CapApp.exitApp();
  };

  return (
    <nav className="p-4 sm:p-6" style={{ backgroundColor: '#E3DAD0' }} dir={lang === 'ar' ? 'rtl' : 'ltr'}>
      <div className="max-w-screen-xl mx-auto flex justify-between items-center">
        
        {/* جهة اللوجو */}
        <Link to="/dashboard" className="flex items-center gap-3 active:scale-95 transition-all">
          <img 
            src="/logo.jpg" 
            alt="Logo" 
            className="h-12 w-12 rounded-full border-2 border-white shadow-xl object-cover" 
          />
          <h1 className="text-xl font-black text-[#1A365D] tracking-tighter">CAREERAK</h1>
        </Link>
        
        {/* جهة زر الخروج الاحترافي */}
        <button 
          onClick={handleExitApp}
          className="flex items-center gap-2 px-5 py-2.5 bg-white text-[#1A365D] rounded-2xl font-black text-sm shadow-md hover:shadow-lg active:scale-90 transition-all border border-[#1A365D]/10"
        >
          <span>{lang === 'ar' ? 'خروج' : 'Exit'}</span>
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round">
            <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
            <polyline points="16 17 21 12 16 7"></polyline>
            <line x1="21" y1="12" x2="9" y2="12"></line>
          </svg>
        </button>

      </div>
    </nav>
  );
};
