import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, Link } from 'react-router-dom';
import { StatusBar, Style } from '@capacitor/status-bar';
import { App as CapApp } from '@capacitor/app';
import { AuthProvider, useAuth } from './context/AuthContext';
import { WelcomePage } from './pages/01_WelcomePage';
import { AuthPage } from './pages/03_AuthPage';
import { OnboardingIndividuals } from './pages/03_OnboardingIndividuals';
import { OnboardingCompanies } from './pages/03_OnboardingCompanies';
import { OTPVerification } from './pages/03_OTPVerification';
import { SplashScreen } from './components/SplashScreen';
import { FloatingWhatsApp } from './components/FloatingWhatsApp';
import { Navbar } from './components/Navbar';
import { SettingsPage } from './pages/08_SettingsPage';
import { PolicyPage } from './pages/07_PolicyPage';
import { LanguagePage } from './pages/00_LanguagePage';
import { CountryPage } from './pages/00_CountryPage';
import { JobPostingsPage } from './pages/04_JobPostingsPage';
import { CoursesPage } from './pages/05_CoursesPage';
import { ProfilePage } from './pages/06_ProfilePage';
import { PostJobPage } from './pages/04_PostJobPage';
import { PostCoursePage } from './pages/05_PostCoursePage';
import { ApplyPage } from './pages/04_ApplyPage';
import './index.css';

const BottomNavbar = ({ user, lang }) => {
  const location = useLocation();
  const isArabic = lang === 'ar';
  const guestPaths = ['/', '/auth', '/policy', '/verify-otp', '/language', '/country'];
  if (guestPaths.includes(location.pathname) || !user) return null;

  const getNavItems = () => {
    const common = [
      { label: isArabic ? 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™' : 'Settings', path: '/settings', icon: '‚öôÔ∏è' },
      { label: isArabic ? 'ÿ≠ÿ≥ÿßÿ®Ÿä' : 'Profile', path: '/profile', icon: 'üë§' },
    ];
    if (user?.role === 'HR') return [{ label: isArabic ? 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©' : 'Home', path: '/dashboard', icon: 'üè¢' }, { label: isArabic ? 'ÿ•ÿ∂ÿßŸÅÿ©' : 'Add', path: '/post-job', icon: '‚ûï', special: true }, ...common];
    return [{ label: isArabic ? 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©' : 'Home', path: '/dashboard', icon: 'üè†' }, { label: isArabic ? 'ÿ∑ŸÑÿ®' : 'Request', path: '/apply-job', icon: 'üîç', special: true }, ...common];
  };

  return (
    <div className="fixed bottom-0 left-0 right-0 z-[1000] px-4 pb-6 pt-2 bg-gradient-to-t from-[#E3DAD0] to-transparent">
      <div className="max-w-md mx-auto bg-white/90 backdrop-blur-xl rounded-[2.5rem] shadow-[0_20px_50px_rgba(26,54,93,0.2)] border border-white/50 flex justify-between items-center p-2">
        {getNavItems().map((item, i) => (
          <Link key={i} to={item.path} className={`flex flex-col items-center justify-center transition-all active:scale-90 ${item.special ? 'bg-[#1A365D] text-white w-14 h-14 rounded-full shadow-lg -mt-8' : 'flex-1 text-gray-400'}`}>
            <span className={`${item.special ? 'text-2xl' : 'text-xl'}`}>{item.icon}</span>
            {!item.special && <span className="text-[8px] font-black mt-1 uppercase tracking-tighter text-center">{item.label}</span>}
          </Link>
        ))}
      </div>
    </div>
  );
};

function AppContent() {
  const { isAuthenticated, user, loading: authLoading, language } = useAuth();
  const [showSplash, setShowSplash] = useState(true);
  const bgMusic = useRef(null);

  // üéµ ŸÉÿßŸÖŸÑ: ÿßŸÑŸÖŸàÿ≥ŸäŸÇŸâ ÿ™ÿ®ÿØÿ£ ŸÅŸàÿ± ÿßŸÜÿ™Ÿáÿßÿ° ÿ¥ÿßÿ¥ÿ© ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ (Splash)
  useEffect(() => {
    if (!showSplash) {
      if (!bgMusic.current) {
        bgMusic.current = new Audio('/Music.mp3');
        bgMusic.current.loop = true;
        bgMusic.current.volume = 0.4;
      }
      const playMusic = () => { bgMusic.current.play().catch(() => {}); };
      playMusic();
      window.addEventListener('click', playMusic, { once: true });
      return () => window.removeEventListener('click', playMusic);
    }
  }, [showSplash]);

  if (showSplash || authLoading) return <SplashScreen onFinish={() => setShowSplash(false)} />;
  if (!language) return <LanguagePage onSelect={() => {}} />;

  return (
    <div className="flex flex-col min-h-screen w-full bg-[#E3DAD0] font-arabic overflow-x-hidden" dir={language === 'ar' ? 'rtl' : 'ltr'}>
      {isAuthenticated && <Navbar lang={language} user={user} />}
      <FloatingWhatsApp />
      <main className={`flex-grow w-full max-w-screen-xl mx-auto px-6 ${isAuthenticated ? 'pb-32 pt-4' : 'py-4'}`}>
        <Routes>
          <Route path="/" element={<CountryPage lang={language} />} />
          <Route path="/01_WelcomePage" element={<WelcomePage lang={language} />} />
          <Route path="/auth" element={<AuthPage lang={language} />} />
          <Route path="/verify-otp" element={<OTPVerification />} />
          <Route path="/policy" element={<PolicyPage lang={language} />} />
          <Route path="/onboarding-individuals" element={<OnboardingIndividuals lang={language} />} />
          <Route path="/onboarding-companies" element={<OnboardingCompanies lang={language} />} />
          <Route path="/dashboard" element={isAuthenticated ? <DashboardSwitcher user={user} /> : <Navigate to="/auth" />} />
          <Route path="/jobs" element={<JobPostingsPage lang={language} />} />
          <Route path="/courses" element={<CoursesPage lang={language} />} />
          <Route path="/profile" element={<ProfilePage lang={language} />} />
          <Route path="/post-job" element={<PostJobPage lang={language} />} />
          <Route path="/apply-job" element={<ApplyPage lang={language} />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </main>
      <BottomNavbar user={user} lang={language} />
    </div>
  );
}

const DashboardSwitcher = ({ user }) => {
  if (user.role === 'HR') return (
    <div className="w-full space-y-10 animate-fade-in text-center">
      <div className="bg-white p-10 rounded-[4rem] shadow-2xl border-b-8 border-[#1A365D]">
        <h1 className="text-3xl font-black text-[#1A365D]">{user?.companyName} üè¢</h1>
        <p className="text-gray-400 font-bold mt-2 uppercase tracking-widest text-xs">ŸÑŸàÿ≠ÿ© ÿ™ÿ≠ŸÉŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿßÿ™</p>
      </div>
      <div className="grid grid-cols-2 gap-6 pb-10">
        <DashboardCard title="ŸÜÿ¥ÿ± Ÿàÿ∏ŸäŸÅÿ©" icon="‚ûï" path="/post-job" />
        <DashboardCard title="ÿ∑ŸÑÿ®ÿßÿ™ ÿßŸÑÿ™Ÿàÿ∏ŸäŸÅ" icon="üì•" path="/applications" />
      </div>
    </div>
  );
  return (
    <div className="w-full space-y-10 animate-fade-in text-center">
      <div className="bg-white p-10 rounded-[4rem] shadow-2xl border-b-8 border-[#1A365D]">
        <h1 className="text-3xl font-black text-[#1A365D]">ÿ£ŸáŸÑÿßŸãÿå {user?.firstName} üëã</h1>
        <p className="text-gray-400 font-bold mt-2 uppercase tracking-widest text-xs">ŸÖÿ≥ÿ™ŸÇÿ®ŸÑŸÉ Ÿäÿ®ÿØÿ£ ŸÖŸÜ ŸáŸÜÿß</p>
      </div>
      <div className="grid grid-cols-2 gap-6 pb-10">
        <DashboardCard title="ŸÉŸÑ ÿßŸÑŸàÿ∏ÿßÿ¶ŸÅ" icon="üè¢" path="/jobs" />
        <DashboardCard title="ÿßŸÑÿ£ŸÉÿßÿØŸäŸÖŸäÿ©" icon="üéì" path="/courses" />
      </div>
    </div>
  );
};

const DashboardCard = ({ title, icon, path }) => (
  <Link to={path} className="bg-white p-10 rounded-[3rem] shadow-xl flex flex-col items-center group active:scale-95 transition-all border border-[#1A365D]/5">
    <div className="text-5xl mb-4 group-hover:scale-110 transition-transform">{icon}</div>
    <h3 className="text-xs font-black text-[#1A365D] uppercase text-center tracking-tighter">{title}</h3>
  </Link>
);

function App() { return ( <AuthProvider><Router><AppContent /></Router></AuthProvider> ); }
export default App;
