import React, { useState, useEffect } from 'react';
import { educationalCourseService, trainingCourseService } from '../services/api';

export const CoursesPage = ({ lang }) => {
  const [educationalCourses, setEducationalCourses] = useState([]);
  const [trainingCourses, setTrainingCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('educational');

  const isArabic = lang === 'ar';

  const t = {
    ar: {
      title: 'ÿ£ŸÉÿßÿØŸäŸÖŸäÿ© ŸÉÿßÿ±Ÿäÿ±ŸÉ',
      subtitle: 'ÿßÿ≥ÿ™ÿ´ŸÖÿ± ŸÅŸä ŸÜŸÅÿ≥ŸÉ Ÿàÿ∑Ÿàÿ± ŸÖŸáÿßÿ±ÿßÿ™ŸÉ ŸÑŸÑŸÖÿ≥ÿ™ŸÇÿ®ŸÑ',
      eduTab: 'üìö ÿØŸàÿ±ÿßÿ™ ÿ™ÿπŸÑŸäŸÖŸäÿ©',
      trainTab: 'üõ†Ô∏è ÿ®ÿ±ÿßŸÖÿ¨ ÿ™ÿØÿ±Ÿäÿ®Ÿäÿ©',
      enroll: 'ÿßŸÑÿ™ÿ≠ÿßŸÇ ÿßŸÑÿ¢ŸÜ',
      noCourses: 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿØŸàÿ±ÿßÿ™ ŸÖÿ™ÿßÿ≠ÿ© ÿ≠ÿßŸÑŸäÿßŸã ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑŸÇÿ≥ŸÖ',
      status: 'ÿßŸÑÿ≠ÿßŸÑÿ©',
      available: 'ŸÖÿ™ÿßÿ≠ ÿßŸÑÿ¢ŸÜ'
    },
    en: {
      title: 'Careerak Academy',
      subtitle: 'Invest in yourself and develop your skills for the future',
      eduTab: 'üìö Educational',
      trainTab: 'üõ†Ô∏è Training Programs',
      enroll: 'Enroll Now',
      noCourses: 'No courses available in this section currently',
      status: 'Status',
      available: 'Available Now'
    }
  }[lang];

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const [eduResponse, trainingResponse] = await Promise.all([
          educationalCourseService.getAll(),
          trainingCourseService.getAll()
        ]);
        setEducationalCourses(eduResponse.data);
        setTrainingCourses(trainingResponse.data);
      } catch (error) {
        console.error('Error fetching courses:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchCourses();
  }, []);

  if (loading) return (
    <div className="flex items-center justify-center min-h-screen" style={{ backgroundColor: '#E3DAD0' }}>
      <div className="animate-spin rounded-full h-16 w-16 border-t-4 border-b-4 border-[#1A365D]"></div>
    </div>
  );

  const currentCourses = activeTab === 'educational' ? educationalCourses : trainingCourses;

  return (
    <div className="min-h-screen pb-24" style={{ backgroundColor: '#E3DAD0' }} dir={isArabic ? 'rtl' : 'ltr'}>
      
      {/* Premium Header */}
      <div className="bg-[#1A365D] pt-16 pb-28 px-6 rounded-b-[4rem] shadow-2xl mb-12 relative overflow-hidden">
        <div className="absolute top-0 left-0 w-64 h-64 bg-white opacity-5 rounded-full blur-3xl -ml-20 -mt-20"></div>
        <div className="max-w-4xl mx-auto text-center relative z-10">
          <h1 className="text-4xl md:text-6xl font-black text-white mb-4 tracking-tight leading-tight">{t.title}</h1>
          <p className="text-blue-200 text-lg md:text-xl font-medium opacity-90">{t.subtitle}</p>
        </div>
      </div>

      <div className="max-w-6xl mx-auto px-6 -mt-20 relative z-20">
        
        {/* Modern Tab Switcher */}
        <div className="flex bg-white/60 backdrop-blur-xl p-2 rounded-[2.5rem] shadow-2xl mb-12 max-w-md mx-auto border border-white/50">
          <button
            onClick={() => setActiveTab('educational')}
            className={`flex-1 py-4 px-6 rounded-[2rem] font-black text-sm transition-all duration-500 transform ${
              activeTab === 'educational' 
                ? 'bg-[#1A365D] text-white shadow-xl scale-[1.05]' 
                : 'text-[#1A365D] hover:bg-white/40'
            }`}
          >
            {t.eduTab}
          </button>
          <button
            onClick={() => setActiveTab('training')}
            className={`flex-1 py-4 px-6 rounded-[2rem] font-black text-sm transition-all duration-500 transform ${
              activeTab === 'training' 
                ? 'bg-[#1A365D] text-white shadow-xl scale-[1.05]' 
                : 'text-[#1A365D] hover:bg-white/40'
            }`}
          >
            {t.trainTab}
          </button>
        </div>

        {/* Courses Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {currentCourses.length > 0 ? currentCourses.map((course) => (
            <div key={course._id} className="bg-white rounded-[3rem] shadow-xl overflow-hidden hover:scale-[1.03] transition-all duration-500 border border-white flex flex-col group">
              <div className="h-52 bg-[#1A365D] flex items-center justify-center relative group-hover:bg-[#2C5282] transition-colors">
                <div className="absolute top-5 right-5 bg-white/20 backdrop-blur-md px-4 py-2 rounded-full text-white text-[10px] font-black border border-white/30 uppercase tracking-widest">
                  {activeTab === 'educational' ? course.category : (course.department || 'General')}
                </div>
                <div className="text-7xl transform group-hover:scale-110 transition-transform duration-500">
                  {activeTab === 'educational' ? 'üìñ' : 'üí°'}
                </div>
                <div className="absolute bottom-0 left-0 w-full h-1/2 bg-gradient-to-t from-black/20 to-transparent"></div>
              </div>
              
              <div className="p-8 flex flex-col flex-grow">
                <h2 className="text-2xl font-black text-[#1A365D] mb-4 leading-tight h-16 line-clamp-2">{course.title}</h2>
                <p className="text-gray-500 font-medium text-sm mb-8 leading-relaxed line-clamp-3">
                  {course.description}
                </p>
                
                <div className="flex items-center justify-between mt-auto pt-6 border-t border-gray-50">
                  <div className="flex flex-col">
                    <span className="text-[10px] text-gray-400 font-black uppercase tracking-widest mb-1">{t.status}</span>
                    <span className="text-sm font-black text-green-600 flex items-center gap-1">
                      <span className="h-2 w-2 bg-green-500 rounded-full animate-pulse"></span>
                      {t.available}
                    </span>
                  </div>
                  <button className="bg-[#1A365D] hover:bg-blue-900 text-white px-7 py-3 rounded-2xl font-black text-sm shadow-[0_10px_20px_rgba(26,54,93,0.2)] transition-all active:scale-95">
                    {t.enroll}
                  </button>
                </div>
              </div>
            </div>
          )) : (
            <div className="col-span-full text-center bg-white/40 p-20 rounded-[4rem] border-2 border-dashed border-[#1A365D]/10">
              <div className="text-7xl mb-6 opacity-20">üîç</div>
              <p className="text-[#1A365D] font-black text-2xl">{t.noCourses}</p>
              <p className="text-gray-500 mt-3 font-bold">Ÿäÿ±ÿ¨Ÿâ ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑŸÇÿ≥ŸÖ ÿßŸÑÿ¢ÿÆÿ± ÿ£Ÿà ÿßŸÑÿπŸàÿØÿ© ŸÑÿßÿ≠ŸÇÿßŸã</p>
            </div>
          )}
        </div>
      </div>

      {/* Footer Branding */}
      <div className="mt-20 text-center">
        <p className="text-[10px] font-black text-[#1A365D]/30 uppercase tracking-[0.5em]">Careerak Learning Ecosystem</p>
      </div>
    </div>
  );
};
