import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/AuthContext';

export default function PolicyPage() {
  const [isVisible, setIsVisible] = useState(false);
  const { language } = useAuth();
  const isRTL = language === 'ar';

  useEffect(() => { setIsVisible(true); }, []);

  const t = {
    ar: {
      title: "سياسة الخصوصية والأحكام",
      p1: "نحن في Careerak نلتزم بحماية بياناتك الشخصية وضمان سرية المعلومات التي تشاركها معنا وفقاً لأعلى معايير الأمان العالمية.",
      p2: "يتم استخدام بياناتك وتطوير ملفك الشخصي بواسطة الذكاء الاصطناعي الخاص بنا فقط لتحسين تجربتك في التوظيف والتدريب، ولن يتم مشاركتها مع أي جهة خارجية دون موافقتك الصريحة.",
      p3: "باستخدامك لمنصة كاريرك، فإنك توافق على أن نكون شريكك في رحلتك المهنية مع الالتزام بكافة القوانين المنظمة للعمل والخصوصية الرقمية.",
      points: [
        "حماية كاملة لكلمات المرور والبيانات الحساسة.",
        "الشفافية المطلقة في كيفية معالجة طلبات الوظائف.",
        "الحق في تعديل أو حذف بياناتك في أي وقت.",
        "الالتزام بالهوية البصرية والمهنية للمنصة."
      ]
    },
    en: {
      title: "Privacy Policy & Terms",
      p1: "At Careerak, we are committed to protecting your personal data and ensuring the confidentiality of the information you share with us.",
      p2: "Your data and profile development are handled by our AI specifically to enhance your recruitment and training experience.",
      p3: "By using the Careerak platform, you agree to let us be your partner in your professional journey.",
      points: [
        "Full protection of passwords and sensitive data.",
        "Absolute transparency in processing job applications.",
        "Right to edit or delete your data at any time.",
        "Commitment to the visual and professional identity of the platform."
      ]
    }
  }[language || 'ar'];

  return (
    <div className={`min-h-screen bg-[#E3DAD0] p-6 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={isRTL ? 'rtl' : 'ltr'}>
      <div className="max-w-3xl mx-auto bg-white rounded-[4rem] shadow-2xl p-10 md:p-20 text-right border border-white">
        <h2 className={`text-4xl font-black text-[#1A365D] mb-12 border-[#1A365D] pr-4 ${isRTL ? 'border-r-8' : 'border-l-8 pl-4 pr-0 text-left'}`}>
          {t.title}
        </h2>

        <div className={`space-y-8 text-[#1A365D] font-bold leading-relaxed text-lg ${!isRTL && 'text-left'}`}>
          <p className="opacity-80">{t.p1}</p>
          <p className="opacity-80">{t.p2}</p>

          <ul className="space-y-4 py-6">
            {t.points.map((point, i) => (
              <li key={i} className="flex items-center gap-4">
                <span className="w-3 h-3 bg-[#1A365D] rounded-full flex-shrink-0"></span>
                <span>{point}</span>
              </li>
            ))}
          </ul>

          <p className="pt-6 border-t border-gray-100 italic">{t.p3}</p>
        </div>
      </div>
    </div>
  );
}
