import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function LanguagePage() {
  const { changeLanguage, setAudio } = useAuth();
  const navigate = useNavigate();
  const [lang, setLang] = useState(null);

  const confirm = async (audio) => {
    await changeLanguage(lang);
    await setAudio(audio);
    navigate("/");
  };

  return (
    <div className="min-h-screen bg-[#E3DAD0] flex flex-col items-center justify-center gap-6">
      <h1 className="text-[#1A365D] font-bold text-xl">
        اختر اللغة / Choose Language / Choisir la langue
      </h1>

      <div className="flex flex-col gap-3">
        <button onClick={() => setLang("ar")}>العربية</button>
        <button onClick={() => setLang("en")}>English</button>
        <button onClick={() => setLang("fr")}>Français</button>
      </div>

      {lang && (
        <div className="bg-white p-6 rounded-2xl shadow-xl text-center">
          <p className="mb-4 font-bold">
            هل توافق على تشغيل الصوتيات في التطبيق؟
          </p>
          <div className="flex gap-4">
            <button onClick={() => confirm(true)}>نعم</button>
            <button onClick={() => confirm(false)}>لا</button>
          </div>
        </div>
      )}
    </div>
  );
}
