import axios from 'axios';
import { Preferences } from '@capacitor/preferences';

// العناوين المتاحة: السيرفر المحلي والجسر (Bridge)
const LOCAL_SERVER = 'http://192.168.1.5:5000/api';
const TUNNEL_BRIDGE = 'https://careerak-server.loca.lt/api';

const api = axios.create({
  baseURL: LOCAL_SERVER, // الافتراضي هو المحلي، وسنقوم بالتحقق من الأفضل لاحقاً
  timeout: 15000,
  headers: {
    'Content-Type': 'application/json',
    'Bypass-Tunnel-Reminder': 'true' // ضروري لعمل الجسر (Tunnel)
  }
});

// وظيفة لاكتشاف السيرفر المتاح (المحلي أو الجسر)
export const discoverBestServer = async () => {
  const servers = [LOCAL_SERVER, TUNNEL_BRIDGE];
  for (const url of servers) {
    try {
      await axios.get(`${url}/users/health-check`, { timeout: 3000 });
      api.defaults.baseURL = url;
      console.log(`Connected to: ${url}`);
      return url;
    } catch (e) {
      continue;
    }
  }
  return api.defaults.baseURL;
};

// قاعدة معالجة التوكن
api.interceptors.request.use(async (config) => {
  try {
    const { value: token } = await Preferences.get({ key: 'auth_token' });
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
  } catch (error) {
    console.error('API Request Interceptor Error:', error);
  }
  return config;
}, (error) => Promise.reject(error));

// الخدمات الأساسية
export const userService = {
  register: (userData) => api.post('/users/register', userData),
  login: (credentials) => api.post('/users/login', credentials),
  getProfile: () => api.get('/users/profile'),
  updateProfile: (data) => api.put('/users/profile', data),
  healthCheck: () => api.get('/users/health-check')
};

export const jobPostingService = {
  getAll: () => api.get('/job-postings'),
  getById: (id) => api.get(`/job-postings/${id}`),
  create: (data) => api.post('/job-postings', data),
};

export const courseService = {
  getEducational: () => api.get('/educational-courses'),
  getTraining: () => api.get('/training-courses'),
};

export default api;
