import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import logo from "../assets/logo.png";

export default function LanguagePage() {
  const { changeLanguage } = useAuth();
  const navigate = useNavigate();

  const [selectedLang, setSelectedLang] = useState(null);

  const texts = {
    ar: {
      title: "اختر اللغة",
      confirm: "تأكيد",
      cancel: "إلغاء",
      text: "سيتم استخدام هذه اللغة في التطبيق",
    },
    en: {
      title: "Choose Language",
      confirm: "Confirm",
      cancel: "Cancel",
      text: "This language will be used in the app",
    },
    fr: {
      title: "Choisir la langue",
      confirm: "Confirmer",
      cancel: "Annuler",
      text: "Cette langue sera utilisée dans l'application",
    },
  };

  const t = selectedLang ? texts[selectedLang] : texts.ar;

  const confirm = async () => {
    await changeLanguage(selectedLang);
    navigate("/auth", { replace: true });
  };

  return (
    <div className="relative min-h-screen bg-[#E3DAD0] flex flex-col items-center justify-center gap-6">
      <div className="overlay"></div>

      <div className="logo-circle">
        <img src={logo} alt="Careerak" />
      </div>

      <h2 className="text-[#1A365D] font-bold text-lg">
        اختر اللغة / Choose Language
      </h2>

      <div className="flex flex-col gap-4 w-72">
        <button className="lang-btn" onClick={() => setSelectedLang("ar")}>العربية</button>
        <button className="lang-btn" onClick={() => setSelectedLang("en")}>English</button>
        <button className="lang-btn" onClick={() => setSelectedLang("fr")}>Français</button>
      </div>

      {selectedLang && (
        <div className="confirm-box">
          <p>{t.text}</p>
          <button onClick={confirm}>{t.confirm}</button>
          <button onClick={() => setSelectedLang(null)}>{t.cancel}</button>
        </div>
      )}
    </div>
  );
}
