import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import api from '../services/api';

export const ApplyPage = ({ lang = 'ar' }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [isVisible, setIsVisible] = useState(false);
  const [loading, setLoading] = useState(false);
  const isArabic = lang === 'ar';
  
  const targetOffer = location.state?.targetOffer || null;

  useEffect(() => { setIsVisible(true); }, []);

  const [formData, setFormData] = useState({
    targetTitle: targetOffer?.title || '',
    expectedSalary: '',
    availability: '',
    workPreference: 'both',
    canTravel: 'yes',
    coverLetter: '',
    additionalInfo: ''
  });

  const t = {
    ar: {
      title: targetOffer ? `ÿßŸÑÿ™ŸÇÿØŸäŸÖ ÿπŸÑŸâ: ${targetOffer.title}` : 'ÿ™ŸÇÿØŸäŸÖ ÿ∑ŸÑÿ® Ÿàÿ∏ŸäŸÅÿ© ÿπÿßŸÖ',
      sub: 'ÿ£ŸÉŸÖŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿ∑ŸÑÿ®ŸÉ ŸÑÿ™ÿµŸÑ ÿ•ŸÑŸâ ÿ£ÿµÿ≠ÿßÿ® ÿßŸÑÿπŸÖŸÑ ÿ®ÿ£ÿπŸÑŸâ ŸÖÿπÿßŸäŸäÿ± ÿßŸÑÿßÿ≠ÿ™ÿ±ÿßŸÅŸäÿ©',
      sec1: 'ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ∑ŸÑÿ® ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©',
      sec2: 'ÿßŸÑÿ™ŸÅÿ∂ŸäŸÑÿßÿ™ ŸàÿßŸÑÿ¨ÿßŸáÿ≤Ÿäÿ© ŸÑŸÑÿπŸÖŸÑ',
      sec3: 'ÿ±ÿ≥ÿßŸÑÿ© ÿ•ÿ∂ÿßŸÅŸäÿ© ŸÑÿ£ÿµÿ≠ÿßÿ® ÿßŸÑÿπŸÖŸÑ',
      labels: {
        title: 'ÿßŸÑŸÖÿ≥ŸÖŸâ ÿßŸÑŸàÿ∏ŸäŸÅŸä ÿßŸÑŸÖÿ±ÿ∫Ÿàÿ®',
        salary: 'ÿßŸÑÿ±ÿßÿ™ÿ® ÿßŸÑŸÖÿ™ŸàŸÇÿπ (ÿ¥Ÿáÿ±ŸäÿßŸã)',
        availability: 'ŸÖÿ™Ÿâ ŸäŸÖŸÉŸÜŸÉ ÿßŸÑÿ®ÿØÿ° ÿ®ÿßŸÑÿπŸÖŸÑÿü',
        workPref: 'ÿ®Ÿäÿ¶ÿ© ÿßŸÑÿπŸÖŸÑ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ© ŸÑÿØŸäŸÉ',
        travel: 'ŸáŸÑ ŸäŸÖŸÉŸÜŸÉ ÿßŸÑÿ™ŸÜŸÇŸÑ ÿ£Ÿà ÿßŸÑÿ≥ŸÅÿ± ŸÑŸÑÿπŸÖŸÑÿü',
        coverLetter: 'ŸÑŸÖÿßÿ∞ÿß ÿ£ŸÜÿ™ ÿßŸÑŸÖÿ±ÿ¥ÿ≠ ÿßŸÑÿ£ŸÜÿ≥ÿ® ŸÑŸáÿ∞Ÿá ÿßŸÑŸÅÿ±ÿµÿ©ÿü',
        other: 'ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ£Ÿà ŸÖŸáÿßÿ±ÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ© ÿ™ŸàÿØ ÿ∞ŸÉÿ±Ÿáÿß'
      },
      options: {
        admin: 'ÿπŸÖŸÑ ÿ•ÿØÿßÿ±Ÿä ŸÖŸÉÿ™ÿ®Ÿä', field: 'ÿπŸÖŸÑ ŸÖŸäÿØÿßŸÜŸä', both: 'ŸÑÿß ÿ£ŸÖÿßŸÜÿπ ŸÅŸä ŸÉŸÑÿßŸáŸÖÿß',
        yes: 'ŸÜÿπŸÖÿå ÿ®ŸÉÿßŸÖŸÑ ÿßŸÑÿ¨ÿßŸáÿ≤Ÿäÿ©', no: 'ÿ£ŸÅÿ∂ŸÑ ÿßŸÑÿπŸÖŸÑ ŸÅŸä ŸÖÿØŸäŸÜÿ™Ÿä ŸÅŸÇÿ∑',
        immediate: 'ŸÅŸàÿ±ÿßŸã (ŸÖÿ™ÿßÿ≠ ÿ≠ÿßŸÑŸäÿßŸã)', month: 'ÿÆŸÑÿßŸÑ ÿ¥Ÿáÿ± ŸÖŸÜ ÿßŸÑÿ¢ŸÜ',
      },
      submit: 'ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ® ÿ®ÿßÿ≠ÿ™ÿ±ÿßŸÅŸäÿ©'
    }
  }[lang] || { ar: {} };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await api.post('/job-applications', { ...formData, offerId: targetOffer?._id });
      alert(isArabic ? '‚úÖ ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ∑ŸÑÿ®ŸÉ ÿ®ŸÜÿ¨ÿßÿ≠! ŸÜÿ±ÿ¨Ÿà ŸÑŸÉ ŸÉŸÑ ÿßŸÑÿ™ŸàŸÅŸäŸÇ.' : 'Application sent successfully!');
      navigate('/dashboard');
    } catch (err) {
      alert(isArabic ? '‚ö†Ô∏è ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ÿå Ÿäÿ±ÿ¨Ÿâ ŸÖÿ±ÿßÿ¨ÿπÿ© ÿ®ŸäÿßŸÜÿßÿ™ŸÉ' : 'Error sending application');
    } finally {
      setLoading(false);
    }
  };

  const PRIMARY_NAVY = '#1A365D';

  const Input = ({ label, name, placeholder, type = 'text' }) => (
    <div className="space-y-2 group">
      <label className="text-[11px] font-black text-[#1A365D]/40 px-4 uppercase tracking-widest">{label}</label>
      <input 
        type={type} name={name} value={formData[name]} onChange={handleInputChange} placeholder={placeholder}
        className={`w-full p-5 bg-gray-50 rounded-[2.2rem] outline-none font-bold text-base border-2 border-transparent focus:border-[#1A365D]/10 focus:bg-white transition-all shadow-inner placeholder-gray-300 text-[${PRIMARY_NAVY}]`}
      />
    </div>
  );

  return (
    <div className={`min-h-screen p-4 md:p-8 bg-[#E3DAD0] pb-32 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      <div className={`max-w-3xl mx-auto bg-white rounded-[4rem] shadow-[0_40px_100px_-20px_rgba(26,54,93,0.15)] p-8 md:p-16 border border-white/50 transform transition-all duration-1000 ${isVisible ? 'translate-y-0' : 'translate-y-10'}`}>
        
        <div className="text-center mb-16">
          <div className="w-24 h-24 bg-[#1A365D]/5 text-[#1A365D] rounded-full flex items-center justify-center mx-auto mb-6 shadow-inner relative group">
            <span className="text-4xl">üìù</span>
          </div>
          <h2 className="text-3xl md:text-4xl font-black text-[#1A365D] leading-tight tracking-tight">{t.ar.title}</h2>
          <p className="text-[#1A365D]/40 font-bold mt-3 text-sm italic px-4">{t.ar.sub}</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-14 text-right" dir="rtl">
          
          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec1}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <Input label={t.ar.labels.title} name="targetTitle" />
              <Input label={t.ar.labels.salary} name="expectedSalary" type="number" />
            </div>
          </section>

          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec2}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <label className="text-[11px] font-black text-[#1A365D]/40 px-4 uppercase tracking-widest">{t.ar.labels.availability}</label>
                <select name="availability" onChange={handleInputChange} className="w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-sm shadow-inner appearance-none text-[#1A365D]">
                  <option value="immediate">{t.ar.options.immediate}</option>
                  <option value="month">{t.ar.options.month}</option>
                </select>
              </div>
              <div className="space-y-2">
                <label className="text-[11px] font-black text-[#1A365D]/40 px-4 uppercase tracking-widest">{t.ar.labels.workPref}</label>
                <select name="workPreference" onChange={handleInputChange} className="w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-sm shadow-inner appearance-none text-[#1A365D]">
                  <option value="both">{t.ar.options.both}</option>
                  <option value="admin">{t.ar.options.admin}</option>
                  <option value="field">{t.ar.options.field}</option>
                </select>
              </div>
            </div>
            <div className="space-y-2">
              <label className="text-[11px] font-black text-[#1A365D]/40 px-4 uppercase tracking-widest">{t.ar.labels.travel}</label>
              <select name="canTravel" onChange={handleInputChange} className="w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-sm shadow-inner appearance-none text-[#1A365D]">
                <option value="yes">{t.ar.options.yes}</option>
                <option value="no">{t.ar.options.no}</option>
              </select>
            </div>
          </section>

          <section className="space-y-8">
            <h3 className="text-xl font-black text-[#1A365D] flex items-center gap-3"><span className="w-2 h-8 bg-[#1A365D] rounded-full"></span>{t.ar.sec3}</h3>
            <textarea 
              name="coverLetter" value={formData.coverLetter} onChange={handleInputChange} placeholder={t.ar.labels.coverLetter}
              className={`w-full p-8 bg-gray-50 rounded-[3rem] outline-none font-bold text-base h-40 shadow-inner border-2 border-transparent focus:border-[#1A365D]/10 focus:bg-white transition-all placeholder-gray-300 text-[${PRIMARY_NAVY}]`}
            />
            <Input label={t.ar.labels.other} name="additionalInfo" />
          </section>

          <button 
            type="submit" disabled={loading}
            className="w-full py-7 rounded-[3.5rem] font-black shadow-[0_25px_50px_-12px_rgba(26,54,93,0.35)] active:scale-95 transition-all text-2xl bg-[#1A365D] text-white hover:bg-[#152c4d] hover:-translate-y-1 flex items-center justify-center gap-4"
          >
            {loading ? <div className="w-8 h-8 border-4 border-white/30 border-t-white rounded-full animate-spin"></div> : t.ar.submit}
          </button>

        </form>
      </div>
    </div>
  );
};
