import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function EntryPage() {
  const navigate = useNavigate();

  useEffect(() => {
    const audio = new Audio("/Music.mp3");
    audio.loop = true;
    audio.volume = 0.5;
    audio.play().catch(() => {});

    const timer = setTimeout(() => {
      audio.pause();
      navigate("/language");
    }, 9000); // ⏱️ 9 ثواني

    return () => {
      clearTimeout(timer);
      audio.pause();
    };
  }, [navigate]);

  return (
    <div className="min-h-screen bg-[#E3DAD0] flex flex-col items-center justify-center">
      {/* هنا نفس تصميمك السابق بدون أي تغيير */}
    </div>
  );
}
