import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

export const LanguagePage = ({ onSelect }) => {
  const navigate = useNavigate();
  const { setLanguage } = useAuth();

  const chooseLanguage = (lang) => {
    setLanguage(lang);
    if (onSelect) onSelect(lang);
    navigate('/');
  };

  return (
    <div className="w-full min-h-screen flex items-center justify-center bg-[#E3DAD0]">
      <div className="bg-white w-full max-w-md rounded-[3rem] shadow-2xl p-10 text-center">

        <h1 className="text-2xl font-black text-[#1A365D] mb-6">
          اختر اللغة / Choose Language
        </h1>

        <div className="flex flex-col gap-4">

          <button
            onClick={() => chooseLanguage('ar')}
            className="w-full py-4 rounded-full bg-[#1A365D] text-white font-bold"
          >
            العربية
          </button>

          <button
            onClick={() => chooseLanguage('en')}
            className="w-full py-4 rounded-full bg-[#1A365D] text-white font-bold"
          >
            English
          </button>

          <button
            onClick={() => chooseLanguage('fr')}
            className="w-full py-4 rounded-full bg-[#1A365D] text-white font-bold"
          >
            Français
          </button>

        </div>

      </div>
    </div>
  );
};
