import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function EntryPage() {
  const navigate = useNavigate();
  const [visible, setVisible] = useState(false);

  // تشغيل الانترو + المؤقت
  useEffect(() => {
    setVisible(true);

    const timer = setTimeout(() => {
      navigate("/");
    }, 9000); // ⏱️ 9 ثواني

    return () => clearTimeout(timer);
  }, [navigate]);

  const PRIMARY_NAVY = "#1A365D";
  const SOFT_BEIGE = "#E3DAD0";

  return (
    <div
      className={`min-h-screen flex flex-col font-sans transition-opacity duration-1000 ${
        visible ? "opacity-100" : "opacity-0"
      }`}
      style={{ backgroundColor: SOFT_BEIGE }}
    >
      {/* Header */}
      <div
        className="pt-24 pb-32 px-6 rounded-b-[4rem] shadow-2xl relative overflow-hidden"
        style={{ backgroundColor: PRIMARY_NAVY }}
      >
        <div className="absolute inset-0 opacity-10 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')]"></div>

        <div className="relative z-10 text-center max-w-4xl mx-auto">
          <h1 className="text-5xl md:text-6xl font-black text-white mb-6 tracking-tight drop-shadow-md">
            Careerak
          </h1>

          <p className="text-blue-100/80 text-lg md:text-xl font-medium italic">
            Your Career. One Platform. No Limits.
          </p>
        </div>
      </div>

      {/* محتوى سفلي (فخم – فارغ عمداً) */}
      <div className="flex-grow flex items-center justify-center text-center">
        <p className="text-[#1A365D]/40 text-sm tracking-widest uppercase">
          Loading experience...
        </p>
      </div>
    </div>
  );
}
