import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

export default function SettingsPage() {
  const { language, setAudio, audioEnabled, changeLanguage } = useAuth();
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => { setIsVisible(true); }, []);

  const t = {
    ar: { title: 'الإعدادات', lang: 'اللغة', audio: 'الصوتيات', save: 'حفظ التغييرات' },
    en: { title: 'Settings', lang: 'Language', audio: 'Audio', save: 'Save Changes' },
    fr: { title: 'Paramètres', lang: 'Langue', audio: 'Audio', save: 'Enregistrer' }
  }[language] || { ar: {} };

  return (
    <div className={`min-h-screen bg-[#E3DAD0] p-6 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={language === 'ar' ? 'rtl' : 'ltr'}>
      <div className="max-w-md mx-auto bg-white rounded-[3rem] shadow-2xl p-10">
        <h2 className="text-2xl font-black text-[#1A365D] mb-8 border-r-8 border-[#1A365D] pr-4">{t.title}</h2>
        
        <div className="space-y-8">
          <div className="flex justify-between items-center">
            <span className="font-bold text-[#1A365D]">{t.lang}</span>
            <select 
              value={language} 
              onChange={(e) => changeLanguage(e.target.value)}
              className="p-2 bg-gray-50 rounded-lg font-black border-2 border-[#1A365D]/10"
            >
              <option value="ar">العربية</option>
              <option value="en">English</option>
              <option value="fr">Français</option>
            </select>
          </div>

          <div className="flex justify-between items-center">
            <span className="font-bold text-[#1A365D]">{t.audio}</span>
            <button 
              onClick={() => setAudio(!audioEnabled)}
              className={`px-6 py-2 rounded-full font-black transition-all ${audioEnabled ? 'bg-[#1A365D] text-white' : 'bg-gray-100 text-gray-400'}`}
            >
              {audioEnabled ? 'ON' : 'OFF'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
