import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import Cropper from 'react-easy-crop';
import userService from '../services/userService';
import { useAuth } from '../context/AuthContext';
import PolicyPage from './14_PolicyPage';

const countries = [
  { code: '+970', flag: 'üáµüá∏', name: 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ', nameEn: 'Palestine' },
  { code: '+962', flag: 'üáØüá¥', name: 'ÿßŸÑÿ£ÿ±ÿØŸÜ', nameEn: 'Jordan' },
  { code: '+964', flag: 'ÿßŸÑÿπÿ±ÿßŸÇ', name: 'ÿßŸÑÿπÿ±ÿßŸÇ' },
  { code: '+963', flag: 'üá∏üáæ', name: 'ÿ≥Ÿàÿ±Ÿäÿß', nameEn: 'Syria' },
  { code: '+961', flag: 'üá±üáß', name: 'ŸÑÿ®ŸÜÿßŸÜ', nameEn: 'Lebanon' },
  { code: '+966', flag: 'üá∏üá¶', name: 'ÿßŸÑŸÖŸÖŸÑŸÉÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©', nameEn: 'Saudi Arabia' },
  { code: '+974', flag: 'üá∂üá¶', name: 'ŸÇÿ∑ÿ±', nameEn: 'Qatar' },
  { code: '+973', flag: 'üáßüá≠', name: 'ÿßŸÑÿ®ÿ≠ÿ±ŸäŸÜ', nameEn: 'Bahrain' },
  { code: '+971', flag: 'üá¶üá™', name: 'ÿßŸÑÿ•ŸÖÿßÿ±ÿßÿ™ ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑŸÖÿ™ÿ≠ÿØÿ©', nameEn: 'UAE' },
  { code: '+968', flag: 'üá¥üá≤', name: 'ÿπŸÖÿßŸÜ', nameEn: 'Oman' },
  { code: '+967', flag: 'üáæüá™', name: 'ÿßŸÑŸäŸÖŸÜ', nameEn: 'Yemen' },
  { code: '+965', flag: 'üá∞üáº', name: 'ÿßŸÑŸÉŸàŸäÿ™', nameEn: 'Kuwait' },
  { code: '+20', flag: 'üá™üá¨', name: 'ŸÖÿµÿ±', nameEn: 'Egypt' },
  { code: '+218', flag: 'üá±üáæ', name: 'ŸÑŸäÿ®Ÿäÿß', nameEn: 'Libya' },
  { code: '+216', flag: 'üáπüá≥', name: 'ÿ™ŸàŸÜÿ≥', nameEn: 'Tunisia' },
  { code: '+212', flag: 'üá≤üá¶', name: 'ÿßŸÑŸÖÿ∫ÿ±ÿ®', nameEn: 'Morocco' },
  { code: '+213', flag: 'üá©üáø', name: 'ÿßŸÑÿ¨ÿ≤ÿßÿ¶ÿ±', nameEn: 'Algeria' },
  { code: '+222', flag: 'üá≤üá∑', name: 'ŸÖŸàÿ±Ÿäÿ™ÿßŸÜŸäÿß', nameEn: 'Mauritania' },
  { code: '+249', flag: 'üá∏üá©', name: 'ÿßŸÑÿ≥ŸàÿØÿßŸÜ', nameEn: 'Sudan' },
  { code: '+252', flag: 'üá∏üá¥', name: 'ÿßŸÑÿµŸàŸÖÿßŸÑ', nameEn: 'Somalia' },
  { code: '+253', flag: 'üá©üáØ', name: 'ÿ¨Ÿäÿ®Ÿàÿ™Ÿä', nameEn: 'Djibouti' },
  { code: '+269', flag: 'üá∞üá≤', name: 'ÿ¨ÿ≤ÿ± ÿßŸÑŸÇŸÖÿ±', nameEn: 'Comoros' },
  { code: '+1', flag: 'üá∫üá∏', name: 'USA', nameEn: 'USA' },
  { code: '+44', flag: 'üá¨üáß', name: 'UK', nameEn: 'UK' },
  { code: '+33', flag: 'üá´üá∑', name: 'France', nameEn: 'France' },
  { code: '+49', flag: 'üá©üá™', name: 'Germany', nameEn: 'Germany' },
  { code: '+90', flag: 'üáπüá∑', name: 'Turkey', nameEn: 'Turkey' },
];

const translations = {
  ar: {
    signup: "ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ", firstName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ", lastName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ÿÆŸäÿ±",
    companyName: "ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ¥ÿ£ÿ©", country: "ÿßÿÆÿ™ÿ± ÿßŸÑÿ®ŸÑÿØ", mustAgree: "Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖŸàÿßŸÅŸÇÿ© ÿπŸÑŸâ ÿßŸÑÿ≥Ÿäÿßÿ≥ÿ©",
    loading: "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...", aiAnalyzing: "ÿ™ÿ≠ŸÑŸäŸÑ ÿßŸÑÿµŸàÿ±ÿ©... ü§ñ",
    invalidFace: "‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ÿ±ŸÅÿπ ÿµŸàÿ±ÿ© ÿ¥ÿÆÿµŸäÿ© ÿ≠ŸÇŸäŸÇŸäÿ© ŸÑŸÑŸàÿ¨Ÿá (Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ∏Ÿáÿ± Ÿàÿ¨ŸáŸÉ ÿ®Ÿàÿ∂Ÿàÿ≠)",
    invalidLogo: "‚ö†Ô∏è Ÿäÿ±ÿ¨Ÿâ ÿ±ŸÅÿπ ŸÑŸàÿ¨Ÿà ÿ≠ŸÇŸäŸÇŸä ŸÑŸÑŸÖŸÜÿ¥ÿ£ÿ© (Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ÿ¥ÿπÿßÿ±ÿßŸã ÿ±ÿ≥ŸÖŸäÿßŸã)",
    email: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä", emailOptional: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)", phone: "ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ", password: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±",
    confirmPassword: "ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±", alreadyHave: "ŸÑÿØŸäŸÉ ÿ≠ÿ≥ÿßÿ® ÿ®ÿßŸÑŸÅÿπŸÑÿü", loginNow: "ÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑŸÉ ÿßŸÑÿ¢ŸÜ",
    eduLevel: "ÿßŸÑŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿπŸÑŸÖŸä", determination: "ŸáŸÑ ÿ£ŸÜÿ™ ŸÖŸÜ ÿ∞ŸàŸä ÿßŸÑŸáŸÖŸÖÿü", needType: "ŸÜŸàÿπ ÿßŸÑÿßÿ≠ÿ™Ÿäÿßÿ¨",
    yes: "ŸÜÿπŸÖ", no: "ŸÑÿß", visual: "ÿ®ÿµÿ±Ÿä", hearing: "ÿ≥ŸÖÿπŸä", speech: "ŸÜÿ∑ŸÇŸä", motor: "ÿ≠ÿ±ŸÉŸä",
    levels: ["ÿØŸÉÿ™Ÿàÿ±ÿßÿ©", "ŸÖÿßÿ¨ÿ≥ÿ™Ÿäÿ±", "ÿ®ŸÉÿßŸÑŸàÿ±ŸäŸàÿ≥", "ÿ´ÿßŸÜŸàŸäÿ©", "ÿßÿπÿØÿßÿØŸäÿ© / ŸÖÿ™Ÿàÿ≥ÿ∑ÿ©", "ÿßÿ®ÿ™ÿØÿßÿ¶Ÿäÿ© / ÿßÿ≥ÿßÿ≥Ÿäÿ©", "ÿ∫Ÿäÿ± ŸÖÿ™ÿπŸÑŸÖ / ÿ£ŸÇÿ±ÿ£ Ÿàÿ£ŸÉÿ™ÿ®", "ÿ£ŸÖŸä / ŸÑÿß ÿ£ŸÇÿ±ÿ£ ŸàŸÑÿß ÿ£ŸÉÿ™ÿ®"],
    companyIndustry: "ŸÖÿ¨ÿßŸÑ ÿπŸÖŸÑ ÿßŸÑÿ¥ÿ±ŸÉÿ©",
    industries: ["ÿ¥ÿ±ŸÉÿ© ÿµŸÜÿßÿπŸäÿ©", "ÿ¥ÿ±ŸÉÿ© ÿ™ÿ¨ÿßÿ±Ÿäÿ©", "ÿ¥ÿ±ŸÉÿ© ÿÆÿØŸÖŸäÿ©", "ŸÖÿ§ÿ≥ÿ≥ÿ© ÿ™ÿπŸÑŸäŸÖŸäÿ© / ÿ£ŸÉÿßÿØŸäŸÖŸäÿ©", "ŸÖÿ§ÿ≥ÿ≥ÿ© ÿ≠ŸÉŸàŸÖŸäÿ©", "ŸÖŸÉÿ™ÿ®", "ŸÖÿ≠ŸÑ", "Ÿàÿ±ÿ¥ÿ©"],
    authorizedName: "ÿßÿ≥ŸÖ ÿßŸÑÿ¥ÿÆÿµ ÿßŸÑŸÖŸÅŸàÿ∂", authorizedPosition: "Ÿàÿ∏ŸäŸÅÿ© ÿßŸÑÿ¥ÿÆÿµ ÿßŸÑŸÖŸÅŸàÿ∂",
    confirmData: "ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ŸÉÿßŸÖŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸàŸÖÿ≥ÿ§ŸàŸÑ ÿπŸÜŸáÿß", cropTitle: "ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑÿµŸàÿ±ÿ©",
    passMatch: "ŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ŸÖÿ™ÿ∑ÿßÿ®ŸÇÿ©", photoReq: "Ÿäÿ±ÿ¨Ÿâ ÿ±ŸÅÿπ ÿµŸàÿ±ÿ© ÿ¥ÿÆÿµŸäÿ©"
  }
};

export default function AuthPage() {
  const { language, login: performContextLogin } = useAuth();
  const lang = language || 'ar';
  const t = translations[lang] || translations.ar;
  const navigate = useNavigate();

  const [userType, setUserType] = useState('individuals');
  const [agreed, setAgreed] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const [tempImage, setTempImage] = useState(null);
  const [showPhotoModal, setShowPhotoModal] = useState(false);
  const [showCropModal, setShowCropModal] = useState(false);
  const [showConfirmPopup, setShowConfirmPopup] = useState(false);
  const [showPolicy, setShowPolicy] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const [imgAnalyzing, setImgAnalyzing] = useState(false);
  const [imgError, setImgError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPass, setShowPass] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);

  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);

  const [formData, setFormData] = useState({
    firstName: '', lastName: '', companyName: '', email: '', password: '',
    confirmPassword: '', phone: '', country: '', countryCode: '+970',
    education: '', isSpecialNeeds: false, specialNeedType: '',
    industry: '', authorizedName: '', authorizedPosition: ''
  });

  const [fieldErrors, setFieldErrors] = useState({});

  useEffect(() => { setIsVisible(true); }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (fieldErrors[name]) {
      setFieldErrors(prev => {
        const newErrs = { ...prev };
        delete newErrs[name];
        return newErrs;
      });
    }
  };

  const onCropComplete = useCallback((activeArea, activeAreaPixels) => {
    setCroppedAreaPixels(activeAreaPixels);
  }, []);

  const getPhoto = async (source) => {
    setShowPhotoModal(false);
    try {
      const image = await Camera.getPhoto({
        quality: 90,
        allowEditing: false,
        resultType: CameraResultType.Base64,
        source
      });
      setTempImage(`data:image/jpeg;base64,${image.base64String}`);
      setShowCropModal(true);
    } catch (e) {}
  };

  const handleCropSave = async () => {
    try {
      const croppedImage = await getCroppedImg(tempImage, croppedAreaPixels);
      setShowCropModal(false);
      setImgAnalyzing(true);
      setImgError('');
      setProfileImage(null); // ŸÖÿ≥ÿ≠ ÿßŸÑÿµŸàÿ±ÿ© ÿßŸÑÿ≥ÿßÿ®ŸÇÿ© ÿ≠ÿ™Ÿâ Ÿäÿ™ŸÖ ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßŸÑÿ¨ÿØŸäÿØÿ©

      try {
        const res = await userService.analyzeImage({
          image: croppedImage,
          targetType: userType === 'companies' ? 'logo' : 'face'
        });

        // ÿßŸÑÿ¢ŸÜ ŸÜÿ±ŸÅÿ∂ ÿ®ÿ¥ŸÉŸÑ ŸÇÿ∑ÿπŸä ÿ•ÿ∞ÿß ŸÑŸÖ ÿ™ŸÉŸÜ ÿßŸÑŸÜÿ™Ÿäÿ¨ÿ© isValid: true
        if (res.data && res.data.isValid === true) {
          setProfileImage(croppedImage);
          setImgError('');
        } else {
          setImgError(userType === 'companies' ? t.invalidLogo : t.invalidFace);
          setProfileImage(null);
        }
      } catch (e) {
        console.error("AI Analysis Failed", e);
        setImgError(lang === 'ar' ? "‚ö†Ô∏è ÿπÿ∞ÿ±ÿßŸãÿå ŸÑŸÖ ŸÜÿ™ŸÖŸÉŸÜ ŸÖŸÜ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿµŸàÿ±ÿ©. ÿ≠ÿßŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ." : "‚ö†Ô∏è Image verification failed.");
        setProfileImage(null);
      } finally {
        setImgAnalyzing(false);
      }
    } catch (e) {
      console.error(e);
      setImgAnalyzing(false);
    }
  };

  const getCroppedImg = (imageSrc, pixelCrop) => {
    return new Promise((resolve) => {
      const image = new Image();
      image.src = imageSrc;
      image.onload = () => {
        const canvas = document.createElement('canvas');
        canvas.width = pixelCrop.width;
        canvas.height = pixelCrop.height;
        const ctx = canvas.getContext('2d');
        ctx.drawImage(image, pixelCrop.x, pixelCrop.y, pixelCrop.width, pixelCrop.height, 0, 0, pixelCrop.width, pixelCrop.height);
        resolve(canvas.toDataURL('image/jpeg'));
      };
    });
  };

  const validate = () => {
    const errors = {};
    if (!formData.country) errors.country = lang === 'ar' ? "Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ®ŸÑÿØ" : "Select Country";
    if (userType === 'individuals') {
      if (!formData.firstName) errors.firstName = lang === 'ar' ? "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ ŸÖÿ∑ŸÑŸàÿ®" : "First Name required";
      if (!formData.lastName) errors.lastName = lang === 'ar' ? "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ÿÆŸäÿ± ŸÖÿ∑ŸÑŸàÿ®" : "Last Name required";
      if (!formData.education) errors.education = lang === 'ar' ? "Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿ™ÿπŸÑŸäŸÖŸä" : "Select Education";
    } else {
      if (!formData.companyName) errors.companyName = lang === 'ar' ? "ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÜÿ¥ÿ£ÿ© ŸÖÿ∑ŸÑŸàÿ®" : "Company Name required";
      if (!formData.industry) errors.industry = lang === 'ar' ? "Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÖÿ¨ÿßŸÑ ÿßŸÑÿπŸÖŸÑ" : "Select Industry";
      if (!formData.authorizedName) errors.authorizedName = lang === 'ar' ? "ÿßÿ≥ŸÖ ÿßŸÑŸÖŸÅŸàÿ∂ ŸÖÿ∑ŸÑŸàÿ®" : "Authorized Name required";
      if (!formData.authorizedPosition) errors.authorizedPosition = lang === 'ar' ? "Ÿàÿ∏ŸäŸÅÿ© ÿßŸÑŸÖŸÅŸàÿ∂ ŸÖÿ∑ŸÑŸàÿ®ÿ©" : "Authorized Position required";
    }
    if (!formData.phone) errors.phone = lang === 'ar' ? "ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ ŸÖÿ∑ŸÑŸàÿ®" : "Phone required";
    if (!formData.email) errors.email = lang === 'ar' ? "ÿßŸÑÿ®ÿ±ŸäÿØ ŸÖÿ∑ŸÑŸàÿ®" : "Email required";
    if (!formData.password) errors.password = lang === 'ar' ? "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿ©" : "Password required";
    if (formData.password !== formData.confirmPassword) errors.confirmPassword = t.passMatch;

    setFieldErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleRegisterClick = (e) => {
    e.preventDefault();
    if (!validate()) return;
    if (imgAnalyzing) return; // ŸÖŸÜÿπ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ ÿ£ÿ´ŸÜÿßÿ° ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑ
    if (!profileImage) { setImgError(t.photoReq); return; }
    if (!agreed) { setFieldErrors(p => ({...p, agreed: t.mustAgree})); return; }
    setShowConfirmPopup(true);
  };

  const performRegister = async () => {
    setShowConfirmPopup(false);
    setLoading(true);
    try {
      const payload = { ...formData, profileImage, role: userType === 'companies' ? 'HR' : 'Employee' };
      const res = await userService.register(payload);
      await performContextLogin(res.data.user, res.data.token);

      const user = res.data.user;
      const isIlliterate = user.education === 'ÿ£ŸÖŸä / ŸÑÿß ÿ£ŸÇÿ±ÿ£ ŸàŸÑÿß ÿ£ŸÉÿ™ÿ®' || user.education === 'Illiterate';
      const isBlind = user.isSpecialNeeds && user.specialNeedType === 'ÿ®ÿµÿ±Ÿä';

      if (isIlliterate && isBlind) navigate('/onboarding-ultimate');
      else if (isIlliterate) navigate('/onboarding-illiterate');
      else if (isBlind) navigate('/onboarding-visual');
      else navigate(user.role === 'HR' ? '/onboarding-companies' : '/onboarding-individuals');

    } catch (err) {
      setFieldErrors({ api: err.response?.data?.error || "Error" });
    } finally {
      setLoading(false);
    }
  };

  const inputBase = "w-full p-5 bg-white/60 rounded-[2rem] font-black text-center shadow-sm border-2 border-transparent focus:border-[#1A365D]/20 outline-none transition-all";
  const errorText = "text-[10px] text-[#FF0000] font-black px-6 mt-1";

  return (
    <div className={`min-h-screen w-full flex flex-col items-center p-4 bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'} select-none`} dir={lang === 'ar' ? 'rtl' : 'ltr'}>
      {showPolicy && <div className="fixed inset-0 z-[12000] bg-white overflow-y-auto"><PolicyPage /><button onClick={() => { setAgreed(true); setShowPolicy(false); }} className="fixed bottom-6 left-1/2 -translate-x-1/2 w-full max-w-xs py-5 bg-[#1A365D] text-white rounded-2xl font-black shadow-2xl">{lang === 'ar' ? 'ÿ£ŸàÿßŸÅŸÇ' : 'I Agree'}</button></div>}

      <div className="w-full max-w-lg flex flex-col items-center mt-4">
        <div className="text-center mb-6">
          <img src="/logo.jpg" alt="Logo" className="h-28 w-28 mx-auto rounded-full shadow-2xl mb-4 border-[3px] border-[#1A365D]" />
          <h2 className="text-4xl font-black text-[#1A365D] italic">{t.signup}</h2>
        </div>

        <div className="flex gap-2 p-1 bg-white/30 rounded-2xl mb-6 w-full shadow-inner">
          <button type="button" onClick={() => setUserType('individuals')} className={`flex-1 py-4 rounded-xl text-xs font-black transition-all ${userType === 'individuals' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-[#1A365D]/40'}`}>{lang === 'ar' ? 'ÿ£ŸÅÿ±ÿßÿØ' : 'Individuals'}</button>
          <button type="button" onClick={() => setUserType('companies')} className={`flex-1 py-4 rounded-xl text-xs font-black transition-all ${userType === 'companies' ? 'bg-[#1A365D] text-white shadow-lg' : 'text-[#1A365D]/40'}`}>{lang === 'ar' ? 'ÿ¥ÿ±ŸÉÿßÿ™' : 'Companies'}</button>
        </div>

        <form onSubmit={handleRegisterClick} className="w-full space-y-4 pb-10" noValidate>
          <div className="flex flex-col items-center mb-2">
            <button type="button" onClick={() => setShowPhotoModal(true)} className="w-36 h-36 rounded-full bg-white/50 border-4 border-white shadow-2xl flex items-center justify-center overflow-hidden relative active:scale-95 transition-all">
              {profileImage ? <img src={profileImage} className="w-full h-full object-cover" /> : <span className="text-7xl opacity-20">üë§</span>}
              {imgAnalyzing && (
                <div className="absolute inset-0 bg-[#1A365D]/60 flex flex-col items-center justify-center gap-2">
                  <div className="w-8 h-8 border-4 border-white border-t-transparent rounded-full animate-spin"></div>
                  <span className="text-[10px] text-white font-bold">{t.aiAnalyzing}</span>
                </div>
              )}
            </button>
            {imgError && <p className={errorText + " text-center"}>{imgError}</p>}
          </div>

          <div className="w-full">
            <select name="country" value={formData.country} onChange={handleInputChange} className={`${inputBase} ${!formData.country ? 'text-gray-400' : 'text-[#1A365D]'} appearance-none`} required>
              <option value="" disabled>-- {t.country} --</option>
              {countries.map(c => <option key={c.nameEn} value={c.nameEn} className="text-[#1A365D]">{c.flag} {lang === 'ar' ? c.name : c.nameEn}</option>)}
            </select>
            {fieldErrors.country && <p className={errorText}>{fieldErrors.country}</p>}
          </div>

          {userType === 'individuals' ? (
            <>
              <div className="w-full grid grid-cols-2 gap-3">
                <div className="w-full">
                  <input type="text" name="firstName" placeholder={t.firstName} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400`} />
                  {fieldErrors.firstName && <p className={errorText}>{fieldErrors.firstName}</p>}
                </div>
                <div className="w-full">
                  <input type="text" name="lastName" placeholder={t.lastName} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400`} />
                  {fieldErrors.lastName && <p className={errorText}>{fieldErrors.lastName}</p>}
                </div>
              </div>
              <div className="w-full">
                <select name="education" value={formData.education} onChange={handleInputChange} className={`${inputBase} ${!formData.education ? 'text-gray-400' : 'text-[#1A365D]'} appearance-none`} required>
                  <option value="" disabled>-- {t.eduLevel} --</option>
                  {t.levels.map(l => <option key={l} value={l} className="text-[#1A365D]">{l}</option>)}
                </select>
                {fieldErrors.education && <p className={errorText}>{fieldErrors.education}</p>}
              </div>
            </>
          ) : (
            <>
              <div className="w-full">
                <input type="text" name="companyName" placeholder={t.companyName} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400`} />
                {fieldErrors.companyName && <p className={errorText}>{fieldErrors.companyName}</p>}
              </div>
              <div className="w-full">
                <select name="industry" value={formData.industry} onChange={handleInputChange} className={`${inputBase} ${!formData.industry ? 'text-gray-400' : 'text-[#1A365D]'} appearance-none`} required>
                  <option value="" disabled>-- {t.companyIndustry} --</option>
                  {t.industries.map(ind => <option key={ind} value={ind} className="text-[#1A365D]">{ind}</option>)}
                </select>
                {fieldErrors.industry && <p className={errorText}>{fieldErrors.industry}</p>}
              </div>
              <div className="grid grid-cols-2 gap-3">
                <div className="w-full">
                  <input type="text" name="authorizedName" placeholder={t.authorizedName} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400 text-xs`} />
                  {fieldErrors.authorizedName && <p className={errorText}>{fieldErrors.authorizedName}</p>}
                </div>
                <div className="w-full">
                  <input type="text" name="authorizedPosition" placeholder={t.authorizedPosition} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400 text-xs`} />
                  {fieldErrors.authorizedPosition && <p className={errorText}>{fieldErrors.authorizedPosition}</p>}
                </div>
              </div>
            </>
          )}

          <div className="w-full flex gap-2 items-start">
            <div className="flex-1">
              <input type="tel" name="phone" placeholder={t.phone} onChange={handleInputChange} className="w-full p-5 bg-white/60 text-[#1A365D] placeholder:text-gray-400 rounded-[2rem] font-black text-center shadow-sm outline-none border-2 border-transparent focus:border-[#1A365D]/20 h-[68px]" />
              {fieldErrors.phone && <p className={errorText}>{fieldErrors.phone}</p>}
            </div>
            <select name="countryCode" value={formData.countryCode} onChange={handleInputChange} className="w-32 p-5 bg-white/60 text-[#1A365D] rounded-[2rem] outline-none font-black text-center shadow-sm appearance-none border-2 border-transparent h-[68px]">
              {countries.map(c => <option key={c.code} value={c.code}>{c.flag} {c.code}</option>)}
            </select>
          </div>

          <div className="w-full">
            <input type="email" name="email" placeholder={t.email} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400`} />
            {fieldErrors.email && <p className={errorText}>{fieldErrors.email}</p>}
          </div>

          <div className="w-full space-y-4">
            <div className="relative">
              <input type={showPass ? "text" : "password"} name="password" placeholder={t.password} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400`} />
              <button type="button" onClick={() => setShowPass(!showPass)} className="absolute left-6 top-1/2 -translate-y-1/2 text-xl opacity-30">{showPass ? 'üëÅÔ∏è' : 'üôà'}</button>
              {fieldErrors.password && <p className={errorText}>{fieldErrors.password}</p>}
            </div>
            <div className="relative">
              <input type={showConfirmPass ? "text" : "password"} name="confirmPassword" placeholder={t.confirmPassword} onChange={handleInputChange} className={`${inputBase} text-[#1A365D] placeholder:text-gray-400`} />
              <button type="button" onClick={() => setShowConfirmPass(!showConfirmPass)} className="absolute left-6 top-1/2 -translate-y-1/2 text-xl opacity-30">{showConfirmPass ? 'üëÅÔ∏è' : 'üôà'}</button>
              {fieldErrors.confirmPassword && <p className={errorText}>{fieldErrors.confirmPassword}</p>}
            </div>
          </div>

          {userType === 'individuals' && (
            <div className="p-6 bg-white/30 rounded-[2.5rem] space-y-4 shadow-inner border border-white/50">
              <div className="flex items-center justify-between px-2">
                <span className="text-xs font-black text-[#1A365D]/60">{t.determination}</span>
                <div className="flex gap-6">
                  <label className="flex items-center gap-2 cursor-pointer"><input type="radio" name="isSpecialNeeds" checked={formData.isSpecialNeeds === true} onChange={() => setFormData(p => ({...p, isSpecialNeeds: true}))} className="w-5 h-5" /><span className="text-xs font-black text-[#1A365D]">{t.yes}</span></label>
                  <label className="flex items-center gap-2 cursor-pointer"><input type="radio" name="isSpecialNeeds" checked={formData.isSpecialNeeds === false} onChange={() => setFormData(p => ({...p, isSpecialNeeds: false}))} className="w-5 h-5" /><span className="text-xs font-black text-[#1A365D]">{t.no}</span></label>
                </div>
              </div>
              {formData.isSpecialNeeds && (
                <div className="w-full">
                  <select name="specialNeedType" value={formData.specialNeedType} onChange={handleInputChange} className={`${inputBase} ${!formData.specialNeedType ? 'text-gray-400' : 'text-[#1A365D]'} !p-4 !text-xs`} required>
                    <option value="" disabled>-- {t.needType} --</option>
                    <option value="ÿ®ÿµÿ±Ÿä">{t.visual}</option><option value="ÿ≥ŸÖÿπŸä">{t.hearing}</option><option value="ŸÜÿ∑ŸÇŸä">{t.speech}</option><option value="ÿ≠ÿ±ŸÉŸä">{t.motor}</option>
                  </select>
                </div>
              )}
            </div>
          )}

          <div className="w-full">
            <div className="flex items-center gap-4 px-6 py-2 text-[11px] font-bold text-[#1A365D]/40">
              <input type="checkbox" checked={agreed} onChange={(e) => { setAgreed(e.target.checked); if(e.target.checked) setFieldErrors(p => { const n={...p}; delete n.agreed; return n; }) }} className="w-5 h-5 rounded-lg border-white text-[#1A365D] bg-white/50" />
              <p>{lang === 'ar' ? 'ÿ£ŸàÿßŸÅŸÇ ÿπŸÑŸâ' : 'I agree to'} <button type="button" onClick={() => setShowPolicy(true)} className="text-[#1A365D] underline font-black">{lang === 'ar' ? 'ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©' : 'Privacy Policy'}</button></p>
            </div>
            {fieldErrors.agreed && <p className={errorText}>{fieldErrors.agreed}</p>}
          </div>

          {fieldErrors.api && <div className="p-4 bg-red-100 text-[#FF0000] rounded-2xl text-[11px] font-black text-center border border-red-200">{fieldErrors.api}</div>}

          <button type="submit" disabled={loading || imgAnalyzing} className="w-full py-7 rounded-[3rem] bg-[#1A365D] text-white font-black shadow-2xl active:scale-95 transition-all text-2xl mt-4">
            {loading ? <div className="w-8 h-8 border-4 border-white/30 border-t-white rounded-full animate-spin mx-auto"></div> : t.signup}
          </button>
        </form>
      </div>

      {showPhotoModal && (
        <div className="fixed inset-0 z-[13000] flex items-center justify-center p-6 bg-black/40 backdrop-blur-sm">
          <div className="bg-white rounded-[3rem] p-10 w-full max-w-xs text-center shadow-2xl">
            <h3 className="text-[#1A365D] font-black text-xl mb-8">{lang === 'ar' ? 'ÿ•ÿ∂ÿßŸÅÿ© ÿµŸàÿ±ÿ©' : 'Add Photo'}</h3>
            <div className="space-y-4">
              <button onClick={() => getPhoto(CameraSource.Camera)} className="w-full py-5 bg-gray-50 text-[#1A365D] rounded-[1.5rem] font-black">üì∑ {lang === 'ar' ? 'ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß' : 'Camera'}</button>
              <button onClick={() => getPhoto(CameraSource.Photos)} className="w-full py-5 bg-gray-50 text-[#1A365D] rounded-[1.5rem] font-black">üñºÔ∏è {lang === 'ar' ? 'ÿßŸÑŸÖÿπÿ±ÿ∂' : 'Gallery'}</button>
              <button onClick={() => setShowPhotoModal(false)} className="w-full py-4 text-gray-400 font-bold">{lang === 'ar' ? 'ÿ•ŸÑÿ∫ÿßÿ°' : 'Cancel'}</button>
            </div>
          </div>
        </div>
      )}

      {showCropModal && (
        <div className="fixed inset-0 z-[14000] bg-black flex flex-col items-center justify-center p-4">
          <div className="relative w-full aspect-square bg-white rounded-3xl overflow-hidden mb-6">
            <Cropper image={tempImage} crop={crop} zoom={zoom} aspect={1} cropShape="round" showGrid={false} onCropChange={setCrop} onCropComplete={onCropComplete} onZoomChange={setZoom} />
          </div>
          <div className="w-full space-y-4 max-w-xs">
            <button onClick={handleCropSave} className="w-full py-5 bg-[#1A365D] text-white rounded-2xl font-black text-xl shadow-lg">{lang === 'ar' ? 'ÿ≠ŸÅÿ∏ ÿßŸÑÿµŸàÿ±ÿ©' : 'Save Image'}</button>
            <button onClick={() => setShowCropModal(false)} className="w-full py-4 text-white/60 font-bold">{lang === 'ar' ? 'ÿ•ŸÑÿ∫ÿßÿ°' : 'Cancel'}</button>
          </div>
        </div>
      )}

      {showConfirmPopup && (
        <div className="fixed inset-0 z-[15000] flex items-center justify-center p-6 bg-black/40 backdrop-blur-sm">
          <div className="bg-white rounded-[3rem] p-10 w-full max-w-xs text-center shadow-2xl">
            <p className="text-[#1A365D] font-black text-lg mb-8 leading-relaxed">{t.confirmData}</p>
            <div className="flex gap-4">
              <button onClick={performRegister} className="flex-1 py-4 bg-[#1A365D] text-white rounded-2xl font-black shadow-lg">{t.yes}</button>
              <button onClick={() => setShowConfirmPopup(false)} className="flex-1 py-4 border-2 border-[#1A365D] text-[#1A365D] rounded-2xl font-black">{t.no}</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
