import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export const OnboardingCompanies = ({ lang = 'ar' }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  
  const [formData, setFormData] = useState({
    bio: '', website: '', commercialRegister: '', employeesCount: '',
    country: '', industry: '', address: '', location: null
  });

  const t = {
    ar: { 
      title: 'ÿ™Ÿàÿ´ŸäŸÇ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖŸÜÿ¥ÿ£ÿ©', 
      sub: 'ÿ£ŸÉŸÖŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿ¥ÿ±ŸÉÿ™ŸÉ ŸÑÿ®ŸÜÿßÿ° ÿ¨ÿ≥ÿ± ŸÖŸÜ ÿßŸÑÿ´ŸÇÿ© ŸÖÿπ ÿßŸÑŸÉŸÅÿßÿ°ÿßÿ™', 
      desc: 'ŸàÿµŸÅ ŸÜÿ¥ÿßÿ∑ ÿßŸÑÿ¥ÿ±ŸÉÿ© Ÿàÿ±ÿ§Ÿäÿ™Ÿáÿß', 
      website: 'ÿßŸÑŸÖŸàŸÇÿπ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿßŸÑÿ±ÿ≥ŸÖŸä', 
      cr: 'ÿ±ŸÇŸÖ ÿßŸÑÿ≥ÿ¨ŸÑ ÿßŸÑÿ™ÿ¨ÿßÿ±Ÿä ÿßŸÑŸÖŸàÿ≠ÿØ', 
      empCount: 'ÿπÿØÿØ ÿßŸÑŸÉŸàÿßÿØÿ± ÿßŸÑÿ®ÿ¥ÿ±Ÿäÿ©',
      country: 'ÿØŸàŸÑÿ© ÿßŸÑŸÖŸÇÿ± ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä',
      industry: 'ŸÇÿ∑ÿßÿπ ÿßŸÑÿ£ÿπŸÖÿßŸÑ',
      address: 'ÿπŸÜŸàÿßŸÜ ÿßŸÑŸÖŸÉÿ™ÿ® / ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä',
      mapLocation: 'ÿ™ÿ≠ÿØŸäÿØ ÿßŸÑÿ•ÿ≠ÿØÿßÿ´Ÿäÿßÿ™ ÿπŸÑŸâ ÿßŸÑÿÆÿ±Ÿäÿ∑ÿ©',
      finish: 'ÿ≠ŸÅÿ∏ ŸàÿßŸÑÿ®ÿØÿ° ÿ®ÿßŸÑÿ™Ÿàÿ∏ŸäŸÅ',
      error: 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ÿå Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÑÿßÿ≠ŸÇÿßŸã',
      fields: {
        countries: ['ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©', 'ÿßŸÑÿ•ŸÖÿßÿ±ÿßÿ™', 'ŸÖÿµÿ±', 'ÿßŸÑÿπÿ±ÿßŸÇ', 'ÿßŸÑÿ£ÿ±ÿØŸÜ', 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ', 'ÿßŸÑŸÉŸàŸäÿ™', 'ŸÇÿ∑ÿ±'],
        industries: ['ÿ™ŸÉŸÜŸàŸÑŸàÿ¨Ÿäÿß ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™', 'ÿßŸÑŸáŸÜÿØÿ≥ÿ© ŸàÿßŸÑÿ•ŸÜÿ¥ÿßÿ°ÿßÿ™', 'ÿßŸÑÿ∑ÿ®Ÿäÿ© ŸàÿßŸÑÿØŸàÿßÿ¶Ÿäÿ©', 'ÿßŸÑÿ™ÿπŸÑŸäŸÖ ŸàÿßŸÑÿ™ÿØÿ±Ÿäÿ®', 'ÿßŸÑÿÆÿØŸÖÿßÿ™ ÿßŸÑŸÑŸàÿ¨ÿ≥ÿ™Ÿäÿ©', 'ŸÖÿ§ÿ≥ÿ≥ÿ© ÿ≠ŸÉŸàŸÖŸäÿ©', 'ÿ£ÿÆÿ±Ÿâ']
      }
    },
    en: { 
      title: 'Company Onboarding', 
      sub: 'Complete your profile to build trust with talents', 
      desc: 'Company Mission & Bio', 
      website: 'Official Website URL', 
      cr: 'Commercial Register Number', 
      empCount: 'Total Employees',
      country: 'Headquarters',
      industry: 'Business Sector',
      address: 'Main Office Address',
      mapLocation: 'Set Map Coordinates',
      finish: 'Save & Start Recruiting',
      error: 'Error, please try again',
      fields: {
        countries: ['Saudi Arabia', 'UAE', 'Egypt', 'Iraq', 'Jordan', 'Palestine', 'Kuwait', 'Qatar'],
        industries: ['IT', 'Engineering', 'Healthcare', 'Education', 'Logistics', 'Government', 'Other']
      }
    }
  }[lang] || { ar: {} };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const res = await api.put('/users/profile', formData);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      navigate('/dashboard');
    } catch (err) {
      setError(t.error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-6 bg-[#E3DAD0]">
      <div className="w-full max-w-xl bg-white rounded-[3.5rem] shadow-2xl p-8 md:p-12 text-center border border-white">
        <div className="mb-8">
          <div className="w-20 h-20 bg-[#1A365D] text-white rounded-full flex items-center justify-center mx-auto text-3xl mb-4 shadow-2xl">üè¢</div>
          <h2 className="text-3xl font-black text-[#1A365D]">{t.title}</h2>
          <p className="text-gray-400 font-bold mt-2 text-sm">{t.sub}</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4 text-right" dir={lang === 'ar' ? 'rtl' : 'ltr'}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <select 
              className={`w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs shadow-inner border-2 border-transparent focus:border-blue-100 ${formData.country ? 'text-[#1A365D]' : 'text-gray-400'}`}
              onChange={e => setFormData({...formData, country: e.target.value})}
              required
            >
              <option value="">{t.country}</option>
              {t.fields.countries.map(c => <option key={c} value={c}>{c}</option>)}
            </select>

            <select 
              className={`w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs shadow-inner border-2 border-transparent focus:border-blue-100 ${formData.industry ? 'text-[#1A365D]' : 'text-gray-400'}`}
              onChange={e => setFormData({...formData, industry: e.target.value})}
              required
            >
              <option value="">{t.industry}</option>
              {t.fields.industries.map(i => <option key={i} value={i}>{i}</option>)}
            </select>
          </div>

          <textarea 
            placeholder={t.desc} 
            className="w-full p-5 bg-gray-50 rounded-[2rem] outline-none font-bold text-xs h-28 shadow-inner border-2 border-transparent focus:border-blue-100 transition-all" 
            onChange={e => setFormData({...formData, bio: e.target.value})} 
          />

          <div className="space-y-3">
            <input 
              type="text" 
              placeholder={t.address} 
              className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs shadow-inner border-2 border-transparent focus:border-blue-100" 
              onChange={e => setFormData({...formData, address: e.target.value})} 
            />
            <button 
              type="button"
              className="w-full p-4 bg-[#1A365D]/5 text-[#1A365D] rounded-2xl font-black text-xs border-2 border-dashed border-[#1A365D]/20 flex items-center justify-center gap-2 hover:bg-[#1A365D]/10 transition-all active:scale-95"
            >
              üìç {t.mapLocation}
            </button>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input 
              type="text" 
              placeholder={t.cr} 
              className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs shadow-inner border-2 border-transparent focus:border-blue-100" 
              onChange={e => setFormData({...formData, commercialRegister: e.target.value})} 
            />
            <select 
              className={`w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs shadow-inner border-2 border-transparent focus:border-blue-100 ${formData.employeesCount ? 'text-[#1A365D]' : 'text-gray-400'}`}
              onChange={e => setFormData({...formData, employeesCount: e.target.value})}
            >
              <option value="" disabled hidden>{t.empCount}</option>
              <option value="1-10">1 - 10</option>
              <option value="11-50">11 - 50</option>
              <option value="51-200">51 - 200</option>
              <option value="201+">201+</option>
            </select>
          </div>

          <input 
            type="url" 
            placeholder={t.website} 
            className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs shadow-inner border-2 border-transparent focus:border-blue-100" 
            onChange={e => setFormData({...formData, website: e.target.value})} 
          />

          {error && <div className="p-3 bg-red-50 text-red-600 rounded-xl font-bold text-[10px] text-center">{error}</div>}

          <button type="submit" disabled={loading} className="w-full py-6 rounded-[2.5rem] font-black shadow-2xl active:scale-95 transition-all text-xl mt-4 bg-[#1A365D] text-white">
            {loading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ≠ŸÅÿ∏...' : t.finish}
          </button>
        </form>
      </div>
    </div>
  );
};
