import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import { userService } from '../services/api';
import { useAuth } from '../context/AuthContext';

const countryCodes = [
  { code: '+970', flag: 'üáµüá∏', name: 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ', name_en: 'Palestine', name_fr: 'Palestine' },
  { code: '+962', flag: 'üáØüá¥', name: 'ÿßŸÑÿ£ÿ±ÿØŸÜ', name_en: 'Jordan', name_fr: 'Jordanie' },
  { code: '+966', flag: 'üá∏üá¶', name: 'ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©', name_en: 'Saudi Arabia', name_fr: 'Arabie Saoudite' },
  { code: '+20', flag: 'üá™üá¨', name: 'ŸÖÿµÿ±', name_en: 'Egypt', name_fr: '√âgypte' },
];

const translations = {
  ar: {
    login: "ÿØÿÆŸàŸÑ", signup: "ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ", email: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä", phone: "ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ",
    password: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±", confirmPassword: "ÿ™ÿ£ŸÉŸäÿØ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±", remember: "ÿ™ÿ∞ŸÉÿ±ŸÜŸä", 
    specialNeeds: "ÿ£ŸÜÿß ŸÖŸÜ ÿ∞ŸàŸä ÿßŸÑŸáŸÖŸÖ ü¶æ", country: "ÿßŸÑÿ®ŸÑÿØ",
    mustAgree: "Ÿäÿ¨ÿ® ÿßŸÑŸÖŸàÿßŸÅŸÇÿ© ŸÑŸÑŸÖÿ™ÿßÿ®ÿπÿ©", loading: "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...",
    privacy: "ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©", needsType: "ŸÜŸàÿπ ÿßŸÑÿßÿ≠ÿ™Ÿäÿßÿ¨",
    firstName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ", lastName: "ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ÿÆŸäÿ±", gender: "ÿßŸÑÿ¨ŸÜÿ≥", dob: "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÖŸäŸÑÿßÿØ",
    dobHint: "ÿßÿÆÿ™ÿ± ÿ™ÿßÿ±ŸäÿÆ ŸÖŸäŸÑÿßÿØŸÉ",
    individuals: "ÿ£ŸÅÿ±ÿßÿØ", companies: "ÿ¥ÿ±ŸÉÿßÿ™", category: "ŸÜŸàÿπ ÿßŸÑÿ≠ÿ≥ÿßÿ®",
    companyName: "ÿßÿ≥ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ© / ÿßŸÑŸÖÿ§ÿ≥ÿ≥ÿ©", industry: "ŸÖÿ¨ÿßŸÑ ÿßŸÑÿπŸÖŸÑ",
    passMismatch: "ŸÉŸÑŸÖÿßÿ™ ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ŸÖÿ™ÿ∑ÿßÿ®ŸÇÿ©",
    agreePart1: "ÿ£ŸàÿßŸÅŸÇ ÿπŸÑŸâ ",
    industries: ["ÿ¥ÿ±ŸÉÿ© ÿµŸÜÿßÿπŸäÿ©", "ÿ¥ÿ±ŸÉÿ© ÿ™ÿ¨ÿßÿ±Ÿäÿ©", "ÿ¥ÿ±ŸÉÿ© ÿÆÿØŸÖŸäÿ©", "ŸÖÿ§ÿ≥ÿ≥ÿ© ÿ≠ŸÉŸàŸÖŸäÿ©", "ŸÖÿ§ÿ≥ÿ≥ÿ© ÿ™ÿπŸÑŸäŸÖŸäÿ© / ÿ£ŸÉÿßÿØŸäŸÖŸäÿ©", "ŸÖŸÉÿ™ÿ®", "ŸÖÿ≠ŸÑ", "Ÿàÿ±ÿ¥ÿ©", "ÿ£ÿÆÿ±Ÿâ"],
    needsOptions: ["ÿ®ÿµÿ±Ÿä", "ÿ≥ŸÖÿπŸä", "ŸÜÿ∑ŸÇŸä", "ÿ≠ÿ±ŸÉŸä", "ÿ≠ÿ≥Ÿä"]
  },
  en: {
    login: "Login", signup: "Sign Up", email: "Email", phone: "Phone",
    password: "Password", confirmPassword: "Confirm Password", remember: "Remember Me",
    specialNeeds: "Person of Determination ü¶æ", country: "Country",
    mustAgree: "Please agree to continue", loading: "Loading...",
    privacy: "Privacy Policy", needsType: "Disability Type",
    firstName: "First Name", lastName: "Last Name", gender: "Gender", dob: "Date of Birth",
    dobHint: "Select your date of birth",
    individuals: "Individuals", companies: "Companies", category: "Account Type",
    companyName: "Company Name", industry: "Industry",
    passMismatch: "Passwords do not match",
    agreePart1: "I agree to ",
    industries: ["Industrial", "Commercial", "Service Provider", "Government", "Educational / Academy", "Office", "Shop", "Workshop", "Other"],
    needsOptions: ["Visual", "Hearing", "Speech", "Physical", "Sensory"]
  },
  fr: {
    login: "Connexion", signup: "S'inscrire", email: "E-mail", phone: "T√©l√©phone",
    password: "Mot de passe", confirmPassword: "Confirmer", remember: "Se souvenir",
    specialNeeds: "Personne d√©termin√©e ü¶æ", country: "Pays",
    mustAgree: "Veuillez accepter pour continuer", loading: "Chargement...",
    privacy: "Politique de confidentialit√©", needsType: "Type de handicap",
    firstName: "Pr√©nom", lastName: "Nom", gender: "Genre", dob: "Date de naissance",
    dobHint: "S√©lectionnez votre date de naissance",
    individuals: "Individus", companies: "Entreprises", category: "Type de compte",
    companyName: "Nom de l'entreprise", industry: "Industrie",
    passMismatch: "Les mots de passe ne correspondent pas",
    agreePart1: "J'accepte la ",
    industries: ["Industrielle", "Commerciale", "Services", "Gouvernementale", "√âducative / Acad√©mie", "Bureau", "Magasin", "Atelier", "Autre"],
    needsOptions: ["Visuel", "Auditif", "Parole", "Physique", "Sensoriel"]
  }
};

export const AuthPage = ({ lang = 'ar' }) => {
  const { login: performContextLogin } = useAuth();
  const t = translations[lang] || translations.ar;
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [userType, setUserType] = useState('individuals');
  const [loginMethod, setLoginMethod] = useState('phone'); 
  const [showPass, setShowPass] = useState(false);
  const [showConfirmPass, setShowConfirmPass] = useState(false);
  const [rememberMe, setRememberMe] = useState(true);
  const [agreed, setAgreed] = useState(false);
  const [profileImage, setProfileImage] = useState(null);
  const [showPhotoModal, setShowPhotoModal] = useState(false);
  
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', companyName: '', companyIndustry: '',
    email: '', password: '', confirmPassword: '', phone: '', gender: '', dob: '', 
    isSpecialNeeds: false, specialNeedsType: '', country: 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ'
  });

  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const getPhoto = async (source) => {
    setShowPhotoModal(false);
    try {
      const image = await Camera.getPhoto({ quality: 90, allowEditing: true, resultType: CameraResultType.Base64, source });
      setProfileImage(`data:image/jpeg;base64,${image.base64String}`);
    } catch (e) {}
  };

  const handleAuth = async (e) => {
    e.preventDefault();
    if (!isLogin && !agreed) { setError(t.mustAgree); return; }
    if (!isLogin && formData.password !== formData.confirmPassword) { setError(t.passMismatch); return; }
    
    setError('');
    setLoading(true);
    try {
      const payload = { ...formData, profileImage, role: userType === 'companies' ? 'HR' : 'Employee' };
      const res = isLogin ? await userService.login(payload) : await userService.register(payload);
      await performContextLogin(res.data.user, res.data.token);
      navigate('/dashboard');
    } catch (err) { setError(err.userMessage || "Error"); } 
    finally { setLoading(false); }
  };

  const isRtl = lang === 'ar';

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center p-4 bg-[#E3DAD0]" dir={isRtl ? 'rtl' : 'ltr'}>
      {showPhotoModal && (
        <div className="fixed inset-0 z-[11000] flex items-end justify-center bg-black/40" onClick={() => setShowPhotoModal(false)}>
          <div className="bg-white w-full max-w-lg rounded-t-[3rem] p-8" onClick={e => e.stopPropagation()}>
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => getPhoto(CameraSource.Camera)} className="p-6 bg-gray-50 rounded-[2rem] font-black text-[#1A365D]">üì∏ {isRtl ? 'ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß' : 'Camera'}</button>
              <button onClick={() => getPhoto(CameraSource.Photos)} className="p-6 bg-gray-50 rounded-[2rem] font-black text-[#1A365D]">üñºÔ∏è {isRtl ? 'ÿßŸÑŸÖÿπÿ±ÿ∂' : 'Gallery'}</button>
            </div>
          </div>
        </div>
      )}

      <div className="w-full max-w-lg bg-white rounded-[3.5rem] shadow-2xl p-8 md:p-12 border border-white">
        <div className="text-center mb-6">
          <img src="/logo.jpg" alt="Logo" className="h-20 w-20 mx-auto rounded-full shadow-lg mb-3 border-4 border-[#E3DAD0]" />
          <h2 className="text-2xl font-black text-[#1A365D] tracking-tight">{isLogin ? t.login : t.signup}</h2>
        </div>

        <div className="flex bg-gray-100 p-1 rounded-[1.8rem] mb-6">
          <button type="button" onClick={() => setIsLogin(true)} className={`flex-1 py-3 rounded-[1.4rem] font-black transition-all ${isLogin ? 'bg-white text-[#1A365D] shadow-sm' : 'text-gray-400'}`}>{t.login}</button>
          <button type="button" onClick={() => setIsLogin(false)} className={`flex-1 py-3 rounded-[1.4rem] font-black transition-all ${!isLogin ? 'bg-white text-[#1A365D] shadow-sm' : 'text-gray-400'}`}>{t.signup}</button>
        </div>

        {!isLogin && (
          <div className="flex gap-2 p-1 bg-blue-50/50 rounded-2xl mb-6 items-center">
            <span className="text-[9px] font-black text-[#1A365D] px-2">{t.category}</span>
            <button type="button" onClick={() => setUserType('individuals')} className={`flex-1 py-2 rounded-xl text-[10px] font-black transition-all ${userType === 'individuals' ? 'bg-[#1A365D] text-white shadow-md' : 'text-gray-400'}`}>{t.individuals}</button>
            <button type="button" onClick={() => setUserType('companies')} className={`flex-1 py-2 rounded-xl text-[10px] font-black transition-all ${userType === 'companies' ? 'bg-[#1A365D] text-white shadow-md' : 'text-gray-400'}`}>{t.companies}</button>
          </div>
        )}

        <form onSubmit={handleAuth} className="space-y-4">
          {!isLogin && (
            <div className="flex flex-col items-center mb-4">
              <button type="button" onClick={() => setShowPhotoModal(true)} className="w-24 h-24 rounded-full bg-gray-100 border-4 border-white shadow-xl flex items-center justify-center overflow-hidden group relative">
                {profileImage ? <img src={profileImage} alt="Profile" className="w-full h-full object-cover" /> : <span className="text-3xl">üë§</span>}
                <div className="absolute inset-0 bg-black/20 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center text-white text-[8px] font-bold uppercase">{isRtl ? 'ÿ™ÿ∫ŸäŸäÿ±' : 'Edit'}</div>
              </button>
            </div>
          )}

          <div className="flex gap-3 justify-center mb-2">
            <button type="button" onClick={() => setLoginMethod('phone')} className={`px-5 py-1.5 rounded-full text-[10px] font-black ${loginMethod === 'phone' ? 'bg-[#1A365D] text-white' : 'bg-gray-100 text-gray-400'}`}>{isRtl ? 'ÿ®ÿßŸÑÿ¨ŸàÿßŸÑ' : 'Phone'}</button>
            <button type="button" onClick={() => setLoginMethod('email')} className={`px-5 py-1.5 rounded-full text-[10px] font-black ${loginMethod === 'email' ? 'bg-[#1A365D] text-white' : 'bg-gray-100 text-gray-400'}`}>{isRtl ? 'ÿ®ÿßŸÑÿ®ÿ±ŸäÿØ' : 'Email'}</button>
          </div>

          <div className="space-y-3">
            {!isLogin && userType === 'individuals' && (
               <div className="grid grid-cols-2 gap-3">
                 <input type="text" name="firstName" placeholder={t.firstName} onChange={handleInputChange} className="p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs border border-gray-100 outline-none focus:bg-white" />
                 <input type="text" name="lastName" placeholder={t.lastName} onChange={handleInputChange} className="p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs border border-gray-100 outline-none focus:bg-white" />
               </div>
            )}

            {!isLogin && userType === 'companies' && (
              <div className="space-y-3 animate-fade-in">
                <input type="text" name="companyName" placeholder={t.companyName} onChange={handleInputChange} className="w-full p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs border border-gray-100 outline-none focus:bg-white" />
                <select name="companyIndustry" onChange={handleInputChange} className="w-full p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs border border-gray-100 outline-none cursor-pointer">
                  <option value="">-- {t.industry} --</option>
                  {t.industries.map(ind => <option key={ind} value={ind}>{ind}</option>)}
                </select>
              </div>
            )}

            {loginMethod === 'phone' ? (
              <div className="flex items-center bg-gray-50 rounded-2xl p-1 border border-gray-100 focus-within:bg-white">
                <select className="bg-transparent font-bold text-xs px-2 outline-none">
                  {countryCodes.map(c => <option key={c.code} value={c.code}>{c.flag} {c.code}</option>)}
                </select>
                <input type="tel" name="phone" onChange={handleInputChange} placeholder={t.phone} className="flex-1 p-3 bg-transparent outline-none font-bold text-center text-sm" />
              </div>
            ) : (
              <input type="email" name="email" onChange={handleInputChange} placeholder={t.email} className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-center text-sm border border-gray-100 focus:bg-white" />
            )}

            <div className="relative group">
              <input type={showPass ? "text" : "password"} name="password" onChange={handleInputChange} placeholder={t.password} className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-center text-sm border border-gray-100 focus:bg-white" />
              <button type="button" onClick={() => setShowPass(!showPass)} className={`absolute ${isRtl ? 'left-4' : 'right-4'} top-1/2 -translate-y-1/2 opacity-40 hover:opacity-100`}>
                {showPass ? 'üîì' : 'üëÅÔ∏è'}
              </button>
            </div>

            {!isLogin && (
              <div className="relative group animate-fade-in">
                <input type={showConfirmPass ? "text" : "password"} name="confirmPassword" onChange={handleInputChange} placeholder={t.confirmPassword} className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-center text-sm border border-gray-100 focus:bg-white shadow-inner" />
                <button type="button" onClick={() => setShowConfirmPass(!showConfirmPass)} className={`absolute ${isRtl ? 'left-4' : 'right-4'} top-1/2 -translate-y-1/2 opacity-40 hover:opacity-100`}>
                  {showConfirmPass ? 'üîì' : 'üëÅÔ∏è'}
                </button>
              </div>
            )}

            {!isLogin && userType === 'individuals' && (
              <>
                <div className="grid grid-cols-2 gap-3">
                  <select name="gender" onChange={handleInputChange} className="p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs border border-gray-100 outline-none">
                    <option value="">{t.gender}</option><option value="male">{lang === 'ar' ? 'ÿ∞ŸÉÿ±' : (lang === 'fr' ? 'Masculin' : 'Male')}</option><option value="female">{lang === 'ar' ? 'ÿ£ŸÜÿ´Ÿâ' : (lang === 'fr' ? 'F√©minin' : 'Female')}</option>
                  </select>
                  <div className="relative flex items-center bg-gray-50 rounded-2xl border border-gray-100 px-2 group">
                    {/* ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑŸáŸäŸÜÿ™ ŸÅŸàŸÇ ÿßŸÑÿ≠ŸÇŸÑ */}
                    {!formData.dob && (
                      <span className={`absolute pointer-events-none text-[8px] font-bold text-gray-400 transition-all ${isRtl ? 'right-4' : 'left-4'} top-1/2 -translate-y-1/2`}>
                        {t.dobHint}
                      </span>
                    )}
                    <input type="date" name="dob" onChange={handleInputChange} className={`w-full p-4 bg-transparent outline-none font-bold text-[10px] text-center ${!formData.dob ? 'opacity-0' : 'opacity-100'}`} />
                  </div>
                </div>
                <div className="space-y-2">
                  <label className="flex items-center justify-between p-4 bg-blue-50/30 rounded-2xl border border-blue-100 cursor-pointer">
                    <span className="text-[11px] font-black text-[#1A365D]">{t.specialNeeds}</span>
                    <input type="checkbox" name="isSpecialNeeds" checked={formData.isSpecialNeeds} onChange={handleInputChange} className="w-5 h-5 accent-[#1A365D]" />
                  </label>
                  {formData.isSpecialNeeds && (
                    <select 
                      name="specialNeedsType" 
                      onChange={handleInputChange} 
                      className="w-full p-4 bg-white rounded-2xl border-2 border-blue-200 outline-none font-bold text-center text-xs animate-fade-in cursor-pointer appearance-none"
                    >
                      <option value="">-- {t.needsType} --</option>
                      {t.needsOptions.map(opt => <option key={opt} value={opt}>{opt}</option>)}
                    </select>
                  )}
                </div>
              </>
            )}
          </div>

          <div className="flex flex-col gap-2 py-1">
            <label className="flex items-center gap-3 cursor-pointer group">
              <input type="checkbox" checked={rememberMe} onChange={() => setRememberMe(!rememberMe)} className="w-4 h-4 accent-[#1A365D]" />
              <span className="text-[11px] font-bold text-gray-500">{t.remember}</span>
            </label>
            
            {!isLogin && (
              <div className="flex items-center gap-3 text-[10px] font-bold text-gray-500">
                <input type="checkbox" checked={agreed} onChange={(e) => setAgreed(e.target.checked)} className="w-4 h-4 accent-[#1A365D]" />
                <p>
                  {t.agreePart1}
                  <button 
                    type="button"
                    onClick={(e) => { e.preventDefault(); e.stopPropagation(); navigate('/policy'); }} 
                    className="text-blue-600 underline underline-offset-4 font-black"
                  >
                    {t.privacy}
                  </button>
                </p>
              </div>
            )}
          </div>

          {error && <div className="p-3 bg-red-50 text-red-600 rounded-xl text-[10px] font-bold text-center border border-red-100">{error}</div>}
          <button type="submit" disabled={loading} className="w-full py-5 rounded-[2.2rem] bg-[#1A365D] text-white font-black shadow-[0_10px_20px_rgba(26,54,93,0.3)] active:scale-95 transition-all text-xl mt-2">
            {loading ? t.loading : (isLogin ? t.login : t.signup)}
          </button>
        </form>
      </div>
    </div>
  );
};
