import React from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export const LanguagePage = () => {
  const navigate = useNavigate();
  const { setLanguage } = useAuth();

  const selectLanguage = (lang) => {
    setLanguage(lang);
    navigate("/");
  };

  return (
    <div className="relative min-h-screen bg-secondary flex items-center justify-center overflow-hidden">

      {/* ===== الخلفية العلوية الشفافة ===== */}
      <div className="absolute top-0 left-0 w-full h-1/2 bg-primary opacity-10 rounded-b-[6rem]" />

      {/* ===== الكارد الرئيسي ===== */}
      <div className="relative z-10 w-[90%] max-w-md bg-white rounded-[3rem] shadow-2xl p-8 text-center">

        {/* اللوجو */}
        <img
          src="/logo.png"
          alt="Careerak Logo"
          className="w-28 mx-auto mb-6"
        />

        {/* العنوان */}
        <h1 className="text-primary text-xl font-bold mb-8">
          Choose Language / اختر اللغة
        </h1>

        {/* الأزرار */}
        <div className="space-y-4">

          <button
            onClick={() => selectLanguage("ar")}
            className="w-full py-4 rounded-full bg-primary text-white font-bold text-lg active:scale-95 transition"
          >
            العربية
          </button>

          <button
            onClick={() => selectLanguage("en")}
            className="w-full py-4 rounded-full bg-primary text-white font-bold text-lg active:scale-95 transition"
          >
            English
          </button>

          <button
            onClick={() => selectLanguage("fr")}
            className="w-full py-4 rounded-full bg-primary text-white font-bold text-lg active:scale-95 transition"
          >
            Français
          </button>

        </div>
      </div>
    </div>
  );
};
