import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";

export default function LanguagePage() {
  const navigate = useNavigate();
  const [showConfirm, setShowConfirm] = useState(false);
  const [selectedLang, setSelectedLang] = useState(null);

  const texts = {
    ar: {
      title: "اختر اللغة",
      confirmTitle: "تأكيد اختيار اللغة",
      confirmText:
        "إن اللغة التي تم اختيارها هي اللغة التي سيتم عرضها في التطبيق، ويمكن للمستخدم تغيير اللغة في أي وقت من خلال لوحة التحكم.",
      confirm: "تأكيد",
      cancel: "إلغاء",
    },
    en: {
      title: "Choose Language",
      confirmTitle: "Confirm Language",
      confirmText:
        "The selected language will be used throughout the app. You can change it anytime from settings.",
      confirm: "Confirm",
      cancel: "Cancel",
    },
    fr: {
      title: "Choisir la langue",
      confirmTitle: "Confirmation de la langue",
      confirmText:
        "La langue sélectionnée sera utilisée dans toute l'application. Vous pouvez la modifier à tout moment depuis les paramètres.",
      confirm: "Confirmer",
      cancel: "Annuler",
    },
  };

  const current = selectedLang ? texts[selectedLang] : texts.ar;

  const selectLanguage = (lang) => {
    setSelectedLang(lang);
    setShowConfirm(true);
  };

  const confirmLanguage = () => {
    localStorage.setItem("language", selectedLang);
    navigate("/auth");
  };

  return (
    <div className="relative min-h-screen bg-[#E3DAD0] flex flex-col items-center justify-center gap-6 overflow-hidden">
      {/* الشفافية الكحلية */}
      <div className="absolute inset-0 bg-[#1A365D]/40 animate-fadeSlow z-0"></div>

      {/* اللوجو */}
      <div className="relative z-10 w-48 h-48 rounded-full border-[4px] border-[#1A365D] overflow-hidden shadow-xl">
        <img
          src={logo}
          alt="Careerak Logo"
          className="w-full h-full object-cover scale-110"
        />
      </div>

      {/* العناوين */}
      <div className="relative z-10 text-center text-[#1A365D] font-bold">
        <div className="text-lg">{texts.ar.title} / Choose Language</div>
        <div className="text-base font-medium">{texts.fr.title}</div>
      </div>

      {/* الأزرار */}
      <div className="relative z-10 flex flex-col gap-4 w-72">
        <button className="lang-btn" onClick={() => selectLanguage("ar")}>
          العربية
        </button>
        <button className="lang-btn" onClick={() => selectLanguage("en")}>
          English
        </button>
        <button className="lang-btn" onClick={() => selectLanguage("fr")}>
          Français
        </button>
      </div>

      {/* نافذة التأكيد */}
      {showConfirm && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
          <div className="bg-white rounded-3xl p-6 w-[90%] max-w-sm text-center">
            <h2 className="text-[#1A365D] font-bold mb-3">
              {current.confirmTitle}
            </h2>
            <p className="text-gray-600 text-sm mb-5">
              {current.confirmText}
            </p>
            <div className="flex gap-3">
              <button
                onClick={confirmLanguage}
                className="flex-1 bg-[#1A365D] text-white py-2 rounded-full"
              >
                {current.confirm}
              </button>
              <button
                onClick={() => setShowConfirm(false)}
                className="flex-1 border border-[#1A365D] text-[#1A365D] py-2 rounded-full"
              >
                {current.cancel}
              </button>
            </div>
          </div>
        </div>
      )}

      {/* ستايل الأزرار */}
      <style>
        {`
          .lang-btn {
            background: #1A365D;
            color: white;
            padding: 14px;
            border-radius: 999px;
            font-size: 18px;
            font-weight: bold;
          }

          @keyframes fadeSlow {
            0% { opacity: 0; }
            20% { opacity: 0.4; }
            80% { opacity: 0.4; }
            100% { opacity: 0; }
          }

          .animate-fadeSlow {
            animation: fadeSlow 10s ease-in-out infinite;
          }
        `}
      </style>
    </div>
  );
}
