import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../assets/logo.png";

export default function LanguagePage() {
  const navigate = useNavigate();

  const [selectedLang, setSelectedLang] = useState(null);
  const [showConfirm, setShowConfirm] = useState(false);

  // عند الضغط على زر لغة
  const onSelectLanguage = (lang) => {
    setSelectedLang(lang);
    setShowConfirm(true);
  };

  // عند تأكيد الاختيار
  const confirmLanguage = () => {
    localStorage.setItem("language", selectedLang);
    setShowConfirm(false);
    navigate("/auth");
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-secondary text-primary px-4">

      {/* اللوجو */}
      <div className="mb-6">
        <div className="w-44 h-44 rounded-full border-2 border-primary overflow-hidden flex items-center justify-center shadow-lg">
          <img
            src={logo}
            alt="Careerak Logo"
            className="w-[120%] h-[120%] object-contain scale-110"
          />
        </div>
      </div>

      {/* العنوان */}
      <h1 className="text-xl font-bold mb-1">
        Choose Language / اختر اللغة
      </h1>
      <p className="text-lg mb-6">
        Choisir la langue
      </p>

      {/* الأزرار */}
      <div className="w-full max-w-sm space-y-4">
        <button
          onClick={() => onSelectLanguage("ar")}
          className="w-full py-4 rounded-full bg-primary text-white text-lg font-bold"
        >
          العربية
        </button>

        <button
          onClick={() => onSelectLanguage("en")}
          className="w-full py-4 rounded-full bg-primary text-white text-lg font-bold"
        >
          English
        </button>

        <button
          onClick={() => onSelectLanguage("fr")}
          className="w-full py-4 rounded-full bg-primary text-white text-lg font-bold"
        >
          Français
        </button>
      </div>

      {/* نافذة التأكيد */}
      {showConfirm && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-6 w-full max-w-sm text-center">

            <h2 className="text-lg font-bold mb-3 text-primary">
              تأكيد اختيار اللغة
            </h2>

            <p className="text-sm text-gray-600 mb-5 leading-relaxed">
              إن اللغة التي تم اختيارها هي اللغة التي سيتم عرضها في التطبيق.
              <br />
              يمكن للمستخدم تغيير اللغة في أي وقت من خلال لوحة التحكم.
            </p>

            <div className="flex gap-3">
              <button
                className="flex-1 py-2 rounded-xl border border-gray-300"
                onClick={() => setShowConfirm(false)}
              >
                إلغاء
              </button>

              <button
                className="flex-1 py-2 rounded-xl bg-primary text-white"
                onClick={confirmLanguage}
              >
                تأكيد
              </button>
            </div>

          </div>
        </div>
      )}
    </div>
  );
}
