import axios from 'axios';
import { Preferences } from '@capacitor/preferences';
import CryptoJS from 'crypto-js';

const SECRET_KEY = 'careerak_secure_key_2024';

// العناوين المتاحة
const LOCAL_SERVER = 'http://192.168.1.5:5000/api';
const TUNNEL_BRIDGE = 'https://careerak-server.loca.lt/api';

const api = axios.create({
  baseURL: LOCAL_SERVER,
  timeout: 15000,
  headers: {
    'Content-Type': 'application/json',
    'Bypass-Tunnel-Reminder': 'true'
  }
});

// اكتشاف السيرفر
export const discoverBestServer = async () => {
  const servers = [LOCAL_SERVER, TUNNEL_BRIDGE];
  for (const url of servers) {
    try {
      await axios.get(`${url}/users/health-check`, { timeout: 3000 });
      api.defaults.baseURL = url;
      return url;
    } catch (e) {
      continue;
    }
  }
  return api.defaults.baseURL;
};

// التوكن
api.interceptors.request.use(async (config) => {
  try {
    const { value: encryptedToken } = await Preferences.get({ key: 'auth_token' });
    if (encryptedToken) {
      const bytes = CryptoJS.AES.decrypt(encryptedToken, SECRET_KEY);
      const token = bytes.toString(CryptoJS.enc.Utf8);
      if (token) {
        config.headers.Authorization = `Bearer ${token}`;
      }
    }
  } catch (error) {
    console.error('API Interceptor Error:', error);
  }
  return config;
}, (error) => Promise.reject(error));

// الخدمات
export const userService = {
  register: (userData) => api.post('/users/register', userData),
  login: (credentials) => api.post('/users/login', credentials),
  getProfile: () => api.get('/users/profile'),
  updateProfile: (data) => api.put('/users/profile', data),
  healthCheck: () => api.get('/users/health-check'),
  analyzeImage: (data) => api.post('/ai/analyze-image', data),
  parseCV: (data) => api.post('/ai/parse-cv', data),
  parseSocial: (data) => api.post('/ai/parse-social', data)
};

export const jobPostingService = {
  getAll: () => api.get('/job-postings'),
  getById: (id) => api.get(`/job-postings/${id}`),
  create: (data) => api.post('/job-postings', data),
};

export const educationalCourseService = {
  getAll: () => api.get('/educational-courses'),
  getById: (id) => api.get(`/educational-courses/${id}`),
};

export const trainingCourseService = {
  getAll: () => api.get('/training-courses'),
  getById: (id) => api.get(`/training-courses/${id}`),
};

export default api;
