import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export const OTPVerification = () => {
  const navigate = useNavigate();
  const [code, setCode] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => { setIsVisible(true); }, []);

  const handleVerify = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const res = await api.post('/users/verify-otp', { otp: code });
      localStorage.setItem('user', JSON.stringify(res.data.user));
      
      const user = res.data.user;
      // ÿ™Ÿàÿ¨ŸäŸá ÿ∞ŸÉŸä ÿ≠ÿ≥ÿ® ŸÜŸàÿπ ÿßŸÑÿ≠ÿ≥ÿßÿ® (HR ÿ£Ÿà Employee)
      if (user.role === 'HR') navigate('/03_OnboardingCompanies');
      else navigate('/03_OnboardingIndividuals');
    } catch (err) {
      setError(err.response?.data?.error || 'ÿ±ŸÖÿ≤ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿå Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ¨ÿØÿØÿßŸã');
    } finally {
      setLoading(false);
    }
  };

  const PRIMARY_NAVY = '#1A365D';
  const SOFT_BEIGE = '#E3DAD0';

  return (
    <div 
      className={`min-h-screen flex flex-col items-center justify-center p-6 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} 
      style={{ backgroundColor: SOFT_BEIGE }}
    >
      <div className={`w-full max-w-md bg-white rounded-[3.5rem] shadow-[0_50px_100px_-20px_rgba(26,54,93,0.2)] p-10 md:p-14 text-center transition-all duration-1000 transform ${isVisible ? 'translate-y-0' : 'translate-y-10'}`}>
        
        <div className="mb-10">
          <div className="w-24 h-24 bg-[#1A365D]/5 text-[#1A365D] rounded-full flex items-center justify-center mx-auto mb-6 shadow-inner relative group">
            <div className="absolute inset-0 bg-[#1A365D] opacity-0 group-hover:opacity-5 rounded-full transition-all duration-500 scale-150"></div>
            <span className="text-4xl">üîê</span>
          </div>
          <h2 className="text-3xl font-black text-[#1A365D] mb-3">ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ±ŸÖÿ≤</h2>
          <p className="text-[#1A365D]/40 font-bold text-sm tracking-wide">ÿ£ÿØÿÆŸÑ ÿßŸÑÿ±ŸÖÿ≤ ÿßŸÑŸÖŸÉŸàŸÜ ŸÖŸÜ 4 ÿ£ÿ±ŸÇÿßŸÖ</p>
        </div>

        <form onSubmit={handleVerify} className="space-y-8">
          <div className="relative">
            <input 
              type="text" 
              maxLength="4"
              placeholder="0 0 0 0" 
              value={code}
              onChange={(e) => setCode(e.target.value)}
              className="w-full p-6 bg-gray-50 rounded-[2.5rem] border-2 border-transparent focus:border-[#1A365D]/10 focus:bg-white outline-none font-black text-4xl text-center tracking-[1.2rem] shadow-inner text-[#1A365D] placeholder-gray-200 transition-all duration-300"
            />
          </div>
          
          {error && (
            <div className="p-4 bg-red-50 text-red-600 rounded-[1.5rem] text-[11px] font-black border border-red-100 animate-shake">
              {error}
            </div>
          )}

          <button 
            type="submit" 
            disabled={loading || code.length < 4}
            className={`w-full py-6 rounded-[2.5rem] font-black shadow-2xl transition-all text-xl flex items-center justify-center gap-3 active:scale-95 ${
              loading || code.length < 4 
              ? 'bg-gray-100 text-gray-400 cursor-not-allowed shadow-none' 
              : 'bg-[#1A365D] text-white hover:bg-[#152c4d] hover:-translate-y-1'
            }`}
          >
            {loading ? (
              <div className="w-6 h-6 border-4 border-white/30 border-t-white rounded-full animate-spin"></div>
            ) : (
              'ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ©'
            )}
          </button>
        </form>

        <div className="mt-12">
          <button className="text-[#1A365D]/50 font-black text-xs hover:text-[#1A365D] transition-colors">
            ŸÑŸÖ ŸäÿµŸÑŸÉ ÿßŸÑÿ±ŸÖÿ≤ÿü <span className="underline underline-offset-4">ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ•ÿ±ÿ≥ÿßŸÑ</span>
          </button>
        </div>
      </div>
    </div>
  );
};
