import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export const OTPVerification = () => {
  const navigate = useNavigate();
  const [code, setCode] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleVerify = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const res = await api.post('/users/verify-otp', { otp: code });
      localStorage.setItem('user', JSON.stringify(res.data.user));
      
      const user = res.data.user;
      if (user.role === 'HR') navigate('/onboarding-companies');
      else navigate('/onboarding-individuals');
    } catch (err) {
      setError(err.response?.data?.error || 'ÿ±ŸÖÿ≤ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-6 bg-[#E3DAD0]">
      <div className="w-full max-w-md bg-white rounded-[3rem] shadow-2xl p-10 text-center">
        <div className="mb-8">
          <div className="w-20 h-20 bg-blue-600 text-white rounded-full flex items-center justify-center mx-auto text-3xl mb-4 shadow-xl">üì±</div>
          <h2 className="text-2xl font-black text-[#1A365D]">ÿ™ÿ£ŸÉŸäÿØ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ</h2>
          <p className="text-gray-500 font-bold mt-2">ÿ£ÿØÿÆŸÑ ÿ±ŸÖÿ≤ ÿßŸÑÿ™ÿ≠ŸÇŸÇ ÿßŸÑŸÖÿ±ÿ≥ŸÑ ŸÑŸáÿßÿ™ŸÅŸÉ</p>
        </div>

        <form onSubmit={handleVerify} className="space-y-6">
          <input 
            type="text" 
            maxLength="4"
            placeholder="0 0 0 0" 
            value={code}
            onChange={(e) => setCode(e.target.value)}
            className="w-full p-5 bg-gray-50 rounded-2xl outline-none font-black text-3xl text-center tracking-[1rem] shadow-inner"
          />
          
          {error && <p className="text-red-500 font-bold text-xs">{error}</p>}

          <button 
            type="submit" 
            disabled={loading}
            className={`w-full py-6 rounded-[2rem] font-black shadow-xl transition-all text-xl ${loading ? 'bg-gray-300' : 'bg-[#1A365D] text-white'}`}
          >
            {loading ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÇŸÇ...' : 'ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ±ŸÖÿ≤'}
          </button>
        </form>
      </div>
    </div>
  );
};
