import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { userService } from '../services/api';
import { Preferences } from '@capacitor/preferences';
import { App } from '@capacitor/app';
import { useAuth } from '../context/AuthContext';
import { FloatingWhatsApp } from '../components/FloatingWhatsApp';
import CryptoJS from 'crypto-js';

const SECRET_KEY = 'careerak_secure_key_2024';

const loginTranslations = {
  ar: {
    subtitle: "تسجيل الدخول",
    userPlaceholder: "البريد الإلكتروني أو اسم المستخدم",
    passPlaceholder: "كلمة المرور",
    loginBtn: "دخول",
    noAccount: "ليس لديك حساب؟",
    createAccount: "أنشئ حساباً الآن"
  },
  en: {
    subtitle: "Login",
    userPlaceholder: "Email or Username",
    passPlaceholder: "Password",
    loginBtn: "Login",
    noAccount: "Don't have an account?",
    createAccount: "Create account now"
  },
  fr: {
    subtitle: "Connexion",
    userPlaceholder: "Email ou nom d'utilisateur",
    passPlaceholder: "Mot de passe",
    loginBtn: "Entrer",
    noAccount: "Pas de compte ?",
    createAccount: "Créer un compte"
  }
};

export default function LoginPage() {
  const navigate = useNavigate();
  const { language, audioEnabled } = useAuth();
  const t = loginTranslations[language] || loginTranslations.ar;
  const isRTL = language === 'ar';

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [isVisible, setIsVisible] = useState(false);
  
  const audioRef = useRef(null);

  useEffect(() => {
    setIsVisible(true);
    
    // إدارة الموسيقى المستمرة Music.mp3 حسب الشروط الصارمة
    if (audioEnabled) {
      if (!audioRef.current) {
        audioRef.current = new Audio('/Music.mp3');
        audioRef.current.loop = true;
        audioRef.current.volume = 0.5;
      }
      audioRef.current.play().catch(() => {});

      // التوقف عند (Home, Lock, Background) والاستئناف عند العودة
      const handleAppState = (state) => {
        if (!audioRef.current) return;
        state.isActive ? audioRef.current.play().catch(() => {}) : audioRef.current.pause();
      };

      const listener = App.addListener('appStateChange', handleAppState);
      return () => {
        listener.then(l => l.remove());
        // لا نقوم بإيقاف الموسيقى عند مغادرة الصفحة فقط، بل تظل تعمل
      };
    }
  }, [audioEnabled]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    try {
      const response = await userService.login({ email, password });
      const encryptedToken = CryptoJS.AES.encrypt(response.data.token, SECRET_KEY).toString();
      await Preferences.set({ key: 'auth_token', value: encryptedToken });
      localStorage.setItem('user', JSON.stringify(response.data.user));
      
      const role = response.data.user.role?.toLowerCase();
      if (role === 'admin') navigate('/admin-dashboard');
      else if (role === 'hr') navigate('/hr-dashboard');
      else navigate('/profile');
    } catch (err) {
      setError(isRTL ? "فشل الدخول، تحقق من البيانات" : "Login Failed");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`min-h-screen flex items-center justify-center bg-[#E3DAD0] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={isRTL ? 'rtl' : 'ltr'}>
      <div className="w-full max-w-sm bg-white rounded-[3.5rem] shadow-2xl p-10 mx-4 relative overflow-hidden">
        
        <div className="flex flex-col items-center mb-8">
          <div className="w-24 h-24 rounded-full border-4 border-[#1A365D] p-1 mb-4 shadow-xl overflow-hidden">
             <img src="/logo.jpg" alt="Logo" className="w-full h-full object-cover rounded-full" />
          </div>
          <h1 className="text-3xl font-black text-[#1A365D] italic">Careerak</h1>
          <p className="text-gray-400 font-bold text-sm mt-1">{t.subtitle}</p>
        </div>

        {error && <div className="bg-red-50 text-red-600 p-3 rounded-xl text-xs font-bold mb-4 text-center border border-red-100">{error}</div>}

        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="relative">
            <input
              type="text"
              placeholder={t.userPlaceholder}
              className="w-full p-5 bg-gray-50 rounded-2xl border-2 border-gray-100 focus:border-[#1A365D]/20 outline-none font-bold text-center transition-all"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="relative">
            <input
              type="password"
              placeholder={t.passPlaceholder}
              className="w-full p-5 bg-gray-50 rounded-2xl border-2 border-gray-100 focus:border-[#1A365D]/20 outline-none font-bold text-center transition-all"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="w-full bg-[#1A365D] text-white p-6 rounded-[2rem] font-black text-xl shadow-xl active:scale-95 transition-all mt-4"
          >
            {loading ? <div className="w-6 h-6 border-4 border-white/30 border-t-white rounded-full animate-spin mx-auto"></div> : t.loginBtn}
          </button>
        </form>

        <div className="mt-8 text-center">
          <p className="text-xs font-bold text-gray-400">
            {t.noAccount} <span onClick={() => navigate('/auth')} className="text-[#1A365D] cursor-pointer hover:underline">{t.createAccount}</span>
          </p>
        </div>
      </div>

      {/* استعادة أيقونة الواتساب المعتمدة مع الوميض */}
      <FloatingWhatsApp />
    </div>
  );
}
