import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation, useNavigate, Link } from 'react-router-dom';
import { StatusBar, Style } from '@capacitor/status-bar';
import { App as CapApp } from '@capacitor/app';
import { AuthProvider, useAuth } from './context/AuthContext';
import { WelcomePage } from './pages/01_WelcomePage';
import { AuthPage } from './pages/03_AuthPage';
import { OnboardingIndividuals } from './pages/03_OnboardingIndividuals';
import { OnboardingCompanies } from './pages/03_OnboardingCompanies';
import { OTPVerification } from './pages/03_OTPVerification';
import { SplashScreen } from './components/SplashScreen';
import { FloatingWhatsApp } from './components/FloatingWhatsApp';
import { Navbar } from './components/Navbar';
import { SettingsPage } from './pages/08_SettingsPage';
import { PolicyPage } from './pages/07_PolicyPage';
import api from './services/api';
import './index.css';

const BottomNavbar = ({ user, lang }) => {
  const isArabic = lang === 'ar';
  const isFrench = lang === 'fr';
  const location = useLocation();
  const guestPaths = ['/', '/auth', '/policy', '/verify-otp'];
  if (guestPaths.includes(location.pathname)) return null;

  const speak = (text) => {
    const u = new SpeechSynthesisUtterance(text);
    u.lang = isArabic ? "ar-SA" : (isFrench ? "fr-FR" : "en-US");
    window.speechSynthesis.speak(u);
  };

  const getNavItems = () => {
    const common = [
      { label: isArabic ? 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™' : (isFrench ? 'Param√®tres' : 'Settings'), path: '/settings', icon: '‚öôÔ∏è' },
      { label: isArabic ? 'ÿ≠ÿ≥ÿßÿ®Ÿä' : (isFrench ? 'Profil' : 'Profile'), path: '/profile', icon: 'üë§' },
    ];
    if (user?.role === 'Admin') {
      return [
        { label: isArabic ? 'ÿßŸÑÿ®ÿ±ÿ¨' : 'Tower', path: '/dashboard', icon: 'üõ∞Ô∏è' },
        { label: isArabic ? 'ÿßŸÑÿ£ÿπÿ∂ÿßÿ°' : 'Users', path: '/admin/users', icon: 'üë•' },
        { label: isArabic ? 'ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™' : 'Stats', path: '/admin/stats', icon: 'üìä', special: true },
        ...common
      ];
    }
    if (user?.role === 'HR') {
      return [
        { label: isArabic ? 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©' : 'Home', path: '/dashboard', icon: 'üè¢' },
        { label: isArabic ? 'ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™' : 'Inbox', path: '/applications', icon: 'üì•' },
        { label: isArabic ? 'ÿ•ÿ∂ÿßŸÅÿ©' : 'Add', path: '/post-job', icon: '‚ûï', special: true },
        ...common
      ];
    }
    return [
      { label: isArabic ? 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©' : 'Home', path: '/dashboard', icon: 'üè†' },
      { label: isArabic ? 'ÿßŸÑÿÆÿØŸÖÿßÿ™' : 'Services', path: '/services', icon: 'üõ†Ô∏è' },
      { label: isArabic ? 'ÿ∑ŸÑÿ®' : 'Request', path: '/apply-job', icon: 'üîç', special: true },
      ...common
    ];
  };

  return (
    <div className="fixed bottom-0 left-0 right-0 z-[1000] px-4 pb-6 pt-2 bg-gradient-to-t from-[#E3DAD0] to-transparent pointer-events-none">
      <div className="max-w-md mx-auto bg-white/90 backdrop-blur-xl rounded-[2.5rem] shadow-[0_20px_50px_rgba(26,54,93,0.2)] border border-white/50 flex justify-between items-center p-2 pointer-events-auto">
        {getNavItems().map((item, i) => (
          <Link key={i} to={item.path} onContextMenu={(e) => { e.preventDefault(); speak(item.label); }}
            className={`flex flex-col items-center justify-center transition-all active:scale-90 ${item.special ? 'bg-[#1A365D] text-white w-14 h-14 rounded-full shadow-lg -mt-8' : 'flex-1 text-gray-400'}`}>
            <span className={`${item.special ? 'text-2xl' : 'text-xl'}`}>{item.icon}</span>
            {!item.special && <span className="text-[8px] font-black mt-1 uppercase tracking-tighter text-center">{item.label}</span>}
          </Link>
        ))}
      </div>
    </div>
  );
};

function AppContent() {
  const { isAuthenticated, user, loading: authLoading, language, changeLanguage } = useAuth();
  const [showSplash, setShowSplash] = useState(true);
  const [showLangSelect, setShowLangSelect] = useState(!localStorage.getItem('lang')); 
  const location = useLocation();
  const bgMusic = useRef(null);

  // üéµ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖŸàÿ≥ŸäŸÇŸâ Ÿàÿ≠ÿßŸÑÿ© ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ (ÿ•ŸäŸÇÿßŸÅ ÿπŸÜÿØ ŸÇŸÅŸÑ ÿßŸÑÿ¥ÿßÿ¥ÿ©)
  useEffect(() => {
    if (!showSplash) {
      if (!bgMusic.current) {
        bgMusic.current = new Audio('/Music.mp3');
        bgMusic.current.loop = true;
        bgMusic.current.volume = 0.4;
      }
      const playMusic = () => { bgMusic.current.play().catch(() => {}); };
      playMusic();
      window.addEventListener('click', playMusic, { once: true });

      // üõë ÿßŸÑÿ≥Ÿäÿ∑ÿ±ÿ© ÿπŸÑŸâ ÿßŸÑŸÖŸàÿ≥ŸäŸÇŸâ ÿπŸÜÿØ ÿßŸÑÿÆÿ±Ÿàÿ¨ ÿ£Ÿà ŸÇŸÅŸÑ ÿßŸÑÿ¥ÿßÿ¥ÿ©
      const stateListener = CapApp.addListener('appStateChange', ({ isActive }) => {
        if (!bgMusic.current) return;
        if (isActive) {
          bgMusic.current.play().catch(() => {});
        } else {
          bgMusic.current.pause();
        }
      });

      return () => {
        stateListener.then(l => l.remove());
        if (bgMusic.current) bgMusic.current.pause();
      };
    }
  }, [showSplash]);

  useEffect(() => {
    const setupApp = async () => {
      try { await StatusBar.setBackgroundColor({ color: '#E3DAD0' }); await StatusBar.setStyle({ style: Style.Light }); } catch (e) {}
      CapApp.addListener('backButton', () => {
        if (['/', '/dashboard', '/auth'].includes(location.pathname) || showLangSelect) {
          CapApp.exitApp();
        } else { window.history.back(); }
      });
    };
    setupApp();
    return () => { CapApp.removeAllListeners(); };
  }, [location, showLangSelect]);

  if (showSplash || authLoading) return <SplashScreen onFinish={() => setShowSplash(false)} />;
  if (showLangSelect) return <LanguageSelect onSelect={(l) => { changeLanguage(l); setShowLangSelect(false); }} />;

  return (
    <div className="flex flex-col min-h-screen w-full bg-[#E3DAD0] font-arabic overflow-x-hidden" dir={language === 'ar' ? 'rtl' : 'ltr'}>
      {isAuthenticated && <Navbar lang={language} user={user} />}
      <FloatingWhatsApp />
      <main className={`flex-grow w-full max-w-screen-xl mx-auto px-6 ${isAuthenticated ? 'pb-32 pt-4' : 'py-4'}`}>
        <Routes>
          <Route path="/" element={<WelcomePage lang={language} />} />
          <Route path="/auth" element={<AuthPage lang={language} />} />
          <Route path="/policy" element={<PolicyPage lang={language} />} />
          <Route path="/verify-otp" element={<OTPVerification />} />
          <Route path="/onboarding-individuals" element={<OnboardingIndividuals lang={language} />} />
          <Route path="/onboarding-companies" element={<OnboardingCompanies lang={language} />} />
          <Route path="/settings" element={<SettingsPage lang={language} />} />
          {isAuthenticated ? (
            <>
              <Route path="/dashboard" element={<DashboardSwitcher user={user} />} />
              {user?.role === 'Admin' && (
                <>
                  <Route path="/preview/employee" element={<EmployeeDashboard user={{firstName: 'ŸÖÿπÿßŸäŸÜÿ©'}} />} />
                  <Route path="/preview/hr" element={<HRDashboard user={{companyName: 'ŸÖÿπÿßŸäŸÜÿ©'}} />} />
                  <Route path="/preview/special" element={<SimplifiedDashboard user={{firstName: 'ŸÖÿπÿßŸäŸÜÿ©'}} />} />
                </>
              )}
              <Route path="*" element={<Navigate to="/dashboard" />} />
            </>
          ) : (
            <Route path="*" element={<Navigate to="/" />} />
          )}
        </Routes>
      </main>
      {isAuthenticated && <BottomNavbar user={user} lang={language} />}
    </div>
  );
}

