import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

export default function ProfilePage() {
  const { user } = useAuth();
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => { setIsVisible(true); }, []);

  return (
    <div className={`min-h-screen bg-[#E3DAD0] p-6 transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
      <div className="max-w-md mx-auto bg-white rounded-[3rem] shadow-2xl p-10 text-center">
        <div className="w-32 h-32 rounded-full border-4 border-[#1A365D] mx-auto mb-6 overflow-hidden shadow-lg">
          <img src={user?.profileImage || "/logo.jpg"} alt="Profile" className="w-full h-full object-cover" />
        </div>
        <h2 className="text-2xl font-black text-[#1A365D] mb-2">{user?.firstName} {user?.lastName}</h2>
        <p className="text-gray-400 font-bold">{user?.role}</p>
        
        <div className="mt-10 space-y-4">
          <div className="p-4 bg-gray-50 rounded-2xl border border-gray-100 text-right">
            <p className="text-xs text-gray-400 font-bold mb-1">البريد الإلكتروني</p>
            <p className="text-[#1A365D] font-black">{user?.email}</p>
          </div>
        </div>
      </div>
    </div>
  );
}
