import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import { userService } from '../services/api';
import { useAuth } from '../context/AuthContext';

const countryCodes = [
  { code: '+970', flag: 'üáµüá∏', name: 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ' },
  { code: '+962', flag: 'üáØüá¥', name: 'ÿßŸÑÿ£ÿ±ÿØŸÜ' },
  { code: '+963', flag: 'üá∏üáæ', name: 'ÿ≥Ÿàÿ±Ÿäÿß' },
  { code: '+961', flag: 'üá±üáß', name: 'ŸÑÿ®ŸÜÿßŸÜ' },
  { code: '+964', flag: 'üáÆüá∂', name: 'ÿßŸÑÿπÿ±ÿßŸÇ' },
  { code: '+966', flag: 'üá∏üá¶', name: 'ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©' },
  { code: '+971', flag: 'üá¶üá™', name: 'ÿßŸÑÿ•ŸÖÿßÿ±ÿßÿ™' },
  { code: '+965', flag: 'üá∞üáº', name: 'ÿßŸÑŸÉŸàŸäÿ™' },
  { code: '+974', flag: 'üá∂üá¶', name: 'ŸÇÿ∑ÿ±' },
  { code: '+973', flag: 'üáßüá≠', name: 'ÿßŸÑÿ®ÿ≠ÿ±ŸäŸÜ' },
  { code: '+968', flag: 'üá¥üá≤', name: 'ÿπŸèŸÖÿßŸÜ' },
  { code: '+967', flag: 'üáæüá™', name: 'ÿßŸÑŸäŸÖŸÜ' },
  { code: '+20', flag: 'üá™üá¨', name: 'ŸÖÿµÿ±' },
  { code: '+218', flag: 'üá±üáæ', name: 'ŸÑŸäÿ®Ÿäÿß' },
  { code: '+216', flag: 'üáπüá≥', name: 'ÿ™ŸàŸÜÿ≥' },
  { code: '+213', flag: 'üá©üáø', name: 'ÿßŸÑÿ¨ÿ≤ÿßÿ¶ÿ±' },
  { code: '+212', flag: 'üá≤üá¶', name: 'ÿßŸÑŸÖÿ∫ÿ±ÿ®' },
  { code: '+222', flag: 'üá≤üá∑', name: 'ŸÖŸàÿ±Ÿäÿ™ÿßŸÜŸäÿß' },
  { code: '+249', flag: 'üá∏üá©', name: 'ÿßŸÑÿ≥ŸàÿØÿßŸÜ' },
  { code: '+252', flag: 'üá∏üá¥', name: 'ÿßŸÑÿµŸàŸÖÿßŸÑ' },
  { code: '+253', flag: 'üá©üáØ', name: 'ÿ¨Ÿäÿ®Ÿàÿ™Ÿä' },
  { code: '+269', flag: 'üá∞üá≤', name: 'ÿ¨ÿ≤ÿ± ÿßŸÑŸÇŸÖÿ±' },
];

const translations = {
  ar: {
    login: "ÿØÿÆŸàŸÑ", signup: "ÿ≠ÿ≥ÿßÿ® ÿ¨ÿØŸäÿØ", email: "ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä", phone: "ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ",
    password: "ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±", individuals: "ÿ£ŸÅÿ±ÿßÿØ", companies: "ÿ¥ÿ±ŸÉÿßÿ™",
    category: "ÿßŸÑŸÅÿ¶ÿ©", loginMethod: "ÿ®ÿßŸÑÿ®ÿ±ŸäÿØ", phoneMethod: "ÿ®ÿßŸÑÿ¨ŸàÿßŸÑ",
    specialNeeds: "ÿ£ŸÜÿß ŸÖŸÜ ÿ∞ŸàŸä ÿßŸÑŸáŸÖŸÖ ü¶æ", country: "ÿßŸÑÿ®ŸÑÿØ",
    mustAgree: "Ÿäÿ¨ÿ® ÿßŸÑŸÖŸàÿßŸÅŸÇÿ© ŸÑŸÑŸÖÿ™ÿßÿ®ÿπÿ©", loading: "ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ..."
  }
};

const education_ar = ["ÿØŸÉÿ™Ÿàÿ±ÿßŸá", "ŸÖÿßÿ¨ÿ≥ÿ™Ÿäÿ±", "ÿ®ŸÉÿßŸÑŸàÿ±ŸäŸàÿ≥", "ÿ´ÿßŸÜŸàŸäÿ©", "ŸÖÿ™Ÿàÿ≥ÿ∑ÿ©", "ÿßÿ®ÿ™ÿØÿßÿ¶Ÿäÿ©", "ÿ£ŸÇÿ±ÿ£ Ÿàÿ£ŸÉÿ™ÿ®", "ÿ£ŸÖŸä"];
const industries_ar = ["ÿ¥ÿ±ŸÉÿ© ÿµŸÜÿßÿπŸäÿ©", "ÿ¥ÿ±ŸÉÿ© ÿ™ÿ¨ÿßÿ±Ÿäÿ©", "ÿ¥ÿ±ŸÉÿ© ÿÆÿØŸÖŸäÿ©", "ŸÖÿ§ÿ≥ÿ≥ÿ© ÿ≠ŸÉŸàŸÖŸäÿ©", "ŸÖŸÉÿ™ÿ®", "ŸÖÿ≠ŸÑ", "Ÿàÿ±ÿ¥ÿ©"];

export const AuthPage = ({ lang = 'ar', onLoginSuccess }) => {
  const { login: performContextLogin } = useAuth();
  const t = translations[lang] || translations.ar;
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [loginMethod, setLoginMethod] = useState('email'); 
  const [userType, setUserType] = useState('individuals');
  const [profileImage, setProfileImage] = useState(null);
  const [showPhotoModal, setShowPhotoModal] = useState(false); 
  const [agreedToPrivacy, setAgreedToPrivacy] = useState(false);
  
  const [formData, setFormData] = useState({
    firstName: '', lastName: '', companyName: '', companyIndustry: '', 
    education: '', email: '', password: '', phone: '', gender: '', dob: '',
    isSpecialNeeds: false, specialNeedsType: '', country: ''
  });

  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const getPhoto = async (source) => {
    setShowPhotoModal(false);
    try {
      const image = await Camera.getPhoto({ quality: 90, allowEditing: true, resultType: CameraResultType.Base64, source });
      setProfileImage(`data:image/jpeg;base64,${image.base64String}`);
    } catch (e) {}
  };

  const handleAuth = async (e) => {
    e.preventDefault();
    setError('');
    if (isLogin && formData.email === 'admin01' && formData.password === 'admin123') {
        await performContextLogin({ role: 'Admin', firstName: 'ŸÖÿØŸäÿ±' }, 'admin_token');
        if (onLoginSuccess) onLoginSuccess();
        navigate('/dashboard');
        return;
    }
    if (!isLogin && !agreedToPrivacy) { setError(t.mustAgree); return; }
    setLoading(true);
    try {
      const payload = { ...formData, profileImage, role: userType === 'companies' ? 'HR' : 'Employee' };
      const res = isLogin ? await userService.login({ email: formData.email, password: formData.password }) : await userService.register(payload);
      await performContextLogin(res.data.user, res.data.token);
      if (onLoginSuccess) onLoginSuccess();
      navigate(isLogin ? '/dashboard' : (userType === 'companies' ? '/onboarding-companies' : '/onboarding-individuals'));
    } catch (err) { setError(err.userMessage || "ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ"); } 
    finally { setLoading(false); }
  };

  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center p-4 bg-[#E3DAD0]" dir="rtl">
      {showPhotoModal && (
        <div className="fixed inset-0 z-[11000] flex items-end justify-center bg-black/40" onClick={() => setShowPhotoModal(false)}>
          <div className="bg-white w-full max-w-lg rounded-t-[3rem] p-8" onClick={e => e.stopPropagation()}>
            <div className="grid grid-cols-2 gap-4">
              <button onClick={() => getPhoto(CameraSource.Camera)} className="p-6 bg-gray-50 rounded-[2rem]">üì∏ ÿßŸÑŸÉÿßŸÖŸäÿ±ÿß</button>
              <button onClick={() => getPhoto(CameraSource.Photos)} className="p-6 bg-gray-50 rounded-[2rem]">üñºÔ∏è ÿßŸÑŸÖÿπÿ±ÿ∂</button>
            </div>
          </div>
        </div>
      )}

      <div className="w-full max-w-lg bg-white rounded-[3rem] shadow-2xl p-6 md:p-10 my-8">
        <div className="text-center mb-6">
          <img src="/logo.jpg" alt="Careerak Logo" className="h-20 w-20 mx-auto rounded-full shadow-xl mb-3 border-4 border-[#E3DAD0]" />
          <h2 className="text-2xl font-black text-[#1A365D]">{isLogin ? t.login : t.signup}</h2>
        </div>

        <div className="flex bg-gray-100 p-1 rounded-[2rem] mb-6">
          <button type="button" onClick={() => setIsLogin(true)} className={`flex-1 py-3 rounded-[1.5rem] font-bold ${isLogin ? 'bg-white text-[#1A365D]' : 'text-gray-400'}`}>{t.login}</button>
          <button type="button" onClick={() => setIsLogin(false)} className={`flex-1 py-3 rounded-[1.5rem] font-bold ${!isLogin ? 'bg-white text-[#1A365D]' : 'text-gray-400'}`}>{t.signup}</button>
        </div>

        <form onSubmit={handleAuth} className="space-y-4">
          {!isLogin && (
            <>
              <div className="flex bg-gray-50 p-2 rounded-2xl mb-4 items-center justify-between text-[10px]">
                <span className="font-black text-gray-400 px-2">{t.category}</span>
                <div className="flex gap-2 flex-1">
                  <button type="button" onClick={() => setUserType('individuals')} className={`flex-1 py-2 rounded-xl font-black ${userType === 'individuals' ? 'bg-[#1A365D] text-white' : 'bg-white text-gray-400'}`}>{t.individuals}</button>
                  <button type="button" onClick={() => setUserType('companies')} className={`flex-1 py-2 rounded-xl font-black ${userType === 'companies' ? 'bg-[#1A365D] text-white' : 'bg-white text-gray-400'}`}>{t.companies}</button>
                </div>
              </div>
              <div className="flex flex-col items-center mb-4">
                <button type="button" onClick={() => setShowPhotoModal(true)} className="w-24 h-24 rounded-full bg-gray-100 border-4 border-white shadow-xl flex items-center justify-center overflow-hidden">
                  {profileImage ? <img src={profileImage} alt="Profile" className="w-full h-full object-cover" /> : <span className="text-3xl">üë§</span>}
                </button>
              </div>
            </>
          )}

          <div className="flex gap-2 mb-2 justify-center">
            <button type="button" onClick={() => setLoginMethod('email')} className={`px-4 py-1.5 rounded-full text-[9px] font-black ${loginMethod === 'email' ? 'bg-[#1A365D] text-white' : 'bg-gray-100 text-gray-400'}`}>{t.loginMethod}</button>
            <button type="button" onClick={() => setLoginMethod('phone')} className={`px-4 py-1.5 rounded-full text-[9px] font-black ${loginMethod === 'phone' ? 'bg-[#1A365D] text-white' : 'bg-gray-100 text-gray-400'}`}>{t.phoneMethod}</button>
          </div>

          <div className="space-y-4">
            {!isLogin && (
              <select name="country" value={formData.country} onChange={handleInputChange} className={`w-full p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs outline-none ${formData.country ? 'text-[#1A365D]' : 'text-gray-400'}`}>
                <option value="" disabled hidden>{t.country}</option>
                {countryCodes.map(c => <option key={c.name} value={c.name}>{c.flag} {c.name}</option>)}
              </select>
            )}
            
            {loginMethod === 'email' ? (
              <input type="text" name="email" value={formData.email} placeholder={t.email} onChange={handleInputChange} className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs text-center border border-transparent focus:border-blue-100 shadow-sm" />
            ) : (
              <div className="flex items-center bg-gray-50 rounded-2xl p-1 border border-transparent shadow-sm">
                <select className="p-3 bg-transparent font-bold text-[10px] outline-none">
                  {countryCodes.map(c => <option key={c.code} value={c.code}>{c.flag} {c.code}</option>)}
                </select>
                <input type="tel" name="phone" value={formData.phone} placeholder={t.phone} onChange={handleInputChange} className="flex-1 p-3 bg-transparent outline-none font-bold text-center text-xs" />
              </div>
            )}
            
            <input type="password" name="password" value={formData.password} placeholder={t.password} onChange={handleInputChange} className="w-full p-4 bg-gray-50 rounded-2xl outline-none font-bold text-xs text-center border border-transparent focus:border-blue-100 shadow-sm" />
          </div>

          {!isLogin && (
            <div className="space-y-3">
              {userType === 'individuals' ? (
                <>
                  <div className="grid grid-cols-2 gap-3">
                    <input type="text" name="firstName" placeholder="ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ŸàŸÑ" onChange={handleInputChange} className="p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs shadow-sm" />
                    <input type="text" name="lastName" placeholder="ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑÿ£ÿÆŸäÿ±" onChange={handleInputChange} className="p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs shadow-sm" />
                  </div>
                  <div className="grid grid-cols-2 gap-3">
                    <select name="gender" value={formData.gender} onChange={handleInputChange} className={`p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs outline-none ${formData.gender ? 'text-[#1A365D]' : 'text-gray-400'}`}>
                      <option value="">ÿßŸÑÿ¨ŸÜÿ≥</option><option value="male">ÿ∞ŸÉÿ±</option><option value="female">ÿ£ŸÜÿ´Ÿâ</option>
                    </select>
                    <input type="date" name="dob" value={formData.dob} onChange={handleInputChange} className={`p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs ${formData.dob ? 'text-[#1A365D]' : 'text-gray-400'}`} />
                  </div>
                  <select name="education" value={formData.education} onChange={handleInputChange} className={`w-full p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs outline-none ${formData.education ? 'text-[#1A365D]' : 'text-gray-400'}`}>
                    <option value="">ÿßŸÑŸÖÿ≥ÿ™ŸàŸâ ÿßŸÑÿ™ÿπŸÑŸäŸÖŸä</option>
                    {education_ar.map(e => <option key={e} value={e}>{e}</option>)}
                  </select>
                  <label className="flex items-center gap-3 cursor-pointer bg-blue-50/50 p-4 rounded-2xl border border-blue-100/50">
                    <input type="checkbox" name="isSpecialNeeds" checked={formData.isSpecialNeeds} onChange={handleInputChange} className="w-5 h-5 accent-[#1A365D] rounded-md" />
                    <span className="text-xs font-black text-[#1A365D]">{t.specialNeeds}</span>
                  </label>
                </>
              ) : (
                <>
                  <input type="text" name="companyName" placeholder="ÿßÿ≥ŸÖ ÿßŸÑÿ¥ÿ±ŸÉÿ©" onChange={handleInputChange} className="w-full p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs shadow-sm" />
                  <select name="companyIndustry" value={formData.companyIndustry} onChange={handleInputChange} className={`w-full p-4 bg-gray-50 rounded-2xl font-bold text-center text-xs outline-none ${formData.companyIndustry ? 'text-[#1A365D]' : 'text-gray-400'}`}>
                    <option value="">ŸÖÿ¨ÿßŸÑ ÿπŸÖŸÑ ÿßŸÑÿ¥ÿ±ŸÉÿ©</option>
                    {industries_ar.map(i => <option key={i} value={i}>{i}</option>)}
                  </select>
                </>
              )}
              <label className="flex items-center gap-3 cursor-pointer bg-[#1A365D]/5 p-4 rounded-2xl border border-[#1A365D]/10">
                <input type="checkbox" checked={agreedToPrivacy} onChange={e => setAgreedToPrivacy(e.target.checked)} className="w-5 h-5 accent-[#1A365D] rounded-md" />
                <span className="text-[10px] font-bold text-[#1A365D]">ÿ£ŸàÿßŸÅŸÇ ÿπŸÑŸâ ÿ≥Ÿäÿßÿ≥ÿ© ÿßŸÑÿÆÿµŸàÿµŸäÿ©</span>
              </label>
            </div>
          )}

          {error && <div className="p-3 bg-red-50 text-red-600 rounded-xl text-xs font-bold text-center">{error}</div>}
          <button type="submit" disabled={loading} className={`w-full py-5 rounded-[2rem] font-black shadow-xl active:scale-95 transition-all text-lg mt-4 ${!isLogin && !agreedToPrivacy ? 'bg-gray-300 text-gray-500 cursor-not-allowed' : 'bg-[#1A365D] text-white'}`}>
            {loading ? t.loading : (isLogin ? t.login : t.signup)}
          </button>
        </form>
      </div>
    </div>
  );
};
