import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

export default function JobPostingsPage() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchJobs = async () => {
      try {
        const res = await api.get('/job-postings');
        setJobs(res.data);
      } catch (err) {
        console.error("Failed to fetch jobs");
      } finally {
        setLoading(false);
      }
    };
    fetchJobs();
  }, []);

  return (
    <div className="min-h-screen bg-[#E3DAD0] p-6" dir="rtl">
      <h2 className="text-3xl font-black text-[#1A365D] mb-8 border-r-8 border-[#1A365D] pr-4">الوظائف المتاحة</h2>
      
      {loading ? (
        <div className="text-center font-bold text-[#1A365D]">جاري التحميل...</div>
      ) : (
        <div className="grid gap-6">
          {jobs.map(job => (
            <div key={job._id} className="bg-white p-6 rounded-[2.5rem] shadow-xl">
              <h3 className="text-xl font-black text-[#1A365D]">{job.title}</h3>
              <p className="text-gray-400 font-bold mb-4">{job.companyName}</p>
              <button 
                onClick={() => navigate(`/apply/${job._id}`)}
                className="bg-[#1A365D] text-white px-8 py-3 rounded-xl font-bold"
              >
                عرض التفاصيل / تقديم
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
