import React, { useState, useMemo, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const countries = [
  { code: 'SA', name: { ar: 'ÿßŸÑŸÖŸÖŸÑŸÉÿ© ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑÿ≥ÿπŸàÿØŸäÿ©', en: 'Saudi Arabia', fr: 'Arabie Saoudite' }, flag: 'üá∏üá¶' },
  { code: 'AE', name: { ar: 'ÿßŸÑÿ•ŸÖÿßÿ±ÿßÿ™ ÿßŸÑÿπÿ±ÿ®Ÿäÿ© ÿßŸÑŸÖÿ™ÿ≠ÿØÿ©', en: 'United Arab Emirates', fr: '√âmirats Arabes Unis' }, flag: 'üá¶üá™' },
  { code: 'EG', name: { ar: 'ÿ¨ŸÖŸáŸàÿ±Ÿäÿ© ŸÖÿµÿ± ÿßŸÑÿπÿ±ÿ®Ÿäÿ©', en: 'Egypt', fr: '√âgypte' }, flag: 'üá™üá¨' },
  { code: 'JO', name: { ar: 'ÿßŸÑÿ£ÿ±ÿØŸÜ', en: 'Jordan', fr: 'Jordanie' }, flag: 'üáØüá¥' },
  { code: 'KW', name: { ar: 'ÿßŸÑŸÉŸàŸäÿ™', en: 'Kuwait', fr: 'Kowe√Øt' }, flag: 'üá∞üáº' },
  { code: 'QA', name: { ar: 'ŸÇÿ∑ÿ±', en: 'Qatar', fr: 'Qatar' }, flag: 'üá∂üá¶' },
  { code: 'BH', name: { ar: 'ÿßŸÑÿ®ÿ≠ÿ±ŸäŸÜ', en: 'Bahrain', fr: 'Bahre√Øn' }, flag: 'üáßüá≠' },
  { code: 'OM', name: { ar: 'ÿπŸÖÿßŸÜ', en: 'Oman', fr: 'Oman' }, flag: 'üá¥üá≤' },
  { code: 'IQ', name: { ar: 'ÿßŸÑÿπÿ±ÿßŸÇ', en: 'Iraq', fr: 'Irak' }, flag: 'üáÆüá∂' },
  { code: 'LB', name: { ar: 'ŸÑÿ®ŸÜÿßŸÜ', en: 'Lebanon', fr: 'Liban' }, flag: 'üá±üáß' },
  { code: 'SY', name: { ar: 'ÿ≥Ÿàÿ±Ÿäÿß', en: 'Syria', fr: 'Syrie' }, flag: 'CUSTOM_SYRIA' },
  { code: 'PS', name: { ar: 'ŸÅŸÑÿ≥ÿ∑ŸäŸÜ', en: 'Palestine', fr: 'Palestine' }, flag: 'üáµüá∏' },
  { code: 'DZ', name: { ar: 'ÿßŸÑÿ¨ÿ≤ÿßÿ¶ÿ±', en: 'Algeria', fr: 'Alg√©rie' }, flag: 'üá©üáø' },
  { code: 'MA', name: { ar: 'ÿßŸÑŸÖÿ∫ÿ±ÿ®', en: 'Morocco', fr: 'Maroc' }, flag: 'üá≤üá¶' },
  { code: 'TN', name: { ar: 'ÿ™ŸàŸÜÿ≥', en: 'Tunisia', fr: 'Tunisie' }, flag: 'üáπüá≥' },
  { code: 'LY', name: { ar: 'ŸÑŸäÿ®Ÿäÿß', en: 'Libya', fr: 'Libye' }, flag: 'üá±üáæ' },
  { code: 'SD', name: { ar: 'ÿßŸÑÿ≥ŸàÿØÿßŸÜ', en: 'Sudan', fr: 'Soudan' }, flag: 'üá∏üá©' },
  { code: 'YE', name: { ar: 'ÿßŸÑŸäŸÖŸÜ', en: 'Yemen', fr: 'Y√©men' }, flag: 'üáæüá™' },
  { code: 'TR', name: { ar: 'ÿ™ÿ±ŸÉŸäÿß', en: 'Turkey', fr: 'Turquie' }, flag: 'üáπüá∑' },
  { code: 'US', name: { ar: 'ÿßŸÑŸàŸÑÿßŸäÿßÿ™ ÿßŸÑŸÖÿ™ÿ≠ÿØÿ©', en: 'United States', fr: '√âtats-Unis' }, flag: 'üá∫üá∏' },
  { code: 'GB', name: { ar: 'ÿßŸÑŸÖŸÖŸÑŸÉÿ© ÿßŸÑŸÖÿ™ÿ≠ÿØÿ©', en: 'United Kingdom', fr: 'Royaume-Uni' }, flag: 'üá¨üáß' },
  { code: 'FR', name: { ar: 'ŸÅÿ±ŸÜÿ≥ÿß', en: 'France', fr: 'France' }, flag: 'üá´üá∑' },
  { code: 'DE', name: { ar: 'ÿ£ŸÑŸÖÿßŸÜŸäÿß', en: 'Germany', fr: 'Allemagne' }, flag: 'üá©üá™' },
  { code: 'CA', name: { ar: 'ŸÉŸÜÿØÿß', en: 'Canada', fr: 'Canada' }, flag: 'üá®üá¶' },
];

const SyriaFinalFlag = () => (
  <div className="w-24 h-24 flex items-center justify-center overflow-hidden rounded-full border border-gray-100 shadow-sm">
    <img src="/sy-flag.jpg" alt="Syria Flag" className="w-full h-full object-cover" />
  </div>
);

export const CountryPage = () => {
  const navigate = useNavigate();
  const [isVisible, setIsVisible] = useState(false);
  const language = localStorage.getItem('lang') || 'ar';
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => { setIsVisible(true); }, []);

  const sortedCountries = useMemo(() => {
    let filtered = countries.filter(country => 
      country.name[language]?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      country.code.toLowerCase().includes(searchTerm.toLowerCase())
    );
    return filtered.sort((a, b) => a.name[language]?.localeCompare(b.name[language], language === 'ar' ? 'ar' : 'en'));
  }, [searchTerm, language]);

  const isArabic = language === 'ar';
  const t = {
    ar: { title: 'ÿßÿÆÿ™ÿ± ŸÖŸàÿ∑ŸÜŸÉ ÿßŸÑŸÖŸáŸÜŸä', slogan: 'ŸÉÿßÿ±Ÿäÿ±ŸÉ ŸäŸÅÿ™ÿ≠ ŸÑŸÉ ÿ£ÿ®Ÿàÿßÿ® ÿßŸÑŸÅÿ±ÿµ ŸÅŸä ÿ¨ŸÖŸäÿπ ÿ£ŸÜÿ≠ÿßÿ° ÿßŸÑÿπÿßŸÑŸÖ', search: 'ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ÿØŸàŸÑÿ™ŸÉ ÿßŸÑŸÖŸÅÿ∂ŸÑÿ©...', select: 'ÿßÿÆÿ™Ÿäÿßÿ±' },
    en: { title: 'Choose Your Hub', slogan: 'Careerak opens doors worldwide', search: 'Search country...', select: 'Select' },
    fr: { title: 'Choisissez votre pays', slogan: 'Careerak vous ouvre les portes', search: 'Cherchez...', select: 'S√©lectionner' }
  }[language];

  // ÿ£ŸÑŸàÿßŸÜ ÿßŸÑŸÑŸàÿ¨Ÿà ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© (ÿßŸÑŸÉÿ≠ŸÑŸä ÿßŸÑŸÅÿßÿÆÿ± ŸàÿßŸÑÿ∞Ÿáÿ®Ÿä/ÿßŸÑÿ®Ÿäÿ¨ ÿßŸÑŸáÿßÿØÿ¶)
  const PRIMARY_NAVY = '#1A365D';
  const SOFT_BEIGE = '#E3DAD0';

  return (
    <div className={`min-h-screen flex flex-col font-sans bg-[${SOFT_BEIGE}] transition-opacity duration-1000 ${isVisible ? 'opacity-100' : 'opacity-0'}`} dir={isArabic ? "rtl" : "ltr"}>
      {/* Header ÿ®ŸÑŸÖÿ≥ÿ© ŸÅÿÆÿßŸÖÿ© ŸàÿßŸÜÿ≠ŸÜÿßÿ°ÿ© ÿØÿßÿ¶ÿ±Ÿäÿ© */}
      <div className={`bg-[${PRIMARY_NAVY}] pt-16 pb-24 px-6 rounded-b-[4rem] shadow-2xl relative overflow-hidden transition-transform duration-1000 ${isVisible ? 'translate-y-0' : '-translate-y-10'}`}>
        <div className="absolute top-0 left-0 w-full h-full opacity-10 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')]"></div>
        <div className="max-w-6xl mx-auto text-center relative z-10">
          <h1 className="text-4xl md:text-6xl font-black text-white mb-4 tracking-tight drop-shadow-md">
            {t.title}
          </h1>
          <p className="text-blue-100/80 text-lg md:text-xl font-medium max-w-xl mx-auto italic">
            {t.slogan}
          </p>
        </div>
      </div>

      <div className="max-w-6xl mx-auto w-full px-6 -mt-12 relative z-20 flex-grow pb-20">
        {/* ÿ≠ŸÇŸÑ ÿßŸÑÿ®ÿ≠ÿ´ ÿ®ŸÇŸàÿßÿπÿØ ÿßŸÑÿØÿ≥ÿ™Ÿàÿ± (ŸáŸäŸÜÿ™ ÿ±ŸÖÿßÿØŸä -> ŸÜÿµ ŸÉÿ≠ŸÑŸä) */}
        <div className="mb-12 flex justify-center">
          <div className="relative w-full max-w-md group">
            <input 
              type="text" 
              placeholder={t.search} 
              value={searchTerm} 
              onChange={(e) => setSearchTerm(e.target.value)} 
              className={`w-full p-6 bg-white rounded-[2.5rem] shadow-xl text-xl font-bold 
                placeholder-gray-300 text-[${PRIMARY_NAVY}] 
                focus:ring-4 focus:ring-[#1A365D]/20 outline-none transition-all duration-300 
                border-2 border-transparent focus:border-[#1A365D]/10 text-center`}
            />
            <div className="absolute left-6 top-1/2 -translate-y-1/2 text-gray-300 group-focus-within:text-[#1A365D] transition-colors">
               <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" /></svg>
            </div>
          </div>
        </div>

        {/* ÿ¥ÿ®ŸÉÿ© ÿßŸÑÿØŸàŸÑ ÿ®ŸÑŸÖÿ≥ÿ© ŸÅÿÆÿßŸÖÿ© Ÿàÿ£ŸÜŸäŸÖŸäÿ¥ŸÜ */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {sortedCountries.map((country, index) => (
            <button 
              key={country.code} 
              onClick={() => { localStorage.setItem('selectedCountry', JSON.stringify(country)); navigate('/01_WelcomePage'); }} 
              style={{ transitionDelay: `${index * 50}ms` }}
              className={`bg-white/80 backdrop-blur-sm group p-8 rounded-[3.5rem] shadow-sm hover:shadow-2xl 
                transition-all duration-500 transform hover:-translate-y-3 border border-white/50 
                flex flex-col items-center relative overflow-hidden ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}
            >
              <div className="absolute inset-0 bg-gradient-to-br from-[#1A365D]/5 to-transparent opacity-0 group-hover:opacity-100 transition-opacity"></div>
              
              <div className="w-32 h-32 mb-6 rounded-full bg-gray-50 flex items-center justify-center border-4 border-white overflow-hidden shadow-inner group-hover:scale-110 transition-transform duration-500 z-10">
                {country.flag === 'CUSTOM_SYRIA' ? (
                  <SyriaFinalFlag />
                ) : (
                  <span className="text-7xl leading-none drop-shadow-sm">{country.flag}</span>
                )}
              </div>
              
              <h3 className="text-xl font-black text-[#1A365D] group-hover:tracking-wide transition-all duration-300 z-10">
                {country.name[language]}
              </h3>
              
              <div className="mt-4 opacity-0 group-hover:opacity-100 transition-all transform translate-y-2 group-hover:translate-y-0 text-[10px] font-black text-[#1A365D] uppercase tracking-[0.3em] z-10">
                {t.select} <span className={isArabic ? 'mr-1' : 'ml-1'}>‚Üí</span>
              </div>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};
