    e.preventDefault();
    if (!isLogin && !agreed) { setError(t.mustAgree); return; }
    if (!isLogin && formData.password !== formData.confirmPassword) { setError(t.passMismatch); return; }
    setError('');
    setLoading(true);
    try {
      const payload = isLogin 
        ? (loginMethod === 'email' ? { email: formData.email, password: formData.password } : { phone: formData.phone, password: formData.password })
        : { ...formData, profileImage, role: userType === 'companies' ? 'HR' : 'Employee' };
      
      const res = isLogin ? await userService.login(payload) : await userService.register(payload);
      await performContextLogin(res.data.user, res.data.token);
      navigate('/dashboard');
    } catch (err) { setError(err.userMessage || "Error"); } 
    finally { setLoading(false); }
  };

